import React, { useContext } from 'react';
import { CartContext } from '../../Context/CartContext';

const Cart = () => {
  const { cart, removeFromCart, clearCart } = useContext(CartContext);

  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-yellow-100 via-red-100 to-pink-100 p-4">
      <div className="bg-white rounded-3xl shadow-2xl p-8 w-full max-w-2xl animate-fadeIn">
        <h2 className="text-3xl font-extrabold mb-6 text-gray-800 text-center">Your Cart</h2>
        {cart.length === 0 ? (
          <p className="text-center text-gray-500">Your cart is empty.</p>
        ) : (
          <>
            <table className="min-w-full bg-white mb-4 rounded-xl overflow-hidden shadow">
              <thead>
                <tr className="bg-yellow-100">
                  <th className="py-2">Product</th>
                  <th className="py-2">Quantity</th>
                  <th className="py-2">Price</th>
                  <th className="py-2">Actions</th>
                </tr>
              </thead>
              <tbody>
                {cart.map(item => (
                  <tr key={item._id} className="hover:bg-yellow-50">
                    <td className="py-2 text-yellow-700 font-semibold">{item.name}</td>
                    <td className="py-2">{item.quantity}</td>
                    <td className="py-2 font-bold text-green-600">9{item.price}</td>
                    <td className="py-2">
                      <button className="px-3 py-1 bg-red-500 text-white rounded-full shadow hover:bg-red-700 transition-all duration-200" onClick={() => removeFromCart(item._id)}>Remove</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="mb-4 font-bold text-xl text-center text-yellow-700">Total: 9{total}</div>
            <div className="flex justify-center">
              <button className="px-6 py-2 bg-gradient-to-r from-yellow-400 via-red-400 to-pink-500 text-white font-semibold rounded-xl shadow-lg hover:scale-105 transform transition-all duration-300" onClick={clearCart}>Clear Cart</button>
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Cart;
