import React, { useEffect, useState, useContext } from 'react';
import { CartContext } from '../../Context/CartContext';
import { FiSearch, FiFilter, FiTruck, FiClock, FiStar, FiShield, FiAward } from 'react-icons/fi';

// Sample products for testing
const sampleProducts = [
  {
    _id: '1',
    name: 'Delicious Pizza',
    description: 'Fresh pizza with amazing toppings',
    price: 299,
    category: 'Pizza',
    imageUrl: '/favicon.ico',
    rating: 4.5
  },
  {
    _id: '2',
    name: 'Classic Burger',
    description: 'Juicy beef burger with cheese',
    price: 199,
    category: 'Burger',
    imageUrl: '/favicon.ico',
    rating: 4.3
  },
  {
    _id: '3',
    name: 'Chicken Biryani',
    description: 'Aromatic basmati rice with tender chicken',
    price: 249,
    category: 'Indian',
    imageUrl: '/favicon.ico',
    rating: 4.7
  }
];

const Products = () => {
  const { addToCart } = useContext(CartContext);
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [searchTerm, setSearchTerm] = useState('');
  const [sortBy, setSortBy] = useState('name');
  const [loading, setLoading] = useState(true);
  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        // For now, use sample data
        setTimeout(() => {
          setProducts(sampleProducts);
          setLoading(false);
        }, 1000);
      } catch (error) {
        console.error('Failed to fetch products:', error);
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const handleAddToCart = (product) => {
    try {
      addToCart(product);
      alert(`${product.name} added to cart!`);
    } catch (error) {
      alert('Failed to add item to cart');
    }
  };

  // Get unique categories
  const categories = ['All', ...new Set(products.map(p => p.category))];

  // Filter and sort products
  const processedProducts = products
    .filter(product => {
      const categoryMatch = selectedCategory === 'All' || product.category === selectedCategory;
      const searchMatch = product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (product.description && product.description.toLowerCase().includes(searchTerm.toLowerCase()));
      return categoryMatch && searchMatch;
    })
    .sort((a, b) => {
      switch (sortBy) {
        case 'price-low':
          return a.price - b.price;
        case 'price-high':
          return b.price - a.price;
        case 'rating':
          return (b.rating || 0) - (a.rating || 0);
        case 'name':
        default:
          return a.name.localeCompare(b.name);
      }
    });

  if (loading) {
    return <LoadingComponent />;
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="bg-gradient-to-br from-orange-400 via-red-500 to-pink-500 text-white py-16">
        <div className="container mx-auto px-4 text-center">
          <h1 className="text-4xl md:text-6xl font-bold mb-4 animate-fadeIn">
            Delicious Food Menu
          </h1>
          <p className="text-xl md:text-2xl opacity-90 mb-8 animate-slideUp delay-200">
            Explore our amazing collection of fresh & tasty meals
          </p>
          
          {/* Search Bar */}
          <div className="max-w-2xl mx-auto relative animate-slideUp delay-400">
            <FiSearch className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400" size={20} />
            <input
              type="text"
              placeholder="Search for your favorite food..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-12 pr-4 py-4 rounded-xl text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-4 focus:ring-white focus:ring-opacity-30 text-lg"
            />
          </div>
        </div>
      </div>

      <div className="container mx-auto px-4 py-8">
        {/* Filters Section */}
        <div className="bg-white rounded-2xl shadow-md p-6 mb-8">
          <div className="flex flex-wrap items-center justify-between gap-4 mb-4">
            <div className="flex items-center space-x-4">
              <button
                onClick={() => setShowFilters(!showFilters)}
                className="inline-flex items-center space-x-2 bg-gray-100 hover:bg-gray-200 px-4 py-2 rounded-lg transition-colors lg:hidden"
              >
                <FiFilter size={16} />
                <span>Filters</span>
              </button>
              
              {/* Category Filter */}
              <div className="hidden lg:flex items-center space-x-2">
                {categories.map((category) => (
                  <button
                    key={category}
                    onClick={() => setSelectedCategory(category)}
                    className={`px-4 py-2 rounded-lg transition-colors ${
                      selectedCategory === category
                        ? 'bg-orange-500 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {category}
                  </button>
                ))}
              </div>
            </div>

            {/* Sort Dropdown */}
            <div className="flex items-center space-x-2">
              <label className="text-gray-700 font-medium">Sort by:</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-orange-500"
              >
                <option value="name">Name (A-Z)</option>
                <option value="price-low">Price (Low to High)</option>
                <option value="price-high">Price (High to Low)</option>
                <option value="rating">Highest Rated</option>
              </select>
            </div>
          </div>

          {/* Mobile Category Filter */}
          {showFilters && (
            <div className="lg:hidden mt-4 pt-4 border-t">
              <div className="flex flex-wrap gap-2">
                {categories.map((category) => (
                  <button
                    key={category}
                    onClick={() => setSelectedCategory(category)}
                    className={`px-3 py-2 rounded-lg text-sm transition-colors ${
                      selectedCategory === category
                        ? 'bg-orange-500 text-white'
                        : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                    }`}
                  >
                    {category}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Results Count */}
          <div className="mt-4 text-gray-600">
            Showing {processedProducts.length} of {products.length} products
          </div>
        </div>

        {/* Products Grid */}
        <ProductGridComponent
          products={processedProducts}
          onAddToCart={handleAddToCart}
        />

        {/* About Sections */}
        <AboutSections />
      </div>
    </div>
  );
};

// Simple Product Grid Component
const ProductGridComponent = ({ products, onAddToCart }) => {
  if (!products || products.length === 0) {
    return (
      <div className="text-center py-16">
        <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-4">
          <span className="text-4xl">üçΩÔ∏è</span>
        </div>
        <h3 className="text-xl font-semibold text-gray-900 mb-2">No Products Found</h3>
        <p className="text-gray-600">Try adjusting your search or filters</p>
      </div>
    );
  }

  return (
    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {products.map((product, index) => (
        <div
          key={product._id || product.id || index}
          className="bg-white rounded-xl shadow-sm hover:shadow-lg transition-all duration-300 overflow-hidden group animate-fadeIn"
          style={{ 
            animationDelay: `${index * 0.1}s`,
            animationFillMode: 'forwards' 
          }}
        >
          {/* Image Container */}
          <div className="relative aspect-square overflow-hidden">
            <img
              src={product.imageUrl || product.image || '/favicon.ico'}
              alt={product.name}
              className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
              onError={(e) => {
                e.target.src = '/favicon.ico';
              }}
            />
            
            {/* Category Badge */}
            {product.category && (
              <div className="absolute top-3 left-3">
                <span className="bg-white bg-opacity-90 text-gray-700 text-xs font-medium px-2 py-1 rounded-full">
                  {product.category}
                </span>
              </div>
            )}
          </div>

          {/* Product Info */}
          <div className="p-4">
            <div className="mb-2">
              <h3 className="font-semibold text-gray-900 mb-1 line-clamp-1">
                {product.name}
              </h3>
              
              {product.description && (
                <p className="text-gray-600 text-sm mb-3 line-clamp-2">
                  {product.description}
                </p>
              )}
            </div>

            {/* Rating */}
            <div className="flex items-center space-x-1 mb-3 text-sm text-gray-500">
              <FiStar className="text-yellow-400" size={14} />
              <span>{product.rating || '4.5'}</span>
            </div>

            {/* Price & Add to Cart */}
            <div className="flex items-center justify-between">
              <span className="text-lg font-bold text-gray-900">
                ‚Çπ{product.price}
              </span>
              
              <button
                onClick={() => onAddToCart(product)}
                className="inline-flex items-center space-x-1 bg-orange-500 hover:bg-orange-600 text-white text-sm font-medium px-4 py-2 rounded-lg transition-all duration-200 hover:shadow-md active:scale-95"
              >
                <span>Add to Cart</span>
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

// Loading Component
const LoadingComponent = () => {
  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center">
      <div className="text-center">
        <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-orange-500 mx-auto mb-4"></div>
        <p className="text-gray-600">Loading delicious food...</p>
      </div>
    </div>
  );
};

// About Sections Component
const AboutSections = () => {
  return (
    <div className="mt-16 space-y-16">
      {/* Why Choose Us Section */}
      <section className="bg-white rounded-3xl shadow-xl p-8 md:p-12">
        <div className="text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
            Why Choose Our Food Delivery?
          </h2>
          <p className="text-xl text-gray-600 max-w-3xl mx-auto">
            We're committed to bringing you the best food experience with quality, speed, and care
          </p>
        </div>

        <div className="grid md:grid-cols-3 gap-8">
          {/* Fast Delivery */}
          <div className="text-center group">
            <div className="w-20 h-20 bg-gradient-to-br from-orange-400 to-red-500 rounded-2xl flex items-center justify-center mx-auto mb-6 group-hover:scale-110 transition-transform duration-300">
              <FiTruck className="text-white" size={32} />
            </div>
            <h3 className="text-xl font-bold text-gray-900 mb-4">Lightning Fast Delivery</h3>
            <p className="text-gray-600 mb-4">
              Get your favorite meals delivered in under 30 minutes with our efficient delivery network
            </p>
            <div className="flex items-center justify-center space-x-2 text-orange-500 font-semibold">
              <FiClock size={16} />
              <span>Average 25 mins</span>
            </div>
          </div>

          {/* Quality Food */}
          <div className="text-center group">
            <div className="w-20 h-20 bg-gradient-to-br from-green-400 to-blue-500 rounded-2xl flex items-center justify-center mx-auto mb-6 group-hover:scale-110 transition-transform duration-300">
              <FiAward className="text-white" size={32} />
            </div>
            <h3 className="text-xl font-bold text-gray-900 mb-4">Premium Quality</h3>
            <p className="text-gray-600 mb-4">
              Fresh ingredients, expert chefs, and rigorous quality checks ensure every meal is perfect
            </p>
            <div className="flex items-center justify-center space-x-2 text-green-500 font-semibold">
              <FiStar size={16} />
              <span>4.8+ Rating</span>
            </div>
          </div>

          {/* Safe & Secure */}
          <div className="text-center group">
            <div className="w-20 h-20 bg-gradient-to-br from-purple-400 to-pink-500 rounded-2xl flex items-center justify-center mx-auto mb-6 group-hover:scale-110 transition-transform duration-300">
              <FiShield className="text-white" size={32} />
            </div>
            <h3 className="text-xl font-bold text-gray-900 mb-4">Safe & Hygienic</h3>
            <p className="text-gray-600 mb-4">
              Contactless delivery, sanitized packaging, and health-certified restaurants for your safety
            </p>
            <div className="flex items-center justify-center space-x-2 text-purple-500 font-semibold">
              <FiShield size={16} />
              <span>100% Safe</span>
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="bg-gradient-to-r from-orange-400 via-red-500 to-pink-500 text-white rounded-3xl p-8 md:p-12">
        <div className="text-center mb-8">
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            Our Amazing Numbers
          </h2>
          <p className="text-xl opacity-90">
            Join thousands of satisfied customers
          </p>
        </div>

        <div className="grid grid-cols-2 md:grid-cols-4 gap-8 text-center">
          {[
            { number: '10K+', label: 'Happy Customers', icon: 'üòä' },
            { number: '500+', label: 'Restaurant Partners', icon: 'üè™' },
            { number: '50K+', label: 'Orders Delivered', icon: 'üì¶' },
            { number: '25', label: 'Cities Covered', icon: 'üåü' }
          ].map((stat, index) => (
            <div key={index} className="animate-fadeIn" style={{ animationDelay: `${index * 0.1}s` }}>
              <div className="text-4xl mb-2">{stat.icon}</div>
              <div className="text-3xl md:text-4xl font-bold mb-2">{stat.number}</div>
              <div className="text-sm md:text-base opacity-90">{stat.label}</div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Products;
