import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../../Context/CartContext';
import { AuthContext } from '../../Context/AuthContext';
import { useToast } from '../../components/ToastContainer';
import { 
  FiArrowLeft, 
  FiMapPin, 
  FiCreditCard, 
  FiCheck, 
  FiTruck, 
  FiShield,
  FiUser,
  FiPhone,
  FiHome,
  FiNavigation
} from 'react-icons/fi';
import orderService from '../../services/orderService';

const ModernCheckout = () => {
  const { cart, getCartTotal, clearCart } = useContext(CartContext);
  const { user } = useContext(AuthContext);
  const { showSuccess, showError } = useToast();
  const navigate = useNavigate();

  // State for checkout steps
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [stepLoading, setStepLoading] = useState(false);

  // Address form state
  const [deliveryAddress, setDeliveryAddress] = useState({
    fullName: user?.name || '',
    phone: user?.phone || '',
    addressLine1: '',
    addressLine2: '',
    city: '',
    state: '',
    pincode: '',
    landmark: ''
  });

  // Payment state
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [orderNotes, setOrderNotes] = useState('');

  // Pricing calculations with safety checks
  const subtotal = getCartTotal ? getCartTotal() : cart.reduce((sum, item) => {
    const price = item.price || 0;
    const quantity = item.quantity || 1;
    return sum + (price * quantity);
  }, 0);
  const deliveryFee = subtotal > 299 ? 0 : 49;
  const tax = Math.round(subtotal * 0.05); // 5% tax
  const total = subtotal + deliveryFee + tax;

  useEffect(() => {
    // Redirect if cart is empty or user not logged in
    if (!user) {
      navigate('/login');
      return;
    }
    if (!cart || cart.length === 0) {
      navigate('/cart');
      return;
    }
  }, [user, cart, navigate]);

  const handleAddressSubmit = async (e) => {
    e.preventDefault();
    setStepLoading(true);

    // Simulate API call for address validation
    await new Promise(resolve => setTimeout(resolve, 2000));
    
    setStepLoading(false);
    setCurrentStep(2);
    showSuccess('Address saved successfully!');
  };

  const handlePaymentSubmit = async (e) => {
    e.preventDefault();
    setStepLoading(true);

    // Simulate payment processing
    await new Promise(resolve => setTimeout(resolve, 3000));
    
    try {
      const orderData = {
        items: cart.map(item => ({
          product: item._id,
          name: item.name,
          price: item.price,
          quantity: item.quantity,
          image: item.imageUrl || item.image
        })),
        deliveryAddress,
        paymentMethod,
        subtotal,
        deliveryFee,
        tax,
        total,
        notes: orderNotes
      };

      await orderService.createOrder(orderData);
      
      setStepLoading(false);
      setCurrentStep(3);
      clearCart();
      showSuccess('Order placed successfully!');
      
      setTimeout(() => {
        navigate('/orders');
      }, 3000);
      
    } catch (error) {
      setStepLoading(false);
      showError('Order failed. Please try again.');
      console.error('Order error:', error);
    }
  };

  const steps = [
    { id: 1, title: 'Delivery Address', icon: FiMapPin },
    { id: 2, title: 'Payment Method', icon: FiCreditCard },
    { id: 3, title: 'Order Confirmation', icon: FiCheck }
  ];

  const renderStepIndicator = () => (
    <div className="flex items-center justify-center mb-8">
      {steps.map((step, index) => (
        <div key={step.id} className="flex items-center">
          <div className={`
            flex items-center justify-center w-12 h-12 rounded-full border-2 transition-all duration-300
            ${currentStep >= step.id 
              ? 'bg-orange-500 border-orange-500 text-white' 
              : 'bg-white dark:bg-gray-800 border-gray-300 dark:border-gray-600 text-gray-400'
            }
          `}>
            {currentStep > step.id ? (
              <FiCheck size={20} />
            ) : (
              <step.icon size={20} />
            )}
          </div>
          {index < steps.length - 1 && (
            <div className={`
              w-16 h-0.5 mx-4 transition-all duration-300
              ${currentStep > step.id ? 'bg-orange-500' : 'bg-gray-300 dark:bg-gray-600'}
            `} />
          )}
        </div>
      ))}
    </div>
  );

  const renderAddressStep = () => (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-8">
      <div className="flex items-center mb-6">
        <FiMapPin className="text-orange-500 mr-3" size={24} />
        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Delivery Address</h2>
      </div>

      <form onSubmit={handleAddressSubmit} className="space-y-6">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              <FiUser className="inline mr-2" size={16} />
              Full Name *
            </label>
            <input
              type="text"
              required
              value={deliveryAddress.fullName}
              onChange={(e) => setDeliveryAddress({...deliveryAddress, fullName: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              placeholder="Enter your full name"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              <FiPhone className="inline mr-2" size={16} />
              Phone Number *
            </label>
            <input
              type="tel"
              required
              value={deliveryAddress.phone}
              onChange={(e) => setDeliveryAddress({...deliveryAddress, phone: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              placeholder="Enter your phone number"
            />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
            <FiHome className="inline mr-2" size={16} />
            Address Line 1 *
          </label>
          <input
            type="text"
            required
            value={deliveryAddress.addressLine1}
            onChange={(e) => setDeliveryAddress({...deliveryAddress, addressLine1: e.target.value})}
            className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
            placeholder="House/Flat/Office No, Building Name"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
            Address Line 2
          </label>
          <input
            type="text"
            value={deliveryAddress.addressLine2}
            onChange={(e) => setDeliveryAddress({...deliveryAddress, addressLine2: e.target.value})}
            className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
            placeholder="Area, Street, Sector, Village"
          />
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              City *
            </label>
            <input
              type="text"
              required
              value={deliveryAddress.city}
              onChange={(e) => setDeliveryAddress({...deliveryAddress, city: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              placeholder="City"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              State *
            </label>
            <input
              type="text"
              required
              value={deliveryAddress.state}
              onChange={(e) => setDeliveryAddress({...deliveryAddress, state: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              placeholder="State"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
              PIN Code *
            </label>
            <input
              type="text"
              required
              pattern="[0-9]{6}"
              value={deliveryAddress.pincode}
              onChange={(e) => setDeliveryAddress({...deliveryAddress, pincode: e.target.value})}
              className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
              placeholder="PIN Code"
            />
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
            <FiNavigation className="inline mr-2" size={16} />
            Landmark (Optional)
          </label>
          <input
            type="text"
            value={deliveryAddress.landmark}
            onChange={(e) => setDeliveryAddress({...deliveryAddress, landmark: e.target.value})}
            className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white"
            placeholder="Nearby landmark for easy identification"
          />
        </div>

        <div className="flex justify-between pt-6">
          <button
            type="button"
            onClick={() => navigate('/cart')}
            className="flex items-center px-6 py-3 text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200 transition-colors"
          >
            <FiArrowLeft className="mr-2" size={16} />
            Back to Cart
          </button>
          
          <button
            type="submit"
            disabled={stepLoading}
            className="flex items-center px-8 py-3 bg-gradient-to-r from-orange-500 to-red-500 text-white font-semibold rounded-lg hover:from-orange-600 hover:to-red-600 transition-all duration-300 disabled:opacity-50"
          >
            {stepLoading ? (
              <>
                <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent mr-2"></div>
                Validating Address...
              </>
            ) : (
              <>
                Continue to Payment
                <FiArrowLeft className="ml-2 rotate-180" size={16} />
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );

  const renderPaymentStep = () => (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-8">
      <div className="flex items-center mb-6">
        <FiCreditCard className="text-orange-500 mr-3" size={24} />
        <h2 className="text-2xl font-bold text-gray-900 dark:text-white">Payment Method</h2>
      </div>

      <form onSubmit={handlePaymentSubmit} className="space-y-6">
        <div className="space-y-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {/* Credit/Debit Card */}
            <label className={`
              flex items-center p-4 border-2 rounded-lg cursor-pointer transition-all
              ${paymentMethod === 'card' 
                ? 'border-orange-500 bg-orange-50 dark:bg-orange-900/20' 
                : 'border-gray-200 dark:border-gray-600 hover:border-orange-300'
              }
            `}>
              <input
                type="radio"
                name="payment"
                value="card"
                checked={paymentMethod === 'card'}
                onChange={(e) => setPaymentMethod(e.target.value)}
                className="hidden"
              />
              <FiCreditCard className="mr-3 text-orange-500" size={24} />
              <div>
                <div className="font-semibold text-gray-900 dark:text-white">Credit/Debit Card</div>
                <div className="text-sm text-gray-500 dark:text-gray-400">Visa, Mastercard, RuPay</div>
              </div>
              {paymentMethod === 'card' && <FiCheck className="ml-auto text-orange-500" size={20} />}
            </label>

            {/* UPI */}
            <label className={`
              flex items-center p-4 border-2 rounded-lg cursor-pointer transition-all
              ${paymentMethod === 'upi' 
                ? 'border-orange-500 bg-orange-50 dark:bg-orange-900/20' 
                : 'border-gray-200 dark:border-gray-600 hover:border-orange-300'
              }
            `}>
              <input
                type="radio"
                name="payment"
                value="upi"
                checked={paymentMethod === 'upi'}
                onChange={(e) => setPaymentMethod(e.target.value)}
                className="hidden"
              />
              <div className="w-6 h-6 mr-3 bg-orange-500 rounded text-white text-xs flex items-center justify-center font-bold">
                â‚¹
              </div>
              <div>
                <div className="font-semibold text-gray-900 dark:text-white">UPI Payment</div>
                <div className="text-sm text-gray-500 dark:text-gray-400">PhonePe, GooglePay, Paytm</div>
              </div>
              {paymentMethod === 'upi' && <FiCheck className="ml-auto text-orange-500" size={20} />}
            </label>

            {/* Cash on Delivery */}
            <label className={`
              flex items-center p-4 border-2 rounded-lg cursor-pointer transition-all
              ${paymentMethod === 'cod' 
                ? 'border-orange-500 bg-orange-50 dark:bg-orange-900/20' 
                : 'border-gray-200 dark:border-gray-600 hover:border-orange-300'
              }
            `}>
              <input
                type="radio"
                name="payment"
                value="cod"
                checked={paymentMethod === 'cod'}
                onChange={(e) => setPaymentMethod(e.target.value)}
                className="hidden"
              />
              <FiTruck className="mr-3 text-orange-500" size={24} />
              <div>
                <div className="font-semibold text-gray-900 dark:text-white">Cash on Delivery</div>
                <div className="text-sm text-gray-500 dark:text-gray-400">Pay when order arrives</div>
              </div>
              {paymentMethod === 'cod' && <FiCheck className="ml-auto text-orange-500" size={20} />}
            </label>

            {/* Wallet */}
            <label className={`
              flex items-center p-4 border-2 rounded-lg cursor-pointer transition-all
              ${paymentMethod === 'wallet' 
                ? 'border-orange-500 bg-orange-50 dark:bg-orange-900/20' 
                : 'border-gray-200 dark:border-gray-600 hover:border-orange-300'
              }
            `}>
              <input
                type="radio"
                name="payment"
                value="wallet"
                checked={paymentMethod === 'wallet'}
                onChange={(e) => setPaymentMethod(e.target.value)}
                className="hidden"
              />
              <FiShield className="mr-3 text-orange-500" size={24} />
              <div>
                <div className="font-semibold text-gray-900 dark:text-white">Digital Wallet</div>
                <div className="text-sm text-gray-500 dark:text-gray-400">Amazon Pay, Mobikwik</div>
              </div>
              {paymentMethod === 'wallet' && <FiCheck className="ml-auto text-orange-500" size={20} />}
            </label>
          </div>
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
            Order Notes (Optional)
          </label>
          <textarea
            value={orderNotes}
            onChange={(e) => setOrderNotes(e.target.value)}
            rows="3"
            className="w-full px-4 py-3 border border-gray-300 dark:border-gray-600 rounded-lg focus:ring-2 focus:ring-orange-500 bg-white dark:bg-gray-700 text-gray-900 dark:text-white resize-none"
            placeholder="Special instructions for delivery (e.g., call before arriving, gate code, etc.)"
          />
        </div>

        <div className="flex justify-between pt-6">
          <button
            type="button"
            onClick={() => setCurrentStep(1)}
            className="flex items-center px-6 py-3 text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200 transition-colors"
          >
            <FiArrowLeft className="mr-2" size={16} />
            Back to Address
          </button>
          
          <button
            type="submit"
            disabled={stepLoading}
            className="flex items-center px-8 py-3 bg-gradient-to-r from-orange-500 to-red-500 text-white font-semibold rounded-lg hover:from-orange-600 hover:to-red-600 transition-all duration-300 disabled:opacity-50"
          >
            {stepLoading ? (
              <>
                <div className="animate-spin rounded-full h-5 w-5 border-2 border-white border-t-transparent mr-2"></div>
                Processing Payment...
              </>
            ) : (
              <>
                Place Order - â‚¹{total}
                <FiCheck className="ml-2" size={16} />
              </>
            )}
          </button>
        </div>
      </form>
    </div>
  );

  const renderConfirmationStep = () => (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-8 text-center">
      <div className="w-20 h-20 bg-green-100 dark:bg-green-900/30 rounded-full flex items-center justify-center mx-auto mb-6">
        <FiCheck className="text-green-500 dark:text-green-400" size={40} />
      </div>
      
      <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">
        Order Placed Successfully!
      </h2>
      
      <p className="text-gray-600 dark:text-gray-400 mb-6">
        Thank you for your order. We'll start preparing your delicious meal right away!
      </p>
      
      <div className="bg-gray-50 dark:bg-gray-700 rounded-lg p-6 mb-6">
        <div className="flex items-center justify-between mb-2">
          <span className="text-gray-600 dark:text-gray-400">Order Total:</span>
          <span className="font-bold text-2xl text-orange-500">â‚¹{total}</span>
        </div>
        <div className="text-sm text-gray-500 dark:text-gray-400">
          Estimated delivery: 25-35 minutes
        </div>
      </div>
      
      <div className="text-sm text-gray-500 dark:text-gray-400 mb-6">
        Redirecting to order tracking in 3 seconds...
      </div>
      
      <button
        onClick={() => navigate('/orders')}
        className="px-6 py-3 bg-orange-500 text-white rounded-lg hover:bg-orange-600 transition-colors"
      >
        Track Your Order
      </button>
    </div>
  );

  const renderOrderSummary = () => (
    <div className="bg-white dark:bg-gray-800 rounded-2xl shadow-lg p-6 sticky top-8">
      <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">Order Summary</h3>
      
      <div className="space-y-3 mb-6 max-h-60 overflow-y-auto">
        {cart.map((item) => {
          const itemPrice = item.price || 0;
          const itemQuantity = item.quantity || 1;
          const itemTotal = itemPrice * itemQuantity;
          
          return (
            <div key={item._id || item.id} className="flex items-center space-x-3 p-2 bg-gray-50 dark:bg-gray-700 rounded-lg">
              <img
                src={item.imageUrl || item.image || '/favicon.ico'}
                alt={item.name || 'Food item'}
                className="w-12 h-12 rounded-lg object-cover border border-gray-200 dark:border-gray-600"
                onError={(e) => {
                  e.target.src = '/favicon.ico';
                }}
              />
              <div className="flex-1 min-w-0">
                <h4 className="font-medium text-gray-900 dark:text-white truncate">
                  {item.name || 'Food Item'}
                </h4>
                <p className="text-sm text-gray-500 dark:text-gray-400">
                  {itemQuantity} Ã— â‚¹{itemPrice.toFixed(2)} = â‚¹{itemTotal.toFixed(2)}
                </p>
              </div>
              <div className="text-right">
                <div className="font-semibold text-orange-600 dark:text-orange-400">
                  â‚¹{itemTotal.toFixed(2)}
                </div>
              </div>
            </div>
          );
        })}
      </div>

      <div className="border-t dark:border-gray-600 pt-4 space-y-2">
        <div className="flex justify-between text-sm">
          <span className="text-gray-600 dark:text-gray-400">Subtotal</span>
          <span className="text-gray-900 dark:text-white">â‚¹{subtotal.toFixed(2)}</span>
        </div>
        <div className="flex justify-between text-sm">
          <span className="text-gray-600 dark:text-gray-400">Delivery Fee</span>
          <span className="text-gray-900 dark:text-white">
            {deliveryFee === 0 ? 'FREE' : `â‚¹${deliveryFee}`}
          </span>
        </div>
        <div className="flex justify-between text-sm">
          <span className="text-gray-600 dark:text-gray-400">Tax (5%)</span>
          <span className="text-gray-900 dark:text-white">â‚¹{tax.toFixed(2)}</span>
        </div>
        <div className="flex justify-between text-lg font-bold border-t dark:border-gray-600 pt-2">
          <span className="text-gray-900 dark:text-white">Total</span>
          <span className="text-orange-500">â‚¹{total.toFixed(2)}</span>
        </div>
      </div>

      {deliveryFee === 0 && (
        <div className="mt-4 text-center">
          <span className="inline-flex items-center text-sm text-green-600 dark:text-green-400 bg-green-100 dark:bg-green-900/30 px-3 py-1 rounded-full">
            <FiTruck className="mr-2" size={14} />
            FREE Delivery!
          </span>
        </div>
      )}
    </div>
  );

  if (!user || !cart || cart.length === 0) {
    return null; // Will be redirected by useEffect
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 py-8">
      <div className="max-w-7xl mx-auto px-4">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-3xl font-bold text-gray-900 dark:text-white mb-2">Checkout</h1>
          <p className="text-gray-600 dark:text-gray-400">Complete your order in {3 - currentStep + 1} simple steps</p>
        </div>

        {/* Step Indicator */}
        {renderStepIndicator()}

        <div className="grid lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2">
            {currentStep === 1 && renderAddressStep()}
            {currentStep === 2 && renderPaymentStep()}
            {currentStep === 3 && renderConfirmationStep()}
          </div>

          {/* Order Summary */}
          <div className="lg:col-span-1">
            {currentStep !== 3 && renderOrderSummary()}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ModernCheckout;
