import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../../Context/CartContext';
import { AuthContext } from '../../Context/AuthContext';
import { useDarkMode } from '../../Context/DarkModeContext';

const PaymentGateway = () => {
  const { cartItems } = useContext(CartContext);
  const { user } = useContext(AuthContext);
  const { isDarkMode } = useDarkMode();
  const navigate = useNavigate();

  useEffect(() => {
    // Redirect if no user or cart items
    if (!user) {
      navigate('/login');
      return;
    }
    if (!cartItems || cartItems.length === 0) {
      navigate('/cart');
      return;
    }
  }, [user, cartItems, navigate]);

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto text-center">
          <h2 className="text-3xl font-bold mb-8">Payment Processing</h2>
          <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md p-8`}>
            <p className="text-lg mb-6">
              Payment processing has been integrated into the checkout flow.
            </p>
            <button 
              onClick={() => navigate('/checkout')}
              className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
            >
              Go to Checkout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentGateway;
