import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
const API = `${API_BASE_URL}/api/orders`;

// Configure axios to send credentials with all requests
axios.defaults.withCredentials = true;

const orderService = {
  createOrder: async (orderData) => {
    try {
      const res = await axios.post(`${API}`, orderData);
      return res.data;
    } catch (error) {
      console.error('Create order error:', error);
      throw error;
    }
  },
  
  getOrders: async () => {
    try {
      const res = await axios.get(`${API}`);
      return res.data;
    } catch (error) {
      console.error('Get orders error:', error);
      throw error;
    }
  },
  
  getOrderById: async (orderId) => {
    try {
      const res = await axios.get(`${API}/${orderId}`);
      return res.data;
    } catch (error) {
      console.error('Get order by ID error:', error);
      throw error;
    }
  },
  
  // Legacy methods for backward compatibility
  placeOrder: async (orderData) => {
    const res = await orderService.createOrder(orderData);
    return res.order;
  },
  
  getAllOrders: async () => {
    const res = await orderService.getOrders();
    return res;
  },
  
  updateOrderStatus: async (orderId, status) => {
    try {
      await axios.put(`${API}/${orderId}/status`, { status });
    } catch (error) {
      console.error('Update order status error:', error);
      throw error;
    }
  },

  trackOrder: async (orderId) => {
    try {
      const res = await axios.get(`${API}/${orderId}/track`);
      return res.data;
    } catch (error) {
      console.error('Track order error:', error);
      throw error;
    }
  },

  cancelOrder: async (orderId, reason) => {
    try {
      const res = await axios.put(`${API}/${orderId}/cancel`, { reason });
      return res.data;
    } catch (error) {
      console.error('Cancel order error:', error);
      throw error;
    }
  },

  rateOrder: async (orderId, rating, review) => {
    try {
      const res = await axios.post(`${API}/${orderId}/rate`, { rating, review });
      return res.data;
    } catch (error) {
      console.error('Rate order error:', error);
      throw error;
    }
  }
};

export default orderService;
