import React from 'react';
import { useCart } from '../../Context/CartContext';
import { useNotification } from '../../Context/NotificationContext';
import { FiMinus, FiPlus, FiTrash2, FiShoppingCart } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import Loader from '../../components/Loader';

const Cart = () => {
  const { cartItems, loading, updateQuantity, removeFromCart, clearCart } = useCart();
  const { showNotification } = useNotification();

  const total = cartItems?.reduce((sum, item) => sum + (item.price * item.quantity), 0) || 0;
  const deliveryFee = total > 50 ? 0 : 5; // Free delivery over $50
  const tax = total * 0.08; // 8% tax
  const finalTotal = total + deliveryFee + tax;

  const handleUpdateQuantity = async (productId, newQuantity) => {
    try {
      await updateQuantity(productId, newQuantity);
      if (showNotification) {
        showNotification('Quantity updated', 'success');
      }
    } catch (error) {
      if (showNotification) {
        showNotification('Failed to update quantity', 'error');
      }
    }
  };

  const handleRemoveItem = async (productId) => {
    try {
      await removeFromCart(productId);
      if (showNotification) {
        showNotification('Item removed from cart', 'success');
      }
    } catch (error) {
      if (showNotification) {
        showNotification('Failed to remove item', 'error');
      }
    }
  };

  const handleClearCart = async () => {
    try {
      await clearCart();
      if (showNotification) {
        showNotification('Cart cleared', 'success');
      }
    } catch (error) {
      if (showNotification) {
        showNotification('Failed to clear cart', 'error');
      }
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <Loader size="large" message="Loading your cart..." />
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-yellow-100 via-red-100 to-pink-100 p-4">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-extrabold text-center text-gray-800 mb-8">Your Cart</h1>

        {cart.length === 0 ? (
          <div className="text-center py-16">
            <div className="text-6xl mb-4">ðŸ›’</div>
            <h2 className="text-2xl font-semibold text-gray-600 mb-4">Your cart is empty</h2>
            <p className="text-gray-500 mb-6">Add some delicious items to get started!</p>
            <a href="/products" className="px-6 py-3 bg-gradient-to-r from-yellow-400 via-red-400 to-pink-500 text-white font-semibold rounded-xl shadow-lg hover:scale-105 transition-all duration-300">
              Browse Products
            </a>
          </div>
        ) : (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Cart Items */}
            <div className="lg:col-span-2 space-y-4">
              {cart.map(item => (
                <div key={item.product._id} className="bg-white rounded-2xl shadow-lg p-6 flex items-center space-x-4 hover:shadow-xl transition-shadow">
                  <img
                    src={item.product.imageUrl || '/favicon.ico'}
                    alt={item.product.name}
                    className="w-20 h-20 object-cover rounded-xl"
                    onError={e => { e.target.src = '/favicon.ico'; }}
                  />
                  <div className="flex-1">
                    <h3 className="text-lg font-semibold text-gray-800">{item.product.name}</h3>
                    <p className="text-gray-500 text-sm">{item.product.description}</p>
                    <p className="text-green-600 font-bold">â‚¹{item.price}</p>
                  </div>
                  <div className="flex items-center space-x-2">
                    <button
                      onClick={() => updateQuantity(item.product._id, Math.max(1, item.quantity - 1))}
                      className="p-2 bg-gray-100 rounded-full hover:bg-gray-200 transition-colors"
                    >
                      <FiMinus />
                    </button>
                    <span className="px-3 py-1 bg-yellow-100 rounded-lg font-semibold">{item.quantity}</span>
                    <button
                      onClick={() => updateQuantity(item.product._id, item.quantity + 1)}
                      className="p-2 bg-gray-100 rounded-full hover:bg-gray-200 transition-colors"
                    >
                      <FiPlus />
                    </button>
                  </div>
                  <button
                    onClick={() => removeFromCart(item.product._id)}
                    className="p-2 text-red-500 hover:bg-red-50 rounded-full transition-colors"
                  >
                    <FiTrash2 />
                  </button>
                </div>
              ))}
            </div>

            {/* Order Summary */}
            <div className="bg-white rounded-2xl shadow-lg p-6 h-fit">
              <h2 className="text-2xl font-bold text-gray-800 mb-6">Order Summary</h2>
              <div className="space-y-3 mb-6">
                <div className="flex justify-between">
                  <span>Subtotal</span>
                  <span>â‚¹{total}</span>
                </div>
                <div className="flex justify-between">
                  <span>Delivery Fee</span>
                  <span className={deliveryFee === 0 ? 'text-green-600' : ''}>
                    {deliveryFee === 0 ? 'Free' : `â‚¹${deliveryFee}`}
                  </span>
                </div>
                <hr />
                <div className="flex justify-between text-xl font-bold">
                  <span>Total</span>
                  <span>â‚¹{finalTotal}</span>
                </div>
              </div>
              <button
                onClick={() => alert('Proceeding to checkout...')}
                className="w-full py-3 bg-gradient-to-r from-yellow-400 via-red-400 to-pink-500 text-white font-semibold rounded-xl shadow-lg hover:scale-105 transition-all duration-300"
              >
                Proceed to Checkout
              </button>
              <button
                onClick={clearCart}
                className="w-full py-2 mt-3 bg-gray-100 text-gray-700 font-semibold rounded-xl hover:bg-gray-200 transition-all duration-300"
              >
                Clear Cart
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
