import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { useCart } from '../../Context/CartContext';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Home = () => {
  const { addToCart } = useCart();
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);

  // Sample data as fallback
  const sampleProducts = [
    {
      _id: '1',
      name: 'Margherita Pizza',
      price: 12.99,
      description: 'Fresh tomatoes, mozzarella, basil',
      category: 'Pizza',
      image: '/images/pizza1.jpg'
    },
    {
      _id: '2',
      name: 'Chicken Burger',
      price: 15.99,
      description: 'Grilled chicken with fresh lettuce',
      category: 'Burger',
      image: '/images/burger1.jpg'
    },
    {
      _id: '3',
      name: 'Caesar Salad',
      price: 10.99,
      description: 'Crispy romaine with parmesan',
      category: 'Salads',
      image: '/images/salad1.jpg'
    },
    {
      _id: '4',
      name: 'Chocolate Cake',
      price: 8.99,
      description: 'Rich chocolate cake with frosting',
      category: 'Desserts',
      image: '/images/dessert1.jpg'
    },
    {
      _id: '5',
      name: 'Pepperoni Pizza',
      price: 14.99,
      description: 'Classic pepperoni with cheese',
      category: 'Pizza',
      image: '/images/pizza2.jpg'
    },
    {
      _id: '6',
      name: 'Veggie Burger',
      price: 13.99,
      description: 'Plant-based patty with fresh toppings',
      category: 'Burger',
      image: '/images/burger2.jpg'
    }
  ];

  // Filter products based on selected category
  const filteredProducts = selectedCategory 
    ? featuredProducts.filter(product => product.category === selectedCategory)
    : featuredProducts;

  // Fetch products from backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_BASE_URL}/products`);
        const products = response.data;
        
        // Get first 12 products as featured
        setFeaturedProducts(products.slice(0, 12));
        
        // Extract unique categories
        const uniqueCategories = [...new Set(products.map(product => product.category))];
        setCategories(uniqueCategories);
        
      } catch (error) {
        console.error('Failed to fetch products:', error);
        
        // Fallback to sample data
        setFeaturedProducts(sampleProducts);
        setCategories(['Pizza', 'Burger', 'Salads', 'Desserts']);
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleAddToCart = async (product) => {
    try {
      await addToCart(product, 1);
      alert(`${product.name} added to cart!`);
    } catch (error) {
      console.error('Failed to add to cart:', error);
      alert('Failed to add item to cart');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-orange-500 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading delicious food...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <section className="relative bg-gradient-to-r from-orange-500 to-red-600 text-white py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-5xl md:text-6xl font-bold mb-6">
              Delicious Food
              <span className="block text-yellow-300">Delivered Fast</span>
            </h1>
            <p className="text-xl md:text-2xl mb-8 max-w-3xl mx-auto opacity-90">
              Fresh ingredients, amazing flavors, and lightning-fast delivery to your door
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link 
                to="/products" 
                className="px-8 py-4 bg-white text-orange-600 font-semibold rounded-lg hover:bg-gray-100 transition-colors"
              >
                Order Now
              </Link>
              <Link 
                to="/products" 
                className="px-8 py-4 border-2 border-white text-white font-semibold rounded-lg hover:bg-white hover:text-orange-600 transition-colors"
              >
                View Menu
              </Link>
            </div>
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
            <div className="text-center">
              <div className="text-4xl font-bold text-orange-600 mb-2">1000+</div>
              <div className="text-gray-600">Happy Customers</div>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-orange-600 mb-2">500+</div>
              <div className="text-gray-600">Delicious Dishes</div>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-orange-600 mb-2">30min</div>
              <div className="text-gray-600">Average Delivery</div>
            </div>
            <div className="text-center">
              <div className="text-4xl font-bold text-orange-600 mb-2">24/7</div>
              <div className="text-gray-600">Customer Support</div>
            </div>
          </div>
        </div>
      </section>

      {/* Categories Section */}
      <section className="py-16 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">Browse Categories</h2>
            <p className="text-xl text-gray-600">Explore our delicious menu categories</p>
          </div>

          {/* Category Filter */}
          <div className="flex flex-wrap justify-center gap-4 mb-8">
            <button
              onClick={() => setSelectedCategory('')}
              className={`px-6 py-3 rounded-full font-semibold transition-colors ${
                selectedCategory === '' 
                  ? 'bg-orange-500 text-white' 
                  : 'bg-white text-gray-700 hover:bg-orange-100'
              }`}
            >
              All Items
            </button>
            {categories.map(category => (
              <button
                key={category}
                onClick={() => setSelectedCategory(category)}
                className={`px-6 py-3 rounded-full font-semibold transition-colors ${
                  selectedCategory === category 
                    ? 'bg-orange-500 text-white' 
                    : 'bg-white text-gray-700 hover:bg-orange-100'
                }`}
              >
                {category}
              </button>
            ))}
          </div>

          {/* Featured Products */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
            {filteredProducts.map(product => (
              <div key={product._id} className="bg-white rounded-xl shadow-sm hover:shadow-lg transition-shadow p-6 border border-gray-100">
                <div className="aspect-w-1 aspect-h-1 mb-4">
                  <div className="w-full h-48 bg-gradient-to-br from-orange-100 to-red-100 rounded-lg flex items-center justify-center">
                    <span className="text-4xl">
                      {product.category === 'Pizza' && 'üçï'}
                      {product.category === 'Burger' && 'üçî'}
                      {product.category === 'Salads' && 'ü•ó'}
                      {product.category === 'Desserts' && 'üç∞'}
                      {!['Pizza', 'Burger', 'Salads', 'Desserts'].includes(product.category) && 'üçΩÔ∏è'}
                    </span>
                  </div>
                </div>
                
                <h3 className="text-lg font-semibold text-gray-900 mb-2">{product.name}</h3>
                <p className="text-gray-600 text-sm mb-4 line-clamp-2">{product.description}</p>
                
                <div className="flex items-center justify-between">
                  <span className="text-2xl font-bold text-orange-600">${product.price}</span>
                  <button
                    onClick={() => handleAddToCart(product)}
                    className="px-4 py-2 bg-gradient-to-r from-orange-500 to-red-600 text-white rounded-lg hover:from-orange-600 hover:to-red-700 transition-colors transform hover:scale-105"
                  >
                    Add to Cart
                  </button>
                </div>
                
                <div className="mt-3">
                  <span className="inline-block px-3 py-1 bg-orange-100 text-orange-800 text-xs font-medium rounded-full">
                    {product.category}
                  </span>
                </div>
              </div>
            ))}
          </div>

          <div className="text-center mt-12">
            <Link 
              to="/products" 
              className="px-8 py-4 bg-gradient-to-r from-orange-500 to-red-600 text-white font-semibold rounded-lg hover:from-orange-600 hover:to-red-700 transition-colors transform hover:scale-105 inline-block"
            >
              View All Products
            </Link>
          </div>
        </div>
      </section>

      {/* How it Works */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-4xl font-bold text-gray-900 mb-4">How It Works</h2>
            <p className="text-xl text-gray-600">Simple steps to get your favorite food</p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center">
              <div className="w-20 h-20 bg-gradient-to-br from-orange-100 to-red-100 rounded-full flex items-center justify-center mx-auto mb-6">
                <span className="text-3xl">üì±</span>
              </div>
              <h3 className="text-xl font-semibold mb-4 text-gray-900">1. Choose Your Food</h3>
              <p className="text-gray-600">Browse our delicious menu and select your favorite dishes from various categories</p>
            </div>
            <div className="text-center">
              <div className="w-20 h-20 bg-gradient-to-br from-orange-100 to-red-100 rounded-full flex items-center justify-center mx-auto mb-6">
                <span className="text-3xl">üí≥</span>
              </div>
              <h3 className="text-xl font-semibold mb-4 text-gray-900">2. Easy Payment</h3>
              <p className="text-gray-600">Secure and fast payment options including cards, digital wallets for your convenience</p>
            </div>
            <div className="text-center">
              <div className="w-20 h-20 bg-gradient-to-br from-orange-100 to-red-100 rounded-full flex items-center justify-center mx-auto mb-6">
                <span className="text-3xl">üöö</span>
              </div>
              <h3 className="text-xl font-semibold mb-4 text-gray-900">3. Fast Delivery</h3>
              <p className="text-gray-600">Get your food delivered hot and fresh to your door within 30 minutes</p>
            </div>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-16 bg-gradient-to-r from-orange-500 to-red-600 text-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-4xl font-bold mb-4">Ready to Order?</h2>
          <p className="text-xl mb-8 opacity-90">Join thousands of satisfied customers and get your favorite food delivered now!</p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link 
              to="/products" 
              className="px-8 py-4 bg-white text-orange-600 font-semibold rounded-lg hover:bg-gray-100 transition-colors transform hover:scale-105"
            >
              Browse Menu
            </Link>
            <Link 
              to="/cart" 
              className="px-8 py-4 border-2 border-white text-white font-semibold rounded-lg hover:bg-white hover:text-orange-600 transition-colors transform hover:scale-105"
            >
              View Cart
            </Link>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
