import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import HeroSection from '../../components/HeroSection';
import ProductGrid from '../../components/ProductGrid';
import { CartContext } from '../../Context/CartContext';
import productService from '../../services/productService';
import { FiTruck, FiShield, FiClock, FiStar, FiArrowRight, FiPlay } from 'react-icons/fi';

const Home = () => {
  const { addToCart } = React.useContext(CartContext);
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchFeaturedProducts = async () => {
      try {
        const data = await productService.getAllProducts();
        // Show only first 6 products as featured
        setFeaturedProducts(data.slice(0, 6));
      } catch (error) {
        console.error('Failed to fetch featured products:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchFeaturedProducts();
  }, []);

  const features = [
    {
      icon: FiTruck,
      title: 'Fast Delivery',
      description: 'Get your food delivered in 30 minutes or less',
      color: 'bg-green-500'
    },
    {
      icon: FiShield,
      title: 'Safe & Secure',
      description: 'Your payments and data are 100% secure',
      color: 'bg-blue-500'
    },
    {
      icon: FiClock,
      title: '24/7 Available',
      description: 'Order anytime, anywhere. We never close',
      color: 'bg-purple-500'
    },
    {
      icon: FiStar,
      title: 'Best Quality',
      description: 'Only the freshest ingredients and best chefs',
      color: 'bg-yellow-500'
    }
  ];

  const stats = [
    { number: '10,000+', label: 'Happy Customers' },
    { number: '500+', label: 'Restaurant Partners' },
    { number: '1M+', label: 'Orders Delivered' },
    { number: '4.9/5', label: 'Customer Rating' }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <HeroSection />

      {/* Features Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
              Why Choose FoodDelivery?
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              We're not just another food delivery app. We're your partner in bringing delicious meals to your table.
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {features.map((feature, index) => (
              <div 
                key={index}
                className="text-center p-6 rounded-2xl hover:shadow-lg transition-all duration-300 animate-slideUp"
                style={{ animationDelay: `${index * 0.1}s` }}
              >
                <div className={`w-16 h-16 ${feature.color} rounded-2xl flex items-center justify-center mx-auto mb-4 transform hover:rotate-6 transition-transform duration-300`}>
                  <feature.icon className="text-white" size={24} />
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">{feature.title}</h3>
                <p className="text-gray-600">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 bg-gradient-to-r from-orange-50 to-red-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-2 lg:grid-cols-4 gap-8">
            {stats.map((stat, index) => (
              <div key={index} className="text-center">
                <div className="text-3xl lg:text-4xl font-bold text-orange-600 mb-2">
                  {stat.number}
                </div>
                <div className="text-gray-600 font-medium">{stat.label}</div>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between mb-12">
            <div>
              <h2 className="text-3xl md:text-4xl font-bold text-gray-900 mb-4">
                Featured Dishes
              </h2>
              <p className="text-xl text-gray-600">
                Discover our most popular and delicious offerings
              </p>
            </div>
            <Link 
              to="/products"
              className="hidden md:flex items-center space-x-2 bg-orange-500 hover:bg-orange-600 text-white font-medium px-6 py-3 rounded-xl transition-colors"
            >
              <span>View All</span>
              <FiArrowRight size={16} />
            </Link>
          </div>

          <ProductGrid 
            products={featuredProducts}
            loading={loading}
            onAddToCart={addToCart}
            columns="grid-cols-1 sm:grid-cols-2 lg:grid-cols-3"
            emptyMessage="Featured products will appear here"
          />

          <div className="text-center mt-12 md:hidden">
            <Link 
              to="/products"
              className="inline-flex items-center space-x-2 bg-orange-500 hover:bg-orange-600 text-white font-medium px-8 py-3 rounded-xl transition-colors"
            >
              <span>View All Menu</span>
              <FiArrowRight size={16} />
            </Link>
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-16 bg-gradient-to-r from-orange-500 to-red-500">
        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <h2 className="text-3xl md:text-4xl font-bold text-white mb-4">
            Ready to Order?
          </h2>
          <p className="text-xl text-orange-100 mb-8 max-w-2xl mx-auto">
            Join thousands of satisfied customers and get your favorite food delivered in minutes.
          </p>
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <Link 
              to="/products"
              className="inline-flex items-center justify-center space-x-2 bg-white text-orange-600 font-semibold px-8 py-4 rounded-xl hover:bg-gray-50 transition-colors shadow-lg"
            >
              <span>Browse Menu</span>
              <FiArrowRight size={18} />
            </Link>
            <button className="inline-flex items-center justify-center space-x-2 bg-transparent border-2 border-white text-white font-semibold px-8 py-4 rounded-xl hover:bg-white hover:text-orange-600 transition-all duration-300">
              <FiPlay size={18} />
              <span>Watch Demo</span>
            </button>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
