import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';

export const CartContext = createContext();

// Custom hook for using cart context with safe fallback
export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    // Return safe fallback instead of throwing error
    return {
      cartItems: [],
      loading: false,
      error: null,
      addToCart: () => Promise.resolve(),
      updateQuantity: () => Promise.resolve(),
      removeFromCart: () => Promise.resolve(),
      clearCart: () => Promise.resolve(),
      getCartTotal: () => 0,
      getCartCount: () => 0
    };
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Load cart from localStorage on mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      try {
        const parsedCart = JSON.parse(savedCart);
        if (Array.isArray(parsedCart)) {
          setCartItems(parsedCart);
        }
      } catch (err) {
        console.error('Failed to parse saved cart:', err);
        localStorage.removeItem('cart');
      }
    }
  }, []);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
  }, [cartItems]);

  // Add item to cart
  const addToCart = useCallback(async (product, quantity = 1) => {
    return new Promise((resolve) => {
      setCartItems(prevItems => {
        const existingItem = prevItems.find(item => 
          (item._id || item.id) === (product._id || product.id)
        );

        if (existingItem) {
          // Update existing item quantity
          return prevItems.map(item =>
            (item._id || item.id) === (product._id || product.id)
              ? { ...item, quantity: (item.quantity || 1) + quantity }
              : item
          );
        } else {
          // Add new item
          return [...prevItems, { 
            ...product, 
            quantity,
            _id: product._id || product.id || Date.now().toString()
          }];
        }
      });
      resolve();
    });
  }, []);

  // Update item quantity
  const updateQuantity = useCallback(async (itemId, newQuantity) => {
    return new Promise((resolve) => {
      if (newQuantity <= 0) {
        removeFromCart(itemId).then(resolve);
        return;
      }

      setCartItems(prevItems =>
        prevItems.map(item =>
          (item._id || item.id) === itemId
            ? { ...item, quantity: newQuantity }
            : item
        )
      );
      resolve();
    });
  }, []);

  // Remove item from cart
  const removeFromCart = useCallback(async (itemId) => {
    return new Promise((resolve) => {
      setCartItems(prevItems =>
        prevItems.filter(item => (item._id || item.id) !== itemId)
      );
      resolve();
    });
  }, []);

  // Clear entire cart
  const clearCart = useCallback(async () => {
    return new Promise((resolve) => {
      setCartItems([]);
      resolve();
    });
  }, []);

  // Calculate cart total
  const getCartTotal = useCallback(() => {
    return cartItems.reduce((total, item) => {
      return total + ((item.price || 0) * (item.quantity || 1));
    }, 0);
  }, [cartItems]);

  // Get total item count
  const getCartCount = useCallback(() => {
    return cartItems.reduce((count, item) => count + (item.quantity || 1), 0);
  }, [cartItems]);

  const value = {
    cartItems,
    loading,
    error,
    addToCart,
    updateQuantity,
    removeFromCart,
    clearCart,
    getCartTotal,
    getCartCount
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
