import React, { useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../../Context/CartContext';
import { AuthContext } from '../../Context/AuthContext';
import { OrderContext } from '../../Context/OrderContext';
import {
  FiCreditCard,
  FiSmartphone,
  FiDollarSign,
  FiMapPin,
  FiUser,
  FiMail,
  FiPhone,
  FiHome,
  FiEdit3,
  FiCheck,
  FiArrowLeft,
  FiArrowRight,
  FiTruck,
  FiClock,
  FiShield,
  FiAlertCircle
} from 'react-icons/fi';

const Checkout = () => {
  const { cart, getCartTotal, clearCart } = useContext(CartContext);
  const { user, isAuthenticated } = useContext(AuthContext);
  const { createOrder, loading: orderLoading } = useContext(OrderContext);
  const navigate = useNavigate();

  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    // Personal Info
    firstName: user?.name?.split(' ')[0] || '',
    lastName: user?.name?.split(' ')[1] || '',
    email: user?.email || '',
    phone: user?.phone || '',
    
    // Address Info
    street: '',
    apartment: '',
    city: '',
    state: '',
    pincode: '',
    landmark: '',
    addressType: 'home',
    
    // Payment Info
    paymentMethod: 'razorpay',
    
    // Order Info
    deliveryInstructions: '',
    isGift: false,
    giftMessage: ''
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);

  // Calculate totals
  const subtotal = getCartTotal ? getCartTotal() : 0;
  const deliveryFee = subtotal > 299 ? 0 : 40;
  const tax = Math.round(subtotal * 0.05);
  const total = subtotal + deliveryFee + tax;

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    if (!cart || cart.length === 0) {
      navigate('/cart');
      return;
    }
  }, [isAuthenticated, cart, navigate]);

  const validateStep = (step) => {
    const newErrors = {};

    if (step === 1) {
      if (!formData.firstName.trim()) newErrors.firstName = 'First name is required';
      if (!formData.lastName.trim()) newErrors.lastName = 'Last name is required';
      if (!formData.email.trim()) newErrors.email = 'Email is required';
      if (!formData.phone.trim()) newErrors.phone = 'Phone is required';
      if (formData.email && !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
        newErrors.email = 'Invalid email format';
      }
      if (formData.phone && !/^[+]?[\d\s-()]{10,}$/.test(formData.phone)) {
        newErrors.phone = 'Invalid phone number';
      }
    }

    if (step === 2) {
      if (!formData.street.trim()) newErrors.street = 'Street address is required';
      if (!formData.city.trim()) newErrors.city = 'City is required';
      if (!formData.state.trim()) newErrors.state = 'State is required';
      if (!formData.pincode.trim()) newErrors.pincode = 'Pincode is required';
      if (formData.pincode && !/^\d{6}$/.test(formData.pincode)) {
        newErrors.pincode = 'Pincode must be 6 digits';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
    
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const nextStep = () => {
    if (validateStep(currentStep)) {
      setCurrentStep(prev => Math.min(prev + 1, 4));
    }
  };

  const prevStep = () => {
    setCurrentStep(prev => Math.max(prev - 1, 1));
  };

  const handlePayment = async () => {
    try {
      setLoading(true);

      // Create order
      const orderData = {
        items: cart.map(item => ({
          product: item.product?._id || item.id,
          quantity: item.quantity,
          price: item.product?.price || item.price
        })),
        totalAmount: total,
        deliveryAddress: {
          firstName: formData.firstName,
          lastName: formData.lastName,
          street: formData.street,
          apartment: formData.apartment,
          city: formData.city,
          state: formData.state,
          pincode: formData.pincode,
          landmark: formData.landmark,
          addressType: formData.addressType
        },
        contactInfo: {
          email: formData.email,
          phone: formData.phone
        },
        paymentMethod: formData.paymentMethod,
        deliveryInstructions: formData.deliveryInstructions,
        isGift: formData.isGift,
        giftMessage: formData.giftMessage
      };

      if (formData.paymentMethod === 'razorpay') {
        // Initialize Razorpay
        const options = {
          key: process.env.REACT_APP_RAZORPAY_KEY_ID || 'rzp_test_123456',
          amount: total * 100,
          currency: 'INR',
          name: 'Food Delivery App',
          description: 'Order Payment',
          handler: async function(response) {
            try {
              // Create order after successful payment
              const order = await createOrder({
                ...orderData,
                paymentId: response.razorpay_payment_id,
                paymentStatus: 'completed'
              });

              // Clear cart
              await clearCart();
              
              // Navigate to order confirmation
              navigate(`/orders/track/${order._id}`, {
                state: { orderSuccess: true }
              });
            } catch (error) {
              console.error('Order creation failed:', error);
              alert('Order creation failed. Please contact support.');
            }
          },
          prefill: {
            name: `${formData.firstName} ${formData.lastName}`,
            email: formData.email,
            contact: formData.phone
          },
          theme: {
            color: '#f97316'
          }
        };

        const rzp = new window.Razorpay(options);
        rzp.open();
      } else {
        // For other payment methods, create order directly
        const order = await createOrder(orderData);
        await clearCart();
        navigate(`/orders/track/${order._id}`, {
          state: { orderSuccess: true }
        });
      }
    } catch (error) {
      console.error('Payment failed:', error);
      alert('Payment failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const steps = [
    { number: 1, title: 'Personal Info', icon: FiUser },
    { number: 2, title: 'Delivery Address', icon: FiMapPin },
    { number: 3, title: 'Payment Method', icon: FiCreditCard },
    { number: 4, title: 'Review Order', icon: FiCheck }
  ];

  if (!isAuthenticated || !cart || cart.length === 0) {
    return null;
  }

  return (
    <div className="min-h-screen bg-gray-50 py-4 sm:py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => navigate('/cart')}
            className="flex items-center text-orange-600 hover:text-orange-700 mb-4"
          >
            <FiArrowLeft className="w-4 h-4 mr-2" />
            Back to Cart
          </button>
          <h1 className="text-3xl font-bold text-gray-900">Checkout</h1>
          <p className="text-gray-600 mt-2">{cart.length} items in your order</p>
        </div>

        {/* Progress Steps */}
        <div className="bg-white rounded-lg shadow-sm mb-8 p-6">
          <div className="flex items-center justify-between">
            {steps.map((step, index) => (
              <div key={step.number} className="flex items-center">
                <div className={`flex items-center justify-center w-10 h-10 rounded-full border-2 ${
                  currentStep >= step.number
                    ? 'bg-orange-500 border-orange-500 text-white'
                    : 'bg-white border-gray-300 text-gray-400'
                }`}>
                  <step.icon className="w-5 h-5" />
                </div>
                <div className="ml-3 hidden sm:block">
                  <p className={`text-sm font-medium ${
                    currentStep >= step.number ? 'text-orange-600' : 'text-gray-400'
                  }`}>
                    Step {step.number}
                  </p>
                  <p className={`text-sm ${
                    currentStep >= step.number ? 'text-gray-900' : 'text-gray-500'
                  }`}>
                    {step.title}
                  </p>
                </div>
                {index < steps.length - 1 && (
                  <div className={`hidden sm:block w-16 h-0.5 ml-6 ${
                    currentStep > step.number ? 'bg-orange-500' : 'bg-gray-300'
                  }`} />
                )}
              </div>
            ))}
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Form */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow-sm p-6">
              {/* Step 1: Personal Info */}
              {currentStep === 1 && (
                <div className="space-y-6">
                  <div>
                    <h2 className="text-xl font-semibold text-gray-900 mb-4">Personal Information</h2>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          First Name *
                        </label>
                        <input
                          type="text"
                          name="firstName"
                          value={formData.firstName}
                          onChange={handleInputChange}
                          className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                            errors.firstName ? 'border-red-500' : 'border-gray-300'
                          }`}
                        />
                        {errors.firstName && (
                          <p className="text-red-500 text-sm mt-1">{errors.firstName}</p>
                        )}
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Last Name *
                        </label>
                        <input
                          type="text"
                          name="lastName"
                          value={formData.lastName}
                          onChange={handleInputChange}
                          className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                            errors.lastName ? 'border-red-500' : 'border-gray-300'
                          }`}
                        />
                        {errors.lastName && (
                          <p className="text-red-500 text-sm mt-1">{errors.lastName}</p>
                        )}
                      </div>
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Email *
                        </label>
                        <div className="relative">
                          <FiMail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                          <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleInputChange}
                            className={`w-full pl-10 pr-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                              errors.email ? 'border-red-500' : 'border-gray-300'
                            }`}
                          />
                        </div>
                        {errors.email && (
                          <p className="text-red-500 text-sm mt-1">{errors.email}</p>
                        )}
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Phone *
                        </label>
                        <div className="relative">
                          <FiPhone className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                          <input
                            type="tel"
                            name="phone"
                            value={formData.phone}
                            onChange={handleInputChange}
                            placeholder="+91 98765 43210"
                            className={`w-full pl-10 pr-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                              errors.phone ? 'border-red-500' : 'border-gray-300'
                            }`}
                          />
                        </div>
                        {errors.phone && (
                          <p className="text-red-500 text-sm mt-1">{errors.phone}</p>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Step 2: Delivery Address */}
              {currentStep === 2 && (
                <div className="space-y-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-4">Delivery Address</h2>
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Street Address *
                      </label>
                      <input
                        type="text"
                        name="street"
                        value={formData.street}
                        onChange={handleInputChange}
                        className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                          errors.street ? 'border-red-500' : 'border-gray-300'
                        }`}
                      />
                      {errors.street && (
                        <p className="text-red-500 text-sm mt-1">{errors.street}</p>
                      )}
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Apartment/Suite (Optional)
                      </label>
                      <input
                        type="text"
                        name="apartment"
                        value={formData.apartment}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                      />
                    </div>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          City *
                        </label>
                        <input
                          type="text"
                          name="city"
                          value={formData.city}
                          onChange={handleInputChange}
                          className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                            errors.city ? 'border-red-500' : 'border-gray-300'
                          }`}
                        />
                        {errors.city && (
                          <p className="text-red-500 text-sm mt-1">{errors.city}</p>
                        )}
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          State *
                        </label>
                        <input
                          type="text"
                          name="state"
                          value={formData.state}
                          onChange={handleInputChange}
                          className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                            errors.state ? 'border-red-500' : 'border-gray-300'
                          }`}
                        />
                        {errors.state && (
                          <p className="text-red-500 text-sm mt-1">{errors.state}</p>
                        )}
                      </div>
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Pincode *
                        </label>
                        <input
                          type="text"
                          name="pincode"
                          value={formData.pincode}
                          onChange={handleInputChange}
                          className={`w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent ${
                            errors.pincode ? 'border-red-500' : 'border-gray-300'
                          }`}
                        />
                        {errors.pincode && (
                          <p className="text-red-500 text-sm mt-1">{errors.pincode}</p>
                        )}
                      </div>
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Landmark (Optional)
                      </label>
                      <input
                        type="text"
                        name="landmark"
                        value={formData.landmark}
                        onChange={handleInputChange}
                        placeholder="Near city mall, opposite park..."
                        className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                      />
                    </div>
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Address Type
                      </label>
                      <div className="flex gap-4">
                        {[
                          { value: 'home', label: 'Home', icon: FiHome },
                          { value: 'work', label: 'Work', icon: FiUser },
                          { value: 'other', label: 'Other', icon: FiMapPin }
                        ].map(type => (
                          <label key={type.value} className="flex items-center">
                            <input
                              type="radio"
                              name="addressType"
                              value={type.value}
                              checked={formData.addressType === type.value}
                              onChange={handleInputChange}
                              className="sr-only"
                            />
                            <div className={`flex items-center px-4 py-2 border rounded-lg cursor-pointer ${
                              formData.addressType === type.value
                                ? 'border-orange-500 bg-orange-50 text-orange-700'
                                : 'border-gray-300 bg-white text-gray-700'
                            }`}>
                              <type.icon className="w-4 h-4 mr-2" />
                              {type.label}
                            </div>
                          </label>
                        ))}
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Step 3: Payment Method */}
              {currentStep === 3 && (
                <div className="space-y-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-4">Payment Method</h2>
                  <div className="space-y-4">
                    {[
                      {
                        value: 'razorpay',
                        label: 'Razorpay (Cards, UPI, Wallets)',
                        icon: FiCreditCard,
                        description: 'Pay securely with cards, UPI, or digital wallets'
                      },
                      {
                        value: 'cod',
                        label: 'Cash on Delivery',
                        icon: FiDollarSign,
                        description: 'Pay when your order arrives'
                      }
                    ].map(method => (
                      <label key={method.value} className="block">
                        <input
                          type="radio"
                          name="paymentMethod"
                          value={method.value}
                          checked={formData.paymentMethod === method.value}
                          onChange={handleInputChange}
                          className="sr-only"
                        />
                        <div className={`border rounded-lg p-4 cursor-pointer ${
                          formData.paymentMethod === method.value
                            ? 'border-orange-500 bg-orange-50'
                            : 'border-gray-300 bg-white hover:bg-gray-50'
                        }`}>
                          <div className="flex items-center">
                            <method.icon className="w-6 h-6 mr-3 text-gray-600" />
                            <div>
                              <p className="font-medium text-gray-900">{method.label}</p>
                              <p className="text-sm text-gray-500">{method.description}</p>
                            </div>
                          </div>
                        </div>
                      </label>
                    ))}
                  </div>

                  {/* Additional Options */}
                  <div className="border-t pt-6">
                    <div className="space-y-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">
                          Delivery Instructions (Optional)
                        </label>
                        <textarea
                          name="deliveryInstructions"
                          value={formData.deliveryInstructions}
                          onChange={handleInputChange}
                          rows={3}
                          placeholder="Leave at door, ring doorbell, avoid stairs..."
                          className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                        />
                      </div>
                      
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          name="isGift"
                          checked={formData.isGift}
                          onChange={handleInputChange}
                          className="h-4 w-4 text-orange-600 focus:ring-orange-500 border-gray-300 rounded"
                        />
                        <label className="ml-2 block text-sm text-gray-900">
                          This is a gift
                        </label>
                      </div>

                      {formData.isGift && (
                        <div>
                          <label className="block text-sm font-medium text-gray-700 mb-1">
                            Gift Message
                          </label>
                          <textarea
                            name="giftMessage"
                            value={formData.giftMessage}
                            onChange={handleInputChange}
                            rows={2}
                            placeholder="Happy Birthday! Hope you enjoy this meal..."
                            className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                          />
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              )}

              {/* Step 4: Review Order */}
              {currentStep === 4 && (
                <div className="space-y-6">
                  <h2 className="text-xl font-semibold text-gray-900 mb-4">Review Your Order</h2>
                  
                  {/* Order Items */}
                  <div className="border rounded-lg p-4">
                    <h3 className="font-medium text-gray-900 mb-4">Order Items</h3>
                    <div className="space-y-3">
                      {cart.map((item, index) => (
                        <div key={index} className="flex items-center space-x-4">
                          <img
                            src={item.product?.imageUrl || item.imageUrl || '/favicon.ico'}
                            alt={item.product?.name || item.name}
                            className="w-12 h-12 bg-gray-200 rounded-lg object-cover"
                          />
                          <div className="flex-1">
                            <h4 className="font-medium text-gray-900">
                              {item.product?.name || item.name}
                            </h4>
                            <p className="text-sm text-gray-500">
                              Qty: {item.quantity}  {item.product?.price || item.price}
                            </p>
                          </div>
                          <p className="font-medium text-gray-900">
                            {((item.product?.price || item.price) * item.quantity).toFixed(2)}
                          </p>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Delivery Details */}
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                    <div className="border rounded-lg p-4">
                      <h3 className="font-medium text-gray-900 mb-3">Delivery Address</h3>
                      <div className="text-sm text-gray-600">
                        <p className="font-medium">{formData.firstName} {formData.lastName}</p>
                        <p>{formData.street}</p>
                        {formData.apartment && <p>{formData.apartment}</p>}
                        <p>{formData.city}, {formData.state} {formData.pincode}</p>
                        {formData.landmark && <p>Near: {formData.landmark}</p>}
                        <p className="mt-2">{formData.phone}</p>
                      </div>
                    </div>
                    
                    <div className="border rounded-lg p-4">
                      <h3 className="font-medium text-gray-900 mb-3">Payment Method</h3>
                      <p className="text-sm text-gray-600">
                        {formData.paymentMethod === 'razorpay' ? 'Razorpay (Online)' : 'Cash on Delivery'}
                      </p>
                      {formData.deliveryInstructions && (
                        <div className="mt-3">
                          <p className="text-sm font-medium text-gray-700">Delivery Instructions:</p>
                          <p className="text-sm text-gray-600">{formData.deliveryInstructions}</p>
                        </div>
                      )}
                    </div>
                  </div>
                </div>
              )}

              {/* Navigation Buttons */}
              <div className="flex justify-between pt-6 border-t">
                <button
                  onClick={prevStep}
                  disabled={currentStep === 1}
                  className="flex items-center px-6 py-2 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <FiArrowLeft className="w-4 h-4 mr-2" />
                  Previous
                </button>
                
                {currentStep < 4 ? (
                  <button
                    onClick={nextStep}
                    className="flex items-center px-6 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600"
                  >
                    Next
                    <FiArrowRight className="w-4 h-4 ml-2" />
                  </button>
                ) : (
                  <button
                    onClick={handlePayment}
                    disabled={loading || orderLoading}
                    className="flex items-center px-8 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    {loading || orderLoading ? 'Processing...' : 'Place Order'}
                    {!loading && !orderLoading && <FiArrowRight className="w-4 h-4 ml-2" />}
                  </button>
                )}
              </div>
            </div>
          </div>

          {/* Order Summary Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm p-6 sticky top-4">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Order Summary</h3>
              
              <div className="space-y-3">
                <div className="flex justify-between text-gray-600">
                  <span>Subtotal ({cart.length} items)</span>
                  <span>{subtotal.toFixed(2)}</span>
                </div>
                
                <div className="flex justify-between text-gray-600">
                  <div className="flex items-center">
                    <FiTruck className="w-4 h-4 mr-1" />
                    <span>Delivery</span>
                  </div>
                  <span className={deliveryFee === 0 ? 'text-green-600' : ''}>
                    {deliveryFee === 0 ? 'FREE' : `${deliveryFee}`}
                  </span>
                </div>

                <div className="flex justify-between text-gray-600">
                  <span>Tax (5%)</span>
                  <span>{tax}</span>
                </div>

                <hr />

                <div className="flex justify-between text-xl font-bold text-gray-900">
                  <span>Total</span>
                  <span>{total.toFixed(2)}</span>
                </div>
              </div>

              {/* Delivery Time */}
              <div className="mt-6 p-4 bg-orange-50 rounded-lg">
                <div className="flex items-center text-orange-800">
                  <FiClock className="w-5 h-5 mr-2" />
                  <span className="font-medium">Estimated Delivery</span>
                </div>
                <p className="text-orange-700 mt-1">25-35 minutes</p>
              </div>

              {/* Security Badge */}
              <div className="mt-4 flex items-center text-sm text-gray-500">
                <FiShield className="w-4 h-4 mr-2" />
                <span>SSL secured checkout</span>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Checkout;
