import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import HeroSection from '../../components/HeroSection';
import ProductGrid from '../../components/ProductGrid';
import CategoryFilter from '../../components/CategoryFilter';
import { useCart } from '../../Context/CartContext';
import { useProduct } from '../../Context/ProductContext';
import { useNotification } from '../../Context/NotificationContext';
import Loader from '../../components/Loader';
import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const Home = () => {
  const { addToCart } = useCart();
  const { showNotification } = useNotification();
  const [featuredProducts, setFeaturedProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [loading, setLoading] = useState(true);
  const [categories, setCategories] = useState([]);

  // Filter products based on selected category
  const filteredProducts = selectedCategory 
    ? featuredProducts.filter(product => product.category === selectedCategory)
    : featuredProducts;

  // Fetch products from backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const response = await axios.get(`${API_BASE_URL}/products`);
        const products = response.data;
        
        // Get first 12 products as featured
        setFeaturedProducts(products.slice(0, 12));
        
        // Extract unique categories
        const uniqueCategories = [...new Set(products.map(product => product.category))];
        setCategories(uniqueCategories);
        
      } catch (error) {
        console.error('Failed to fetch products:', error);
        showNotification('Failed to load products', 'error');
        
        // Fallback to sample data if API fails
        const sampleProducts = [
          {
            _id: '1',
            name: 'Margherita Pizza',
            description: 'Classic pizza with fresh mozzarella, tomatoes, and basil',
            price: 12.99,
            imageUrl: 'https://images.unsplash.com/photo-1604068549290-dea0e4a305ca?w=400',
            category: 'Pizza',
            rating: { average: 4.5, count: 128 },
            inStock: true
          },
          {
            _id: '2',
            name: 'Chicken Burger',
            description: 'Juicy grilled chicken with lettuce, tomato, and mayo',
            price: 9.99,
            imageUrl: 'https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=400',
            category: 'Burger',
            rating: { average: 4.3, count: 95 },
            inStock: true
          },
          {
            _id: '3',
            name: 'Caesar Salad',
            description: 'Fresh romaine lettuce with caesar dressing and croutons',
            price: 8.99,
            imageUrl: 'https://images.unsplash.com/photo-1512621776951-a57141f2eefd?w=400',
            category: 'Snacks',
            rating: { average: 4.2, count: 67 },
            inStock: true
          }
        ];
        setFeaturedProducts(sampleProducts);
        setCategories(['Pizza', 'Burger', 'Snacks']);
      } finally {
        setLoading(false);
      }
    };
    
    fetchProducts();
  }, [showNotification]);

  const handleAddToCart = async (product) => {
    try {
      await addToCart(product);
      showNotification(`${product.name} added to cart!`, 'success');
    } catch (error) {
      showNotification('Failed to add to cart', 'error');
    }
  };

  const features = [
    {
      icon: FiTruck,
      title: 'Fast Delivery',
      description: 'Get your food delivered in 30 minutes or less',
      color: 'bg-green-500'
    },
    {
      icon: FiShield,
      title: 'Safe & Secure',
      description: 'Your payments and data are 100% secure',
      color: 'bg-blue-500'
    },
    {
      icon: FiClock,
      title: '24/7 Available',
      description: 'Order anytime, anywhere. We never close',
      color: 'bg-purple-500'
    },
    {
      icon: FiStar,
      title: 'Best Quality',
      description: 'Only the freshest ingredients and best chefs',
      color: 'bg-yellow-500'
    }
  ];

  const stats = [
    { number: '10,000+', label: 'Happy Customers' },
    { number: '500+', label: 'Restaurant Partners' },
    { number: '1M+', label: 'Orders Delivered' },
    { number: '4.9/5', label: 'Customer Rating' }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <HeroSection />

      {/* Category Filter Section */}
      <section className="py-8 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-8">
            <h2 className="text-3xl font-bold text-gray-900 mb-2">
              Explore Our Menu
            </h2>
            <p className="text-lg text-gray-600">
              Discover delicious food from various categories
            </p>
          </div>
          <CategoryFilter 
            selectedCategory={selectedCategory}
            onCategorySelect={setSelectedCategory}
          />
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="py-12 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              {selectedCategory ? `${selectedCategory} Dishes` : 'Featured Dishes'} üçΩÔ∏è
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              {selectedCategory 
                ? `Delicious ${selectedCategory.toLowerCase()} options just for you`
                : 'Our most popular and highest-rated dishes, prepared with love and the finest ingredients'
              }
            </p>
          </div>
          <ProductGrid products={filteredProducts} loading={loading} />
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 bg-gradient-to-r from-orange-500 to-red-600">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-8 text-center text-white">
            <div className="animate-fadeIn">
              <div className="text-4xl font-bold mb-2">500+</div>
              <div className="text-lg opacity-90">Happy Customers</div>
            </div>
            <div className="animate-fadeIn delay-200">
              <div className="text-4xl font-bold mb-2">50+</div>
              <div className="text-lg opacity-90">Restaurant Partners</div>
            </div>
            <div className="animate-fadeIn delay-300">
              <div className="text-4xl font-bold mb-2">30min</div>
              <div className="text-lg opacity-90">Average Delivery</div>
            </div>
            <div className="animate-fadeIn delay-500">
              <div className="text-4xl font-bold mb-2">24/7</div>
              <div className="text-lg opacity-90">Customer Support</div>
            </div>
          </div>
        </div>
      </section>

      {/* How It Works Section */}
      <section className="py-16 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              How It Works
            </h2>
            <p className="text-xl text-gray-600">
              Simple steps to get your favorite food delivered
