import React, { useContext, useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { CartContext } from '../../Context/CartContext';
import { AuthContext } from '../../Context/AuthContext';
import { 
  FiPlus, 
  FiMinus, 
  FiTrash2, 
  FiShoppingCart, 
  FiArrowRight,
  FiMapPin,
  FiClock,
  FiTruck,
  FiTag,
  FiPercent,
  FiStar,
  FiHeart
} from 'react-icons/fi';

const ResponsiveCart = () => {
  const { 
    cart, 
    loading,
    addToCart,
    removeFromCart,
    updateQuantity,
    clearCart,
    getCartTotal
  } = useContext(CartContext);
  const { user, isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();

  const [deliveryFee, setDeliveryFee] = useState(40);
  const [discount, setDiscount] = useState(0);
  const [couponCode, setCouponCode] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [loadingCoupon, setLoadingCoupon] = useState(false);

  // Calculate totals
  const subtotal = getCartTotal ? getCartTotal() : 0;
  const cartCount = cart?.length || 0;
  const tax = Math.round(subtotal * 0.05); // 5% tax
  const finalTotal = Math.max(0, subtotal + deliveryFee + tax - discount);

  const handleQuantityChange = async (productId, newQuantity) => {
    try {
      if (newQuantity <= 0) {
        await removeFromCart(productId);
      } else {
        await updateQuantity(productId, newQuantity);
      }
    } catch (error) {
      console.error('Error updating quantity:', error);
    }
  };

  const handleApplyCoupon = async () => {
    if (!couponCode.trim()) return;
    
    setLoadingCoupon(true);
    try {
      // Mock coupon validation - replace with actual API call
      const coupons = {
        'SAVE10': { discount: 50, type: 'fixed', description: '₹50 off', minOrder: 200 },
        'FIRST20': { discount: 20, type: 'percentage', description: '20% off', minOrder: 300 },
        'DELIVERY': { discount: 40, type: 'delivery', description: 'Free delivery', minOrder: 100 }
      };

      const coupon = coupons[couponCode.toUpperCase()];
      if (coupon && subtotal >= coupon.minOrder) {
        let discountAmount = 0;
        if (coupon.type === 'fixed') {
          discountAmount = coupon.discount;
        } else if (coupon.type === 'percentage') {
          discountAmount = Math.round(subtotal * (coupon.discount / 100));
        } else if (coupon.type === 'delivery') {
          setDeliveryFee(0);
          discountAmount = 40;
        }
        
        setDiscount(discountAmount);
        setAppliedCoupon(coupon);
        setCouponCode('');
      } else if (coupon && subtotal < coupon.minOrder) {
        alert(`Minimum order amount ₹${coupon.minOrder} required for this coupon`);
      } else {
        alert('Invalid coupon code');
      }
    } catch (error) {
      console.error('Error applying coupon:', error);
      alert('Failed to apply coupon. Please try again.');
    } finally {
      setLoadingCoupon(false);
    }
  };

  const handleRemoveCoupon = () => {
    setDiscount(0);
    setAppliedCoupon(null);
    setDeliveryFee(40);
  };

  const handleProceedToCheckout = () => {
    if (!isAuthenticated) {
      // Store checkout intent and redirect to login
      localStorage.setItem('checkoutIntent', JSON.stringify({
        redirectTo: '/checkout',
        timestamp: Date.now()
      }));
      navigate('/login');
    } else {
      navigate('/checkout');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8 flex items-center justify-center">
        <div className="text-center">
          <div className="w-16 h-16 border-4 border-orange-200 border-t-orange-500 rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your cart...</p>
        </div>
      </div>
    );
  }

  if (!cart || cart.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center py-16">
            <div className="w-24 h-24 bg-gray-200 rounded-full flex items-center justify-center mx-auto mb-6">
              <FiShoppingCart className="w-12 h-12 text-gray-400" />
            </div>
            <h2 className="text-3xl font-bold text-gray-900 mb-4">Your cart is empty</h2>
            <p className="text-gray-600 mb-8 max-w-md mx-auto">
              Looks like you haven't added any items to your cart yet. Browse our menu and discover delicious meals!
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <Link
                to="/products"
                className="inline-flex items-center px-8 py-3 bg-orange-500 text-white font-semibold rounded-lg hover:bg-orange-600 transition-colors"
              >
                Browse Menu
                <FiArrowRight className="ml-2" />
              </Link>
              {isAuthenticated && (
                <Link
                  to="/orders"
                  className="inline-flex items-center px-8 py-3 border border-orange-500 text-orange-500 font-semibold rounded-lg hover:bg-orange-50 transition-colors"
                >
                  View Order History
                </Link>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-4 sm:py-8">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-6 sm:mb-8">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div>
              <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-2">Shopping Cart</h1>
              <p className="text-gray-600">{cartCount} {cartCount === 1 ? 'item' : 'items'} in your cart</p>
            </div>
            {isAuthenticated && user && (
              <div className="flex items-center space-x-3 bg-white px-4 py-2 rounded-lg shadow-sm">
                <div className="w-10 h-10 bg-orange-100 rounded-full flex items-center justify-center">
                  <span className="text-orange-600 font-semibold">
                    {user.name?.charAt(0)?.toUpperCase() || 'U'}
                  </span>
                </div>
                <div className="hidden sm:block">
                  <p className="text-sm font-medium text-gray-900">{user.name}</p>
                  <p className="text-xs text-gray-500">Logged in</p>
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 lg:gap-8">
          {/* Cart Items */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-xl shadow-sm overflow-hidden">
              <div className="p-4 sm:p-6 border-b border-gray-200">
                <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                  <h2 className="text-lg font-semibold text-gray-900">Cart Items</h2>
                  <button
                    onClick={clearCart}
                    className="inline-flex items-center px-4 py-2 text-red-600 hover:text-red-700 hover:bg-red-50 rounded-lg transition-colors text-sm font-medium"
                  >
                    <FiTrash2 className="w-4 h-4 mr-2" />
                    Clear Cart
                  </button>
                </div>
              </div>

              <div className="divide-y divide-gray-200">
                {cart.map((item, index) => (
                  <div key={`${item.product?._id || item.id}-${index}`} className="p-4 sm:p-6">
                    <div className="flex flex-col sm:flex-row gap-4">
                      {/* Product Image */}
                      <div className="flex-shrink-0 w-full sm:w-24 h-48 sm:h-24 bg-gray-200 rounded-lg overflow-hidden group">
                        <img
                          src={item.product?.imageUrl || item.imageUrl || '/favicon.ico'}
                          alt={item.product?.name || item.name}
                          className="w-full h-full object-cover group-hover:scale-105 transition-transform duration-300"
                          onError={(e) => {
                            e.target.src = '/favicon.ico';
                          }}
                        />
                      </div>

                      {/* Product Info */}
                      <div className="flex-1 min-w-0">
                        <div className="flex flex-col sm:flex-row sm:justify-between gap-2 sm:gap-4">
                          <div>
                            <h3 className="text-lg font-medium text-gray-900 mb-1 line-clamp-1">
                              {item.product?.name || item.name}
                            </h3>
                            <p className="text-sm text-gray-500 mb-2 line-clamp-2">
                              {item.product?.description || item.description}
                            </p>
                            <div className="flex flex-wrap items-center gap-4 text-sm text-gray-500">
                              <div className="flex items-center">
                                <FiMapPin className="w-4 h-4 mr-1" />
                                <span>{item.product?.category || item.category}</span>
                              </div>
                              <div className="flex items-center">
                                <FiStar className="w-4 h-4 mr-1 text-yellow-400" />
                                <span>4.5</span>
                              </div>
                              <div className="flex items-center">
                                <FiClock className="w-4 h-4 mr-1" />
                                <span>25-30 min</span>
                              </div>
                            </div>
                          </div>

                          {/* Price */}
                          <div className="text-right">
                            <div className="text-xl font-bold text-gray-900">
                              ₹{((item.product?.price || item.price) * item.quantity).toFixed(2)}
                            </div>
                            <div className="text-sm text-gray-500">
                              ₹{item.product?.price || item.price} each
                            </div>
                          </div>
                        </div>

                        {/* Quantity Controls and Actions */}
                        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4 mt-4">
                          <div className="flex items-center space-x-1">
                            <button
                              onClick={() => handleQuantityChange(item.product?._id || item.id, item.quantity - 1)}
                              className="w-10 h-10 rounded-full border border-gray-300 flex items-center justify-center hover:bg-gray-50 hover:border-orange-300 transition-colors"
                            >
                              <FiMinus className="w-4 h-4" />
                            </button>
                            <div className="w-16 h-10 flex items-center justify-center bg-gray-50 rounded-lg">
                              <span className="font-medium text-gray-900">{item.quantity}</span>
                            </div>
                            <button
                              onClick={() => handleQuantityChange(item.product?._id || item.id, item.quantity + 1)}
                              className="w-10 h-10 rounded-full border border-gray-300 flex items-center justify-center hover:bg-gray-50 hover:border-orange-300 transition-colors"
                            >
                              <FiPlus className="w-4 h-4" />
                            </button>
                          </div>

                          <div className="flex items-center gap-2">
                            <button
                              onClick={() => removeFromCart(item.product?._id || item.id)}
                              className="p-2 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded-lg transition-colors"
                            >
                              <FiTrash2 className="w-4 h-4" />
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* Order Summary */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-sm overflow-hidden sticky top-4">
              <div className="p-4 sm:p-6 border-b border-gray-200">
                <h2 className="text-lg font-semibold text-gray-900">Order Summary</h2>
              </div>

              <div className="p-4 sm:p-6 space-y-6">
                {/* Coupon Section */}
                <div className="border border-gray-200 rounded-lg p-4">
                  <div className="flex items-center mb-3">
                    <FiTag className="w-4 h-4 mr-2 text-orange-500" />
                    <span className="font-medium text-gray-900">Coupon Code</span>
                  </div>
                  
                  {appliedCoupon ? (
                    <div className="flex items-center justify-between p-3 bg-green-50 border border-green-200 rounded-lg">
                      <div className="flex items-center">
                        <FiPercent className="w-4 h-4 mr-2 text-green-600" />
                        <span className="text-sm font-medium text-green-800">
                          {appliedCoupon.description} applied
                        </span>
                      </div>
                      <button
                        onClick={handleRemoveCoupon}
                        className="text-red-500 hover:text-red-700 text-sm font-medium"
                      >
                        Remove
                      </button>
                    </div>
                  ) : (
                    <div className="space-y-2">
                      <div className="flex gap-2">
                        <input
                          type="text"
                          value={couponCode}
                          onChange={(e) => setCouponCode(e.target.value)}
                          placeholder="Enter coupon code"
                          className="flex-1 px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent text-sm"
                          onKeyPress={(e) => e.key === 'Enter' && handleApplyCoupon()}
                        />
                        <button
                          onClick={handleApplyCoupon}
                          disabled={!couponCode.trim() || loadingCoupon}
                          className="px-4 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600 disabled:opacity-50 disabled:cursor-not-allowed text-sm font-medium min-w-[70px]"
                        >
                          {loadingCoupon ? '...' : 'Apply'}
                        </button>
                      </div>
                      <div className="text-xs text-gray-500">
                        Try: SAVE10, FIRST20, DELIVERY
                      </div>
                    </div>
                  )}
                </div>

                {/* Price Breakdown */}
                <div className="space-y-3">
                  <div className="flex justify-between text-gray-600">
                    <span>Subtotal ({cartCount} items)</span>
                    <span>₹{subtotal.toFixed(2)}</span>
                  </div>
                  
                  <div className="flex justify-between text-gray-600">
                    <div className="flex items-center">
                      <FiTruck className="w-4 h-4 mr-1" />
                      <span>Delivery Fee</span>
                    </div>
                    <span className={deliveryFee === 0 ? 'text-green-600 line-through' : ''}>
                      ₹{deliveryFee}
                    </span>
                  </div>

                  <div className="flex justify-between text-gray-600">
                    <span>Tax (5%)</span>
                    <span>₹{tax}</span>
                  </div>

                  {discount > 0 && (
                    <div className="flex justify-between text-green-600">
                      <span>Discount</span>
                      <span>-₹{discount}</span>
                    </div>
                  )}

                  <hr className="border-gray-200" />

                  <div className="flex justify-between text-xl font-bold text-gray-900">
                    <span>Total</span>
                    <span>₹{finalTotal.toFixed(2)}</span>
                  </div>
                </div>

                {/* Delivery Info */}
                <div className="bg-orange-50 border border-orange-200 rounded-lg p-4">
                  <div className="flex items-center mb-2">
                    <FiClock className="w-4 h-4 mr-2 text-orange-600" />
                    <span className="text-sm font-medium text-orange-800">Estimated Delivery</span>
                  </div>
                  <p className="text-sm text-orange-700">25-35 minutes</p>
                  <p className="text-xs text-orange-600 mt-1">Free delivery on orders above ₹299</p>
                </div>

                {/* User Info */}
                {isAuthenticated && user && (
                  <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
                    <div className="flex items-center mb-2">
                      <FiMapPin className="w-4 h-4 mr-2 text-blue-600" />
                      <span className="text-sm font-medium text-blue-800">Delivering to</span>
                    </div>
                    <p className="text-sm text-blue-700 font-medium">{user.name || 'User'}</p>
                    <p className="text-sm text-blue-600">{user.email}</p>
                  </div>
                )}

                {/* Checkout Button */}
                <button
                  onClick={handleProceedToCheckout}
                  disabled={!cart || cart.length === 0}
                  className="w-full bg-orange-500 text-white py-4 px-6 rounded-lg font-semibold hover:bg-orange-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex items-center justify-center"
                >
                  {isAuthenticated ? 'Proceed to Checkout' : 'Login to Checkout'}
                  <FiArrowRight className="ml-2 w-5 h-5" />
                </button>

                <div className="flex flex-col sm:flex-row gap-3 text-center">
                  <Link
                    to="/products"
                    className="flex-1 text-orange-600 hover:text-orange-700 text-sm font-medium py-2"
                  >
                    Continue Shopping
                  </Link>
                  {isAuthenticated && (
                    <Link
                      to="/orders"
                      className="flex-1 text-gray-600 hover:text-gray-700 text-sm font-medium py-2"
                    >
                      Order History
                    </Link>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResponsiveCart;
