import React, { createContext, useState, useContext, useCallback, useEffect } from 'react';
import productService from '../services/productService';

export const ProductContext = createContext();

// Custom hook for using product context with safe fallback
export const useProduct = () => {
  const context = useContext(ProductContext);
  if (!context) {
    // Return safe fallback instead of throwing error
    return {
      products: [],
      categories: [],
      loading: false,
      error: null,
      fetchProducts: () => Promise.resolve([]),
      searchProducts: () => Promise.resolve([]),
      getProductsByCategory: () => []
    };
  }
  return context;
};

export const ProductProvider = ({ children }) => {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [filters, setFilters] = useState({
    category: '',
    priceRange: '',
    rating: '',
    searchQuery: '',
    availability: true
  });
  const [sortBy, setSortBy] = useState('name');
  const [favorites, setFavorites] = useState([]);
  const [currentProduct, setCurrentProduct] = useState(null);

  const { showError, showSuccess } = useNotification();

  // Load products from API
  const fetchProducts = useCallback(async (options = {}) => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await productService.getProducts(options);
      setProducts(response.data || []);
      
      // Extract unique categories
      const uniqueCategories = [...new Set(
        (response.data || []).map(product => product.category)
      )].filter(Boolean);
      setCategories(uniqueCategories);
      
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to fetch products';
      setError(errorMessage);
      showError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [showError]);

  // Get single product
  const fetchProduct = useCallback(async (productId) => {
    try {
      setLoading(true);
      const response = await productService.getProduct(productId);
      setCurrentProduct(response.data);
      return response.data;
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to fetch product';
      setError(errorMessage);
      showError(errorMessage);
      return null;
    } finally {
      setLoading(false);
    }
  }, [showError]);

  // Search products
  const searchProducts = useCallback(async (query, options = {}) => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await productService.searchProducts(query, options);
      setProducts(response.data || []);
      
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to search products';
      setError(errorMessage);
      showError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [showError]);

  // Filter products locally
  const getFilteredProducts = useCallback(() => {
    let filtered = [...products];

    // Category filter
    if (filters.category) {
      filtered = filtered.filter(product => 
        product.category?.toLowerCase() === filters.category.toLowerCase()
      );
    }

    // Price range filter
    if (filters.priceRange) {
      const [min, max] = filters.priceRange.split('-').map(Number);
      filtered = filtered.filter(product => {
        const price = product.price || 0;
        return price >= min && (max ? price <= max : true);
      });
    }

    // Rating filter
    if (filters.rating) {
      const minRating = parseFloat(filters.rating);
      filtered = filtered.filter(product => {
        const rating = product.averageRating || product.rating || 0;
        return rating >= minRating;
      });
    }

    // Search query filter
    if (filters.searchQuery) {
      const query = filters.searchQuery.toLowerCase();
      filtered = filtered.filter(product =>
        product.name?.toLowerCase().includes(query) ||
        product.description?.toLowerCase().includes(query) ||
        product.category?.toLowerCase().includes(query)
      );
    }

    // Availability filter
    if (filters.availability) {
      filtered = filtered.filter(product => product.available !== false);
    }

    // Sort products
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'name':
          return (a.name || '').localeCompare(b.name || '');
        case 'price-low':
          return (a.price || 0) - (b.price || 0);
        case 'price-high':
          return (b.price || 0) - (a.price || 0);
        case 'rating':
          return (b.averageRating || b.rating || 0) - (a.averageRating || a.rating || 0);
        case 'popular':
          return (b.orders || 0) - (a.orders || 0);
        case 'newest':
          return new Date(b.createdAt || 0) - new Date(a.createdAt || 0);
        default:
          return 0;
      }
    });

    return filtered;
  }, [products, filters, sortBy]);

  // Update filters
  const updateFilter = useCallback((filterName, value) => {
    setFilters(prev => ({
      ...prev,
      [filterName]: value
    }));
  }, []);

  // Clear filters
  const clearFilters = useCallback(() => {
    setFilters({
      category: '',
      priceRange: '',
      rating: '',
      searchQuery: '',
      availability: true
    });
  }, []);

  // Toggle favorite
  const toggleFavorite = useCallback(async (productId) => {
    try {
      const isFavorite = favorites.includes(productId);
      
      if (isFavorite) {
        // Remove from favorites
        await productService.removeFavorite(productId);
        setFavorites(prev => prev.filter(id => id !== productId));
        showSuccess('Removed from favorites');
      } else {
        // Add to favorites
        await productService.addFavorite(productId);
        setFavorites(prev => [...prev, productId]);
        showSuccess('Added to favorites');
      }
    } catch (err) {
      showError('Failed to update favorites');
    }
  }, [favorites, showSuccess, showError]);

  // Load favorites
  const loadFavorites = useCallback(async () => {
    try {
      const response = await productService.getFavorites();
      setFavorites(response.data || []);
    } catch (err) {
      // Silently fail for favorites
      console.error('Failed to load favorites:', err);
    }
  }, []);

  // Get product statistics
  const getProductStats = useCallback(() => {
    const stats = {
      total: products.length,
      categories: categories.length,
      averagePrice: 0,
      averageRating: 0,
      inStock: 0,
      outOfStock: 0
    };

    if (products.length > 0) {
      stats.averagePrice = products.reduce((sum, product) => sum + (product.price || 0), 0) / products.length;
      stats.averageRating = products.reduce((sum, product) => sum + (product.averageRating || product.rating || 0), 0) / products.length;
      stats.inStock = products.filter(product => product.available !== false).length;
      stats.outOfStock = products.filter(product => product.available === false).length;
    }

    return stats;
  }, [products, categories]);

  // Get featured products
  const getFeaturedProducts = useCallback(() => {
    return products
      .filter(product => product.featured || (product.averageRating || product.rating || 0) >= 4.0)
      .slice(0, 6);
  }, [products]);

  // Get products by category
  const getProductsByCategory = useCallback((category) => {
    return products.filter(product => 
      product.category?.toLowerCase() === category.toLowerCase()
    );
  }, [products]);

  // Initialize data
  useEffect(() => {
    fetchProducts();
    loadFavorites();
  }, [fetchProducts, loadFavorites]);

  const value = {
    // State
    products,
    categories,
    loading,
    error,
    filters,
    sortBy,
    favorites,
    currentProduct,
    
    // Derived state
    filteredProducts: getFilteredProducts(),
    productStats: getProductStats(),
    featuredProducts: getFeaturedProducts(),
    
    // Actions
    fetchProducts,
    fetchProduct,
    searchProducts,
    updateFilter,
    clearFilters,
    setSortBy,
    toggleFavorite,
    loadFavorites,
    setCurrentProduct,
    getProductsByCategory,
    
    // Utilities
    isProductFavorite: (productId) => favorites.includes(productId),
    hasFilters: Object.values(filters).some(value => value && value !== true),
    isLoading: loading,
    hasError: !!error
  };

  return (
    <ProductContext.Provider value={value}>
      {children}
    </ProductContext.Provider>
  );
};
