import React, { useEffect, useState } from 'react';
import productService from '../../services/productService';
import CategoryFilter from '../../components/CategoryFilter';

const Products = () => {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const fetchProducts = async () => {
      const data = await productService.getAllProducts();
      setProducts(data);
    };
    fetchProducts();
  }, []);

  const filteredProducts =
    selectedCategory === 'All'
      ? (products || [])
      : (products || []).filter(product => product.category === selectedCategory);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Products</h2>
      <CategoryFilter
        selectedCategory={selectedCategory}
        onCategoryChange={setSelectedCategory}
      />
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-4">
        {(filteredProducts || []).map(product => (
          <div key={product._id} className="bg-white shadow rounded p-4 flex flex-col items-center">
            <img
              src={product.imageUrl || '/favicon.ico'}
              alt={product.name}
              className="w-32 h-32 object-cover mb-2 rounded"
              onError={e => { e.target.src = '/favicon.ico'; }}
            />
            <h3 className="font-semibold mb-2 text-center">{product.name}</h3>
            <p className="mb-1 text-center">{product.description}</p>
            <p className="mb-1 text-center">Category: {product.category}</p>
            <p className="mb-1 text-center">Price: â‚¹{product.price}</p>
            <button className="px-4 py-2 bg-blue-600 text-white rounded mt-2">Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
