import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import orderService from '../services/orderService';

export const OrderContext = createContext();

// Custom hook for using order context
export const useOrder = () => {
  const context = useContext(OrderContext);
  if (!context) {
    throw new Error('useOrder must be used within an OrderProvider');
  }
  return context;
};

export const OrderProvider = ({ children }) => {
  const [orders, setOrders] = useState([]);
  const [currentOrder, setCurrentOrder] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [orderHistory, setOrderHistory] = useState([]);
  const [activeOrders, setActiveOrders] = useState([]);

  // Load orders on mount
  useEffect(() => {
    fetchOrders();
  }, [fetchOrders]);

  // Separate orders into active and history
  useEffect(() => {
    const active = orders.filter(order => 
      ['pending', 'confirmed', 'preparing', 'out_for_delivery'].includes(order.status)
    );
    const history = orders.filter(order => 
      ['delivered', 'cancelled', 'refunded'].includes(order.status)
    );
    
    setActiveOrders(active);
    setOrderHistory(history);
  }, [orders]);

  // Fetch all orders
  const fetchOrders = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const userOrders = await orderService.getOrders();
      setOrders(userOrders || []);
      return { success: true, orders: userOrders };
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to fetch orders';
      setError(errorMessage);
      console.error('Fetch orders error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, []);

  // Place new order
  const placeOrder = useCallback(async (orderData) => {
    try {
      setLoading(true);
      setError(null);
      
      const newOrder = await orderService.placeOrder(orderData);
      
      if (newOrder) {
        setOrders(prevOrders => [newOrder, ...prevOrders]);
        setCurrentOrder(newOrder);
        
        return { 
          success: true, 
          order: newOrder,
          message: 'Order placed successfully!'
        };
      } else {
        throw new Error('No order data received');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || err.message || 'Failed to place order';
      setError(errorMessage);
      console.error('Place order error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, []);

  // Get order by ID
  const getOrderById = useCallback(async (orderId) => {
    try {
      setLoading(true);
      setError(null);
      
      const order = await orderService.getOrderById(orderId);
      
      if (order) {
        setCurrentOrder(order);
        return { success: true, order };
      } else {
        throw new Error('Order not found');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Order not found';
      setError(errorMessage);
      console.error('Get order error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, []);

  // Cancel order
  const cancelOrder = useCallback(async (orderId, reason = '') => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await orderService.cancelOrder(orderId, reason);
      
      if (response.success) {
        // Update order in state
        setOrders(prevOrders => 
          prevOrders.map(order => 
            order._id === orderId 
              ? { ...order, status: 'cancelled', cancelReason: reason }
              : order
          )
        );
        
        // Update current order if it's the cancelled one
        if (currentOrder && currentOrder._id === orderId) {
          setCurrentOrder({ ...currentOrder, status: 'cancelled', cancelReason: reason });
        }
        
        return { success: true, message: 'Order cancelled successfully' };
      } else {
        throw new Error(response.message || 'Failed to cancel order');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to cancel order';
      setError(errorMessage);
      console.error('Cancel order error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, [currentOrder]);

  // Track order status
  const trackOrder = useCallback(async (orderId) => {
    try {
      setLoading(true);
      setError(null);
      
      const trackingInfo = await orderService.trackOrder(orderId);
      
      if (trackingInfo) {
        // Update order status in state
        setOrders(prevOrders => 
          prevOrders.map(order => 
            order._id === orderId 
              ? { ...order, ...trackingInfo }
              : order
          )
        );
        
        if (currentOrder && currentOrder._id === orderId) {
          setCurrentOrder({ ...currentOrder, ...trackingInfo });
        }
        
        return { success: true, tracking: trackingInfo };
      } else {
        throw new Error('Tracking information not available');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to track order';
      setError(errorMessage);
      console.error('Track order error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, [currentOrder]);

  // Reorder (place same order again)
  const reorder = useCallback(async (orderId) => {
    try {
      const orderToReorder = orders.find(order => order._id === orderId);
      if (!orderToReorder) {
        throw new Error('Order not found');
      }

      // Create new order data based on previous order
      const reorderData = {
        items: orderToReorder.items,
        deliveryAddress: orderToReorder.deliveryAddress,
        paymentMethod: orderToReorder.paymentMethod,
        specialInstructions: orderToReorder.specialInstructions
      };

      return await placeOrder(reorderData);
    } catch (err) {
      const errorMessage = 'Failed to reorder';
      setError(errorMessage);
      console.error('Reorder error:', err);
      return { success: false, message: errorMessage };
    }
  }, [orders, placeOrder]);

  // Rate order
  const rateOrder = useCallback(async (orderId, rating, review = '') => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await orderService.rateOrder(orderId, rating, review);
      
      if (response.success) {
        // Update order with rating
        setOrders(prevOrders => 
          prevOrders.map(order => 
            order._id === orderId 
              ? { ...order, rating, review, hasRated: true }
              : order
          )
        );
        
        if (currentOrder && currentOrder._id === orderId) {
          setCurrentOrder({ ...currentOrder, rating, review, hasRated: true });
        }
        
        return { success: true, message: 'Rating submitted successfully' };
      } else {
        throw new Error(response.message || 'Failed to submit rating');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.message || 'Failed to submit rating';
      setError(errorMessage);
      console.error('Rate order error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, [currentOrder]);

  // Get order statistics
  const getOrderStats = useCallback(() => {
    const stats = {
      total: orders.length,
      delivered: orders.filter(order => order.status === 'delivered').length,
      cancelled: orders.filter(order => order.status === 'cancelled').length,
      active: activeOrders.length,
      totalSpent: orders
        .filter(order => order.status === 'delivered')
        .reduce((total, order) => total + (order.total || 0), 0)
    };
    
    stats.successRate = stats.total > 0 ? ((stats.delivered / stats.total) * 100).toFixed(1) : 0;
    
    return stats;
  }, [orders, activeOrders]);

  // Get order by status
  const getOrdersByStatus = useCallback((status) => {
    return orders.filter(order => order.status === status);
  }, [orders]);

  // Clear error
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Clear current order
  const clearCurrentOrder = useCallback(() => {
    setCurrentOrder(null);
  }, []);

  // Update order status (for real-time updates)
  const updateOrderStatus = useCallback((orderId, newStatus, additionalData = {}) => {
    setOrders(prevOrders => 
      prevOrders.map(order => 
        order._id === orderId 
          ? { ...order, status: newStatus, ...additionalData }
          : order
      )
    );
    
    if (currentOrder && currentOrder._id === orderId) {
      setCurrentOrder({ ...currentOrder, status: newStatus, ...additionalData });
    }
  }, [currentOrder]);

  const value = {
    // State
    orders,
    currentOrder,
    loading,
    error,
    orderHistory,
    activeOrders,
    
    // Actions
    fetchOrders,
    placeOrder,
    getOrderById,
    cancelOrder,
    trackOrder,
    reorder,
    rateOrder,
    
    // Utilities
    getOrderStats,
    getOrdersByStatus,
    clearError,
    clearCurrentOrder,
    updateOrderStatus,
    
    // Derived state
    hasActiveOrders: activeOrders.length > 0,
    hasOrderHistory: orderHistory.length > 0
  };

  return (
    <OrderContext.Provider value={value}>
      {children}
    </OrderContext.Provider>
  );
};
