import React from 'react';

const TestRazorpay = () => {
  const handleTestPayment = () => {
    const options = {
      key: 'rzp_test_FDXSZ76LEo92zE', // Your test key
      amount: 100 * 100, // ₹100 in paise
      currency: 'INR',
      name: 'Food Delivery App',
      description: 'Test Payment',
      image: '/favicon.ico',
      handler: function (response) {
        alert(`Payment Successful! Payment ID: ${response.razorpay_payment_id}`);
        console.log('Payment response:', response);
      },
      prefill: {
        name: 'Test User',
        email: 'test@example.com',
        contact: '9999999999'
      },
      theme: {
        color: '#f97316'
      },
      modal: {
        ondismiss: function() {
          console.log('Payment modal closed');
        }
      }
    };

    if (window.Razorpay) {
      const rzp = new window.Razorpay(options);
      rzp.open();
    } else {
      alert('Razorpay is not loaded. Please refresh the page.');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-2xl mx-auto px-4">
        <div className="bg-white rounded-lg shadow-sm p-8 text-center">
          <h1 className="text-3xl font-bold text-gray-900 mb-4">Test Razorpay Payment</h1>
          <p className="text-gray-600 mb-8">
            Click the button below to test your Razorpay integration with key: 
            <code className="bg-gray-100 px-2 py-1 rounded ml-2">rzp_test_FDXSZ76LEo92zE</code>
          </p>
          
          <div className="bg-blue-50 border border-blue-200 rounded-lg p-4 mb-6">
            <h3 className="font-semibold text-blue-900 mb-2">Test Card Details:</h3>
            <div className="text-sm text-blue-800">
              <p><strong>Card Number:</strong> 4111 1111 1111 1111</p>
              <p><strong>Expiry:</strong> Any future date</p>
              <p><strong>CVV:</strong> Any 3 digits</p>
              <p><strong>Name:</strong> Any name</p>
            </div>
          </div>

          <button
            onClick={handleTestPayment}
            className="inline-flex items-center px-8 py-4 bg-orange-500 text-white font-semibold rounded-lg hover:bg-orange-600 transition-colors"
          >
            Test Payment - ₹100
          </button>

          <div className="mt-8 text-left">
            <h3 className="font-semibold text-gray-900 mb-4">Integration Status:</h3>
            <ul className="space-y-2 text-sm text-gray-600">
              <li className="flex items-center">
                <span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>
                Razorpay Key ID: rzp_test_FDXSZ76LEo92zE ✅
              </li>
              <li className="flex items-center">
                <span className="w-2 h-2 bg-green-500 rounded-full mr-3"></span>
                Razorpay Script Loaded ✅
              </li>
              <li className="flex items-center">
                <span className="w-2 h-2 bg-yellow-500 rounded-full mr-3"></span>
                Key Secret: Required for backend verification ⚠️
              </li>
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TestRazorpay;
