import React, { createContext, useState, useEffect, useContext, useCallback } from 'react';
import cartService from '../services/cartService';

export const CartContext = createContext();

// Custom hook for using cart context
export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [lastAction, setLastAction] = useState(null);

  // Load cart from localStorage on mount
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      try {
        setCartItems(JSON.parse(savedCart));
      } catch (err) {
        console.error('Error parsing saved cart:', err);
        localStorage.removeItem('cart');
      }
    }
    
    // Also try to fetch from server if user is logged in
    fetchCart();
  }, [fetchCart]);

  // Save cart to localStorage whenever it changes
  useEffect(() => {
    if (cartItems.length > 0) {
      localStorage.setItem('cart', JSON.stringify(cartItems));
    } else {
      localStorage.removeItem('cart');
    }
  }, [cartItems]);

  // Fetch cart from server
  const fetchCart = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const cartData = await cartService.getCart();
      if (cartData && Array.isArray(cartData)) {
        setCartItems(cartData);
      }
    } catch (err) {
      // Don't set error for fetch failures, just log them
      console.error('Failed to fetch cart from server:', err);
    } finally {
      setLoading(false);
    }
  }, []);

  // Add item to cart
  const addToCart = useCallback(async (product, quantity = 1, options = {}) => {
    try {
      setLoading(true);
      setError(null);

      // Check if item already exists
      const existingItemIndex = cartItems.findIndex(
        item => item._id === product._id && 
        JSON.stringify(item.options || {}) === JSON.stringify(options)
      );

      let newCartItems;

      if (existingItemIndex !== -1) {
        // Update existing item
        newCartItems = [...cartItems];
        newCartItems[existingItemIndex] = {
          ...newCartItems[existingItemIndex],
          quantity: newCartItems[existingItemIndex].quantity + quantity
        };
      } else {
        // Add new item
        const cartItem = {
          ...product,
          quantity,
          options,
          addedAt: new Date().toISOString(),
          cartItemId: `${product._id}_${Date.now()}_${Math.random()}`
        };
        newCartItems = [...cartItems, cartItem];
      }

      setCartItems(newCartItems);
      setLastAction({ type: 'ADD', product, quantity });

      // Try to sync with server
      try {
        await cartService.addToCart(product._id, quantity, options);
      } catch (serverError) {
        console.error('Failed to sync cart with server:', serverError);
      }

      return { success: true, message: `${product.name} added to cart` };
    } catch (err) {
      const errorMessage = 'Failed to add item to cart';
      setError(errorMessage);
      console.error('Add to cart error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, [cartItems]);

  // Update item quantity
  const updateQuantity = useCallback(async (cartItemId, newQuantity) => {
    try {
      setLoading(true);
      setError(null);

      if (newQuantity <= 0) {
        return removeFromCart(cartItemId);
      }

      const updatedItems = cartItems.map(item =>
        item.cartItemId === cartItemId || item._id === cartItemId
          ? { ...item, quantity: newQuantity }
          : item
      );

      setCartItems(updatedItems);
      setLastAction({ type: 'UPDATE', itemId: cartItemId, quantity: newQuantity });

      // Try to sync with server
      try {
        await cartService.updateCartItem(cartItemId, newQuantity);
      } catch (serverError) {
        console.error('Failed to sync quantity update with server:', serverError);
      }

      return { success: true, message: 'Quantity updated' };
    } catch (err) {
      const errorMessage = 'Failed to update quantity';
      setError(errorMessage);
      console.error('Update quantity error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, [cartItems]);

  // Remove item from cart
  const removeFromCart = useCallback(async (cartItemId) => {
    try {
      setLoading(true);
      setError(null);

      const itemToRemove = cartItems.find(item => 
        item.cartItemId === cartItemId || item._id === cartItemId
      );

      const updatedItems = cartItems.filter(item =>
        item.cartItemId !== cartItemId && item._id !== cartItemId
      );

      setCartItems(updatedItems);
      setLastAction({ type: 'REMOVE', product: itemToRemove });

      // Try to sync with server
      try {
        await cartService.removeFromCart(cartItemId);
      } catch (serverError) {
        console.error('Failed to sync removal with server:', serverError);
      }

      return { success: true, message: `${itemToRemove?.name || 'Item'} removed from cart` };
    } catch (err) {
      const errorMessage = 'Failed to remove item from cart';
      setError(errorMessage);
      console.error('Remove from cart error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, [cartItems]);

  // Clear entire cart
  const clearCart = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);

      setCartItems([]);
      setLastAction({ type: 'CLEAR' });

      // Try to sync with server
      try {
        await cartService.clearCart();
      } catch (serverError) {
        console.error('Failed to sync cart clear with server:', serverError);
      }

      return { success: true, message: 'Cart cleared' };
    } catch (err) {
      const errorMessage = 'Failed to clear cart';
      setError(errorMessage);
      console.error('Clear cart error:', err);
      return { success: false, message: errorMessage };
    } finally {
      setLoading(false);
    }
  }, []);

  // Get cart totals
  const getCartTotals = useCallback(() => {
    const subtotal = cartItems.reduce((total, item) => {
      return total + (item.price * item.quantity);
    }, 0);

    const itemCount = cartItems.reduce((total, item) => {
      return total + item.quantity;
    }, 0);

    // Calculate tax (assuming 10% tax rate)
    const taxRate = 0.10;
    const tax = subtotal * taxRate;

    // Calculate delivery fee (free over $25)
    const deliveryFee = subtotal >= 25 ? 0 : 3.99;

    const total = subtotal + tax + deliveryFee;

    return {
      subtotal: parseFloat(subtotal.toFixed(2)),
      tax: parseFloat(tax.toFixed(2)),
      deliveryFee: parseFloat(deliveryFee.toFixed(2)),
      total: parseFloat(total.toFixed(2)),
      itemCount
    };
  }, [cartItems]);

  // Check if item is in cart
  const isInCart = useCallback((productId) => {
    return cartItems.some(item => item._id === productId);
  }, [cartItems]);

  // Get item quantity in cart
  const getItemQuantity = useCallback((productId) => {
    const item = cartItems.find(item => item._id === productId);
    return item ? item.quantity : 0;
  }, [cartItems]);

  // Validate cart items (check if products still exist, prices are current, etc.)
  const validateCart = useCallback(async () => {
    try {
      setLoading(true);
      // This would typically validate against current product data
      // For now, just return current cart items
      return { valid: true, items: cartItems };
    } catch (err) {
      console.error('Cart validation error:', err);
      return { valid: false, errors: ['Cart validation failed'] };
    } finally {
      setLoading(false);
    }
  }, [cartItems]);

  // Clear error
  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Clear last action
  const clearLastAction = useCallback(() => {
    setLastAction(null);
  }, []);

  const value = {
    // State
    cartItems,
    loading,
    error,
    lastAction,
    
    // Actions
    addToCart,
    updateQuantity,
    removeFromCart,
    clearCart,
    fetchCart,
    
    // Utilities
    getCartTotals,
    isInCart,
    getItemQuantity,
    validateCart,
    clearError,
    clearLastAction,
    
    // Derived state
    isEmpty: cartItems.length === 0,
    itemCount: cartItems.reduce((total, item) => total + item.quantity, 0)
  };

  return (
    <CartContext.Provider value={value}>
      {children}
    </CartContext.Provider>
  );
};
