import React, { useEffect, useState } from 'react';
import { useContext } from 'react';
import { CartContext } from '../../Context/CartContext';
import productService from '../../services/productService';
import CategoryFilter from '../../components/CategoryFilter';

const Products = () => {
  const { addToCart } = useContext(CartContext);
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const fetchProducts = async () => {
      const data = await productService.getAllProducts();
      setProducts(data);
    };
    fetchProducts();
  }, []);

  const filteredProducts =
    selectedCategory === 'All'
      ? (products || [])
      : (products || []).filter(product => product.category === selectedCategory);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Products</h2>
      <CategoryFilter
        selectedCategory={selectedCategory}
        onCategoryChange={setSelectedCategory}
      />
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mt-6">
        {(filteredProducts || []).map(product => (
          <div
            key={product._id}
            className="bg-white shadow-lg rounded-xl p-6 flex flex-col items-center transform transition duration-300 hover:scale-105 hover:shadow-2xl animate-fadeIn"
            style={{ animationDelay: `${Math.random() * 0.5}s` }}
          >
            <img
              src={product.imageUrl || '/favicon.ico'}
              alt={product.name}
              className="w-36 h-36 object-cover mb-4 rounded-lg transition duration-300 hover:scale-110"
              onError={e => { e.target.src = '/favicon.ico'; }}
            />
            <h3 className="font-semibold mb-2 text-center text-lg text-blue-700 transition duration-300 hover:text-blue-900">{product.name}</h3>
            <p className="mb-2 text-center text-gray-600">{product.description}</p>
            <p className="mb-1 text-center text-sm text-gray-500">Category: {product.category}</p>
            <p className="mb-2 text-center font-bold text-green-600">Price: â‚¹{product.price}</p>
            <button className="px-6 py-2 bg-gradient-to-r from-blue-500 to-blue-700 text-white rounded-full shadow hover:from-blue-700 hover:to-blue-900 transition-all duration-300 mt-2">Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Products;
