import React, { useContext, useState, useEffect } from 'react';
import { useParams, useNavigate, useLocation } from 'react-router-dom';
import { OrderContext } from '../../Context/OrderContext';
import { AuthContext } from '../../Context/AuthContext';
import {
  FiClock,
  FiMapPin,
  FiTruck,
  FiCheckCircle,
  FiXCircle,
  FiRefreshCw,
  FiShoppingBag,
  FiPhone,
  FiArrowLeft,
  FiUser,
  FiPackage,
  FiHome,
  FiStar,
  FiMessageSquare,
  FiNavigation
} from 'react-icons/fi';

const OrderTracking = () => {
  const { orderId } = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const { getOrder, trackOrder, loading } = useContext(OrderContext);
  const { user, isAuthenticated } = useContext(AuthContext);

  const [order, setOrder] = useState(null);
  const [trackingData, setTrackingData] = useState(null);
  const [refreshing, setRefreshing] = useState(false);
  const [estimatedTime, setEstimatedTime] = useState('');

  // Check if user came from successful order placement
  const orderSuccess = location.state?.orderSuccess;

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    if (orderId) {
      fetchOrderData();
    }
  }, [orderId, isAuthenticated, navigate]);

  useEffect(() => {
    // Update estimated time every minute
    if (order?.estimatedDeliveryTime) {
      const updateTime = () => {
        const now = new Date();
        const estimated = new Date(order.estimatedDeliveryTime);
        const diff = estimated.getTime() - now.getTime();
        
        if (diff > 0) {
          const minutes = Math.floor(diff / (1000 * 60));
          if (minutes > 60) {
            const hours = Math.floor(minutes / 60);
            const mins = minutes % 60;
            setEstimatedTime(`${hours}h ${mins}m`);
          } else {
            setEstimatedTime(`${minutes} min`);
          }
        } else {
          setEstimatedTime('Should arrive soon');
        }
      };

      updateTime();
      const interval = setInterval(updateTime, 60000); // Update every minute
      
      return () => clearInterval(interval);
    }
  }, [order?.estimatedDeliveryTime]);

  const fetchOrderData = async () => {
    try {
      const orderData = await getOrder(orderId);
      setOrder(orderData);
      
      const tracking = await trackOrder(orderId);
      setTrackingData(tracking);
    } catch (error) {
      console.error('Error fetching order data:', error);
    }
  };

  const handleRefresh = async () => {
    setRefreshing(true);
    await fetchOrderData();
    setTimeout(() => setRefreshing(false), 1000);
  };

  const getStatusColor = (status) => {
    const colors = {
      pending: 'text-yellow-600 bg-yellow-100',
      confirmed: 'text-blue-600 bg-blue-100',
      preparing: 'text-orange-600 bg-orange-100',
      ready: 'text-purple-600 bg-purple-100',
      out_for_delivery: 'text-indigo-600 bg-indigo-100',
      delivered: 'text-green-600 bg-green-100',
      cancelled: 'text-red-600 bg-red-100'
    };
    return colors[status] || 'text-gray-600 bg-gray-100';
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending':
        return <FiClock className="w-5 h-5" />;
      case 'confirmed':
        return <FiCheckCircle className="w-5 h-5" />;
      case 'preparing':
        return <FiRefreshCw className="w-5 h-5" />;
      case 'ready':
        return <FiShoppingBag className="w-5 h-5" />;
      case 'out_for_delivery':
        return <FiTruck className="w-5 h-5" />;
      case 'delivered':
        return <FiCheckCircle className="w-5 h-5" />;
      case 'cancelled':
        return <FiXCircle className="w-5 h-5" />;
      default:
        return <FiClock className="w-5 h-5" />;
    }
  };

  const getProgressPercentage = (status) => {
    const statusFlow = {
      pending: 10,
      confirmed: 25,
      preparing: 50,
      ready: 75,
      out_for_delivery: 90,
      delivered: 100,
      cancelled: 0
    };
    return statusFlow[status] || 0;
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-4xl mx-auto px-4 text-center">
          <div className="w-16 h-16 border-4 border-orange-200 border-t-orange-500 rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading order details...</p>
        </div>
      </div>
    );
  }

  if (!order) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-4xl mx-auto px-4 text-center">
          <FiPackage className="w-16 h-16 text-gray-400 mx-auto mb-4" />
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Order Not Found</h2>
          <p className="text-gray-600 mb-6">We couldn't find the order you're looking for.</p>
          <div className="flex gap-4 justify-center">
            <button
              onClick={() => navigate('/orders')}
              className="px-6 py-3 bg-orange-500 text-white rounded-lg hover:bg-orange-600"
            >
              View All Orders
            </button>
            <button
              onClick={() => navigate('/')}
              className="px-6 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50"
            >
              Go Home
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-4 sm:py-8">
      <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-6">
          <div className="flex items-center justify-between mb-4">
            <button
              onClick={() => navigate('/orders')}
              className="flex items-center text-orange-600 hover:text-orange-700"
            >
              <FiArrowLeft className="w-4 h-4 mr-2" />
              Back to Orders
            </button>
            <button
              onClick={handleRefresh}
              disabled={refreshing}
              className="flex items-center px-4 py-2 border border-gray-300 rounded-lg hover:bg-gray-50 disabled:opacity-50"
            >
              <FiRefreshCw className={`w-4 h-4 mr-2 ${refreshing ? 'animate-spin' : ''}`} />
              Refresh
            </button>
          </div>
          
          {orderSuccess && (
            <div className="bg-green-50 border border-green-200 rounded-lg p-4 mb-6">
              <div className="flex items-center">
                <FiCheckCircle className="w-5 h-5 text-green-600 mr-3" />
                <div>
                  <h3 className="font-medium text-green-900">Order Placed Successfully!</h3>
                  <p className="text-green-700 text-sm mt-1">Your order has been confirmed and is being processed.</p>
                </div>
              </div>
            </div>
          )}

          <h1 className="text-2xl sm:text-3xl font-bold text-gray-900">
            Order #{order._id?.slice(-8).toUpperCase()}
          </h1>
          <p className="text-gray-600 mt-1">
            Placed on {new Date(order.createdAt).toLocaleDateString('en-IN', {
              day: 'numeric',
              month: 'long',
              year: 'numeric',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </p>
        </div>

        {/* Current Status Card */}
        <div className="bg-white rounded-lg shadow-sm mb-8 overflow-hidden">
          <div className="p-6">
            <div className="flex items-center justify-between mb-6">
              <div>
                <div className={`inline-flex items-center px-4 py-2 rounded-full text-sm font-medium ${getStatusColor(order.status)}`}>
                  {getStatusIcon(order.status)}
                  <span className="ml-2 capitalize">{order.status.replace('_', ' ')}</span>
                </div>
                <p className="text-gray-600 mt-2">
                  {order.status === 'delivered' 
                    ? 'Your order has been delivered!'
                    : order.status === 'out_for_delivery'
                    ? `Arriving in ${estimatedTime}`
                    : 'Estimated delivery time'}
                </p>
                {order.status !== 'delivered' && order.estimatedDeliveryTime && (
                  <p className="text-lg font-semibold text-gray-900 mt-1">
                    {new Date(order.estimatedDeliveryTime).toLocaleTimeString('en-IN', {
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </p>
                )}
              </div>
              {order.status === 'out_for_delivery' && (
                <div className="text-right">
                  <FiTruck className="w-12 h-12 text-orange-500 mb-2" />
                  <p className="text-sm text-gray-600">Out for delivery</p>
                </div>
              )}
            </div>

            {/* Progress Bar */}
            <div className="mb-6">
              <div className="flex justify-between text-sm text-gray-600 mb-2">
                <span>Order Confirmed</span>
                <span>Delivered</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className="bg-orange-500 h-2 rounded-full transition-all duration-500"
                  style={{ width: `${getProgressPercentage(order.status)}%` }}
                ></div>
              </div>
            </div>

            {/* Delivery Partner Info */}
            {order.deliveryPartner && (
              <div className="bg-blue-50 rounded-lg p-4 mb-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                      <FiUser className="w-6 h-6 text-blue-600" />
                    </div>
                    <div>
                      <h4 className="font-medium text-blue-900">
                        {order.deliveryPartner.name}
                      </h4>
                      <p className="text-blue-700 text-sm">
                        {order.deliveryPartner.vehicleType || 'Bike'}   {order.deliveryPartner.rating || '4.5'}
                      </p>
                    </div>
                  </div>
                  {order.status === 'out_for_delivery' && (
                    <a
                      href={`tel:${order.deliveryPartner.phone}`}
                      className="flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                    >
                      <FiPhone className="w-4 h-4 mr-2" />
                      Call
                    </a>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Order Timeline */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow-sm p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-6">Order Timeline</h3>
              
              <div className="space-y-6">
                {(trackingData?.trackingUpdates || order.trackingUpdates || [
                  {
                    status: order.status,
                    timestamp: order.createdAt,
                    message: 'Order placed successfully',
                    location: 'Restaurant'
                  }
                ]).map((update, index) => (
                  <div key={index} className="flex items-start space-x-4">
                    <div className={`flex-shrink-0 w-10 h-10 rounded-full flex items-center justify-center ${getStatusColor(update.status)}`}>
                      {getStatusIcon(update.status)}
                    </div>
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center justify-between">
                        <h4 className="font-medium text-gray-900 capitalize">
                          {update.status.replace('_', ' ')}
                        </h4>
                        <span className="text-sm text-gray-500">
                          {new Date(update.timestamp).toLocaleTimeString('en-IN', {
                            hour: '2-digit',
                            minute: '2-digit'
                          })}
                        </span>
                      </div>
                      <p className="text-gray-600 text-sm mt-1">
                        {update.message}
                      </p>
                      {update.location && (
                        <div className="flex items-center text-xs text-gray-500 mt-1">
                          <FiMapPin className="w-3 h-3 mr-1" />
                          {update.location}
                        </div>
                      )}
                    </div>
                  </div>
                ))}
              </div>

              {/* Live Location (Mock) */}
              {order.status === 'out_for_delivery' && (
                <div className="mt-8 border-t pt-6">
                  <h4 className="font-medium text-gray-900 mb-4">Live Location</h4>
                  <div className="bg-gray-100 rounded-lg h-48 flex items-center justify-center">
                    <div className="text-center">
                      <FiNavigation className="w-12 h-12 text-orange-500 mx-auto mb-2" />
                      <p className="text-gray-600">Live tracking coming soon</p>
                      <p className="text-sm text-gray-500 mt-1">Your delivery partner is on the way</p>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>

          {/* Order Summary Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Order Details</h3>
              
              {/* Items */}
              <div className="space-y-3 mb-6">
                {order.items?.map((item, index) => (
                  <div key={index} className="flex items-center space-x-3">
                    <img
                      src={item.product?.imageUrl || '/favicon.ico'}
                      alt={item.product?.name}
                      className="w-12 h-12 bg-gray-200 rounded-lg object-cover"
                    />
                    <div className="flex-1">
                      <h4 className="font-medium text-gray-900">
                        {item.product?.name}
                      </h4>
                      <p className="text-sm text-gray-500">
                        Qty: {item.quantity}  {item.price}
                      </p>
                    </div>
                    <p className="font-medium text-gray-900">
                      {(item.price * item.quantity).toFixed(2)}
                    </p>
                  </div>
                ))}
              </div>

              {/* Total */}
              <div className="border-t pt-4">
                <div className="flex justify-between text-lg font-semibold text-gray-900">
                  <span>Total</span>
                  <span>{order.totalAmount?.toFixed(2)}</span>
                </div>
              </div>
            </div>

            {/* Delivery Address */}
            <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Delivery Address</h3>
              {order.deliveryAddress && (
                <div className="text-sm text-gray-600">
                  <p className="font-medium text-gray-900">
                    {order.deliveryAddress.firstName} {order.deliveryAddress.lastName}
                  </p>
                  <p>{order.deliveryAddress.street}</p>
                  {order.deliveryAddress.apartment && <p>{order.deliveryAddress.apartment}</p>}
                  <p>{order.deliveryAddress.city}, {order.deliveryAddress.state} {order.deliveryAddress.pincode}</p>
                  {order.deliveryAddress.landmark && <p>Near: {order.deliveryAddress.landmark}</p>}
                </div>
              )}
            </div>

            {/* Actions */}
            <div className="space-y-3">
              {order.status === 'delivered' && (
                <button className="w-full flex items-center justify-center px-4 py-3 bg-orange-500 text-white rounded-lg hover:bg-orange-600">
                  <FiStar className="w-4 h-4 mr-2" />
                  Rate this Order
                </button>
              )}
              
              <button className="w-full flex items-center justify-center px-4 py-3 border border-gray-300 text-gray-700 rounded-lg hover:bg-gray-50">
                <FiMessageSquare className="w-4 h-4 mr-2" />
                Get Help
              </button>
              
              <button
                onClick={() => navigate('/products')}
                className="w-full flex items-center justify-center px-4 py-3 border border-orange-500 text-orange-500 rounded-lg hover:bg-orange-50"
              >
                Order Again
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default OrderTracking;
