import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import HeroSection from '../../components/HeroSection';
import ProductGrid from '../../components/ProductGrid';
import CategoryFilter from '../../components/CategoryFilter';
import { useCart } from '../../Context/CartContext';
import { useProduct } from '../../Context/ProductContext';
import productService from '../../services/productService';
import { FiTruck, FiShield, FiClock, FiStar, FiArrowRight, FiPlay } from 'react-icons/fi';

// Sample products data (fallback if API fails)
const sampleProducts = [
  {
    _id: '1',
    name: 'Margherita Pizza',
    description: 'Classic pizza with fresh mozzarella, tomatoes, and basil',
    price: 12.99,
    image: 'https://images.unsplash.com/photo-1604068549290-dea0e4a305ca?w=400',
    category: 'Pizza',
    rating: { average: 4.5, count: 128 },
    available: true
  },
  {
    _id: '2',
    name: 'Chicken Burger',
    description: 'Juicy grilled chicken with lettuce, tomato, and mayo',
    price: 9.99,
    image: 'https://images.unsplash.com/photo-1565299624946-b28f40a0ca4b?w=400',
    category: 'Burgers',
    rating: { average: 4.3, count: 95 },
    available: true
  },
  {
    _id: '3',
    name: 'Caesar Salad',
    description: 'Fresh romaine lettuce with caesar dressing and croutons',
    price: 8.99,
    image: 'https://images.unsplash.com/photo-1512621776951-a57141f2eefd?w=400',
    category: 'Salads',
    rating: { average: 4.2, count: 67 },
    available: true
  },
  {
    _id: '4',
    name: 'Spaghetti Carbonara',
    description: 'Creamy pasta with bacon, eggs, and parmesan cheese',
    price: 14.99,
    image: 'https://images.unsplash.com/photo-1621996346565-e3dbc353d2e5?w=400',
    category: 'Pasta',
    rating: { average: 4.7, count: 156 },
    available: true
  },
  {
    _id: '5',
    name: 'Fish Tacos',
    description: 'Grilled fish with cabbage slaw and spicy mayo',
    price: 11.99,
    image: 'https://images.unsplash.com/photo-1551504734-5ee1c4a1479b?w=400',
    category: 'Mexican',
    rating: { average: 4.4, count: 89 },
    available: true
  },
  {
    _id: '6',
    name: 'Chocolate Cake',
    description: 'Rich chocolate cake with chocolate frosting',
    price: 6.99,
    image: 'https://images.unsplash.com/photo-1578985545062-69928b1d9587?w=400',
    category: 'Desserts',
    rating: { average: 4.6, count: 203 },
    available: true
  }
];

const Home = () => {
  const { addToCart } = useCart();
  const { products, fetchProducts } = useProduct();
  const [featuredProducts, setFeaturedProducts] = useState(sampleProducts);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [loading, setLoading] = useState(false);

  // Filter products based on selected category
  const filteredProducts = selectedCategory 
    ? featuredProducts.filter(product => product.category === selectedCategory)
    : featuredProducts;

  useEffect(() => {
    const loadProducts = async () => {
      try {
        setLoading(true);
        // Try to fetch from API first
        if (fetchProducts) {
          await fetchProducts();
          if (products && products.length > 0) {
            setFeaturedProducts(products.slice(0, 12));
          }
        }
      } catch (error) {
        console.error('Failed to fetch products:', error);
        // Fall back to sample products
        setFeaturedProducts(sampleProducts);
      } finally {
        setLoading(false);
      }
    };
    
    loadProducts();
  }, [fetchProducts, products]);

  const features = [
    {
      icon: FiTruck,
      title: 'Fast Delivery',
      description: 'Get your food delivered in 30 minutes or less',
      color: 'bg-green-500'
    },
    {
      icon: FiShield,
      title: 'Safe & Secure',
      description: 'Your payments and data are 100% secure',
      color: 'bg-blue-500'
    },
    {
      icon: FiClock,
      title: '24/7 Available',
      description: 'Order anytime, anywhere. We never close',
      color: 'bg-purple-500'
    },
    {
      icon: FiStar,
      title: 'Best Quality',
      description: 'Only the freshest ingredients and best chefs',
      color: 'bg-yellow-500'
    }
  ];

  const stats = [
    { number: '10,000+', label: 'Happy Customers' },
    { number: '500+', label: 'Restaurant Partners' },
    { number: '1M+', label: 'Orders Delivered' },
    { number: '4.9/5', label: 'Customer Rating' }
  ];

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <HeroSection />

      {/* Category Filter Section */}
      <section className="py-8 bg-white">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-8">
            <h2 className="text-3xl font-bold text-gray-900 mb-2">
              Explore Our Menu
            </h2>
            <p className="text-lg text-gray-600">
              Discover delicious food from various categories
            </p>
          </div>
          <CategoryFilter 
            selectedCategory={selectedCategory}
            onCategorySelect={setSelectedCategory}
          />
        </div>
      </section>

      {/* Featured Products Section */}
      <section className="py-12 bg-gray-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              {selectedCategory ? `${selectedCategory} Dishes` : 'Featured Dishes'} üçΩÔ∏è
            </h2>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto">
              {selectedCategory 
                ? `Delicious ${selectedCategory.toLowerCase()} options just for you`
                : 'Our most popular and highest-rated dishes, prepared with love and the finest ingredients'
              }
            </p>
          </div>
          <ProductGrid products={filteredProducts} loading={loading} />
        </div>
      </section>

      {/* Stats Section */}
      <section className="py-16 bg-gradient-to-r from-orange-500 to-red-600">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
