import React, { useEffect, useState } from 'react';
import productService from '../../services/productService';
import CategoryFilter from '../../components/CategoryFilter';

const Home = () => {
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');

  useEffect(() => {
    const fetchProducts = async () => {
      const data = await productService.getAllProducts();
      setProducts(data);
    };
    fetchProducts();
  }, []);

  const filteredProducts =
    selectedCategory === 'All'
      ? (products || [])
      : (products || []).filter(product => product.category === selectedCategory);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Welcome to Food Delivery</h2>
      <CategoryFilter
        selectedCategory={selectedCategory}
        onCategoryChange={setSelectedCategory}
      />
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-4">
    {(filteredProducts || []).map(product => (
          <div key={product._id} className="bg-white shadow rounded p-4">
            <h3 className="font-semibold mb-2">{product.name}</h3>
            <p className="mb-1">Category: {product.category}</p>
            <p className="mb-1">Price: â‚¹{product.price}</p>
            <button className="px-4 py-2 bg-blue-600 text-white rounded">Add to Cart</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
