import axios from 'axios';

const API = '/api/cart';

// Configure axios to send credentials with all requests
axios.defaults.withCredentials = true;

const cartService = {
  getCart: async () => {
    const res = await axios.get(`${API}`);
    return res.data.cart;
  },

  addToCart: async (productId, quantity = 1) => {
    const res = await axios.post(`${API}/add`, { productId, quantity });
    return res.data.cart;
  },

  updateCartItem: async (productId, quantity) => {
    const res = await axios.put(`${API}/update`, { productId, quantity });
    return res.data.cart;
  },

  removeFromCart: async (productId) => {
    const res = await axios.delete(`${API}/remove/${productId}`);
    return res.data.cart;
  },

  clearCart: async () => {
    const res = await axios.post(`${API}/clear`);
    return res.data.cart || [];
  }
};

export default cartService;
