import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { CartContext } from '../../Context/CartContext';
import { useDarkMode } from '../../Context/DarkModeContext';
import paymentService from '../../services/paymentService';

const PaymentGateway = () => {
  const { cartItems, cartTotal, clearCart } = useContext(CartContext);
  const { isDarkMode } = useDarkMode();
  const navigate = useNavigate();
  const [status, setStatus] = useState('');
  const [loading, setLoading] = useState(false);
  const [addressData, setAddressData] = useState(null);

  useEffect(() => {
    // Get address data from localStorage (set by ModernCheckout)
    const savedAddress = localStorage.getItem('checkoutAddress');
    if (savedAddress) {
      setAddressData(JSON.parse(savedAddress));
    }

    // Redirect if no cart items
    if (!cartItems || cartItems.length === 0) {
      navigate('/cart');
      return;
    }
  }, [cartItems, navigate]);

  const calculateTotal = () => {
    return cartItems.reduce((total, item) => {
      const price = item.product?.price || item.price || 0;
      return total + (price * (item.quantity || 1));
    }, 0);
  };

  const handlePayment = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      const orderData = {
        amount: calculateTotal(),
        items: cartItems,
        address: addressData,
        total: calculateTotal()
      };
      
      const res = await paymentService.processPayment(orderData);
      setStatus('Payment successful! Your order has been placed.');
      
      // Clear cart after successful payment
      setTimeout(() => {
        clearCart();
        localStorage.removeItem('checkoutAddress');
        navigate('/orders');
      }, 2000);
      
    } catch (err) {
      setStatus('Payment failed. Please try again.');
    }
    setLoading(false);
  };

  if (!cartItems || cartItems.length === 0) {
    return (
      <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">
            <h2 className="text-2xl font-bold mb-4">No Items in Cart</h2>
            <button 
              onClick={() => navigate('/products')}
              className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
            >
              Shop Now
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="container mx-auto px-4 py-8">
        <div className="max-w-2xl mx-auto">
          <h2 className="text-3xl font-bold mb-8 text-center">Complete Your Payment</h2>
          
          {/* Order Summary */}
          <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md p-6 mb-6`}>
            <h3 className="text-xl font-semibold mb-4">Order Summary</h3>
            <div className="space-y-3">
              {cartItems.map((item) => (
                <div key={item._id} className="flex justify-between items-center">
                  <div className="flex-1">
                    <p className="font-medium">{item.product?.name || item.name}</p>
                    <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                      Quantity: {item.quantity || 1}
                    </p>
                  </div>
                  <p className="font-medium">
                    ₹{((item.product?.price || item.price || 0) * (item.quantity || 1)).toFixed(2)}
                  </p>
                </div>
              ))}
            </div>
            <div className={`border-t ${isDarkMode ? 'border-gray-700' : 'border-gray-200'} mt-4 pt-4`}>
              <div className="flex justify-between items-center text-lg font-bold">
                <span>Total Amount:</span>
                <span>₹{calculateTotal().toFixed(2)}</span>
              </div>
            </div>
          </div>

          {/* Delivery Address */}
          {addressData && (
            <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md p-6 mb-6`}>
              <h3 className="text-xl font-semibold mb-4">Delivery Address</h3>
              <div className={`${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                <p>{addressData.street}</p>
                <p>{addressData.city}, {addressData.state} {addressData.pincode}</p>
                <p>Phone: {addressData.phone}</p>
              </div>
            </div>
          )}

          {/* Payment Form */}
          <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} rounded-lg shadow-md p-6`}>
            <h3 className="text-xl font-semibold mb-4">Payment Details</h3>
            <form onSubmit={handlePayment}>
              <div className="mb-6">
                <label className="block text-sm font-medium mb-2">
                  Amount to Pay
                </label>
                <input
                  type="text"
                  className={`w-full p-3 border rounded-lg ${
                    isDarkMode 
                      ? 'bg-gray-700 border-gray-600 text-white' 
                      : 'bg-white border-gray-300 text-gray-900'
                  }`}
                  value={`₹${calculateTotal().toFixed(2)}`}
                  readOnly
                />
              </div>
              
              <button 
                type="submit" 
                className="w-full px-6 py-3 bg-green-600 text-white rounded-lg hover:bg-green-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                disabled={loading}
              >
                {loading ? (
                  <div className="flex items-center justify-center">
                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                    Processing Payment...
                  </div>
                ) : (
                  `Pay ₹${calculateTotal().toFixed(2)}`
                )}
              </button>
            </form>

            {status && (
              <div className={`mt-4 p-4 rounded-lg ${
                status.includes('successful') 
                  ? 'bg-green-100 text-green-800 border border-green-200' 
                  : 'bg-red-100 text-red-800 border border-red-200'
              }`}>
                <p className="font-medium">{status}</p>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default PaymentGateway;
