import React, { useContext, useState, useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { OrderContext } from '../../Context/OrderContext';
import { AuthContext } from '../../Context/AuthContext';
import {
  FiClock,
  FiMapPin,
  FiTruck,
  FiCheckCircle,
  FiXCircle,
  FiEye,
  FiRefreshCw,
  FiStar,
  FiUser,
  FiCalendar,
  FiShoppingBag,
  FiFilter,
  FiSearch
} from 'react-icons/fi';

const Orders = () => {
  const { 
    orders, 
    loading, 
    fetchUserOrders, 
    trackOrder 
  } = useContext(OrderContext);
  const { user, isAuthenticated } = useContext(AuthContext);
  const navigate = useNavigate();

  const [filteredOrders, setFilteredOrders] = useState([]);
  const [filter, setFilter] = useState('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('date');
  const [trackingOrder, setTrackingOrder] = useState(null);

  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/login');
      return;
    }
    
    if (user?._id) {
      fetchUserOrders();
    }
  }, [isAuthenticated, user?._id, navigate, fetchUserOrders]);

  useEffect(() => {
    let filtered = orders || [];

    // Filter by status
    if (filter !== 'all') {
      filtered = filtered.filter(order => order.status === filter);
    }

    // Search filter
    if (searchQuery.trim()) {
      filtered = filtered.filter(order => 
        order._id?.toLowerCase().includes(searchQuery.toLowerCase()) ||
        order.items?.some(item => 
          item.product?.name?.toLowerCase().includes(searchQuery.toLowerCase())
        )
      );
    }

    // Sort orders
    filtered.sort((a, b) => {
      switch (sortBy) {
        case 'date':
          return new Date(b.createdAt) - new Date(a.createdAt);
        case 'amount':
          return b.totalAmount - a.totalAmount;
        case 'status':
          return a.status.localeCompare(b.status);
        default:
          return 0;
      }
    });

    setFilteredOrders(filtered);
  }, [orders, filter, searchQuery, sortBy]);

  const getStatusColor = (status) => {
    const colors = {
      pending: 'bg-yellow-100 text-yellow-800 border-yellow-200',
      confirmed: 'bg-blue-100 text-blue-800 border-blue-200',
      preparing: 'bg-orange-100 text-orange-800 border-orange-200',
      ready: 'bg-purple-100 text-purple-800 border-purple-200',
      out_for_delivery: 'bg-indigo-100 text-indigo-800 border-indigo-200',
      delivered: 'bg-green-100 text-green-800 border-green-200',
      cancelled: 'bg-red-100 text-red-800 border-red-200'
    };
    return colors[status] || 'bg-gray-100 text-gray-800 border-gray-200';
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending':
        return <FiClock className="w-4 h-4" />;
      case 'confirmed':
        return <FiCheckCircle className="w-4 h-4" />;
      case 'preparing':
        return <FiRefreshCw className="w-4 h-4" />;
      case 'ready':
        return <FiShoppingBag className="w-4 h-4" />;
      case 'out_for_delivery':
        return <FiTruck className="w-4 h-4" />;
      case 'delivered':
        return <FiCheckCircle className="w-4 h-4" />;
      case 'cancelled':
        return <FiXCircle className="w-4 h-4" />;
      default:
        return <FiClock className="w-4 h-4" />;
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-IN', {
      day: '2-digit',
      month: 'short',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const handleTrackOrder = async (orderId) => {
    try {
      setTrackingOrder(orderId);
      await trackOrder(orderId);
      navigate(`/orders/track/${orderId}`);
    } catch (error) {
      console.error('Error tracking order:', error);
    } finally {
      setTrackingOrder(null);
    }
  };

  const statusFilters = [
    { key: 'all', label: 'All Orders' },
    { key: 'pending', label: 'Pending' },
    { key: 'confirmed', label: 'Confirmed' },
    { key: 'preparing', label: 'Preparing' },
    { key: 'ready', label: 'Ready' },
    { key: 'out_for_delivery', label: 'Out for Delivery' },
    { key: 'delivered', label: 'Delivered' },
    { key: 'cancelled', label: 'Cancelled' }
  ];

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-4xl mx-auto px-4 text-center">
          <div className="bg-white rounded-lg shadow-sm p-8">
            <FiUser className="w-16 h-16 mx-auto text-gray-400 mb-4" />
            <h2 className="text-2xl font-bold text-gray-900 mb-4">Please Login</h2>
            <p className="text-gray-600 mb-6">You need to login to view your order history</p>
            <Link
              to="/login"
              className="inline-flex items-center px-6 py-3 bg-orange-500 text-white font-semibold rounded-lg hover:bg-orange-600 transition-colors"
            >
              Login Now
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 py-8">
        <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="text-center py-16">
            <div className="w-16 h-16 border-4 border-orange-200 border-t-orange-500 rounded-full animate-spin mx-auto mb-4"></div>
            <p className="text-gray-600">Loading your orders...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 py-4 sm:py-8">
      <div className="max-w-6xl mx-auto px-4 sm:px-6 lg:px-8">
        {/* Header */}
        <div className="mb-6 sm:mb-8">
          <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
            <div>
              <h1 className="text-2xl sm:text-3xl font-bold text-gray-900 mb-2">Order History</h1>
              <p className="text-gray-600">
                {filteredOrders.length} {filteredOrders.length === 1 ? 'order' : 'orders'}
                {user?.name && ` for ${user.name}`}
              </p>
            </div>
            
            {user && (
              <div className="flex items-center space-x-3 bg-white px-4 py-2 rounded-lg shadow-sm">
                <div className="w-10 h-10 bg-orange-100 rounded-full flex items-center justify-center">
                  <span className="text-orange-600 font-semibold">
                    {user.name?.charAt(0)?.toUpperCase() || 'U'}
                  </span>
                </div>
                <div className="hidden sm:block">
                  <p className="text-sm font-medium text-gray-900">{user.name}</p>
                  <p className="text-xs text-gray-500">{user.email}</p>
                </div>
              </div>
            )}
          </div>
        </div>

        {/* Filters and Search */}
        <div className="bg-white rounded-lg shadow-sm mb-6 p-4">
          <div className="flex flex-col lg:flex-row gap-4">
            {/* Search */}
            <div className="flex-1">
              <div className="relative">
                <FiSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                <input
                  type="text"
                  placeholder="Search by order ID or item name..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                />
              </div>
            </div>

            {/* Filters */}
            <div className="flex flex-wrap gap-2">
              <select
                value={filter}
                onChange={(e) => setFilter(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent text-sm"
              >
                {statusFilters.map(status => (
                  <option key={status.key} value={status.key}>
                    {status.label}
                  </option>
                ))}
              </select>

              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent text-sm"
              >
                <option value="date">Sort by Date</option>
                <option value="amount">Sort by Amount</option>
                <option value="status">Sort by Status</option>
              </select>
            </div>
          </div>
        </div>

        {/* Orders List */}
        {filteredOrders.length === 0 ? (
          <div className="text-center py-16 bg-white rounded-lg shadow-sm">
            <FiShoppingBag className="w-16 h-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-gray-900 mb-2">No Orders Found</h3>
            <p className="text-gray-600 mb-6">
              {orders?.length === 0 
                ? "You haven't placed any orders yet. Start exploring our menu!"
                : "No orders match your current filters."
              }
            </p>
            {orders?.length === 0 ? (
              <Link
                to="/products"
                className="inline-flex items-center px-6 py-3 bg-orange-500 text-white font-semibold rounded-lg hover:bg-orange-600 transition-colors"
              >
                Browse Menu
              </Link>
            ) : (
              <button
                onClick={() => {
                  setFilter('all');
                  setSearchQuery('');
                }}
                className="inline-flex items-center px-6 py-3 bg-orange-500 text-white font-semibold rounded-lg hover:bg-orange-600 transition-colors"
              >
                Clear Filters
              </button>
            )}
          </div>
        ) : (
          <div className="space-y-4">
            {filteredOrders.map((order) => (
              <div key={order._id} className="bg-white rounded-lg shadow-sm overflow-hidden hover:shadow-md transition-shadow">
                <div className="p-4 sm:p-6">
                  {/* Order Header */}
                  <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between mb-4">
                    <div className="flex items-center space-x-4 mb-2 sm:mb-0">
                      <div>
                        <h3 className="font-semibold text-gray-900">#{order._id?.slice(-8).toUpperCase()}</h3>
                        <div className="flex items-center text-sm text-gray-500 mt-1">
                          <FiCalendar className="w-4 h-4 mr-1" />
                          {formatDate(order.createdAt)}
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex items-center space-x-3">
                      <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-medium border ${getStatusColor(order.status)}`}>
                        {getStatusIcon(order.status)}
                        <span className="ml-1 capitalize">{order.status.replace('_', ' ')}</span>
                      </div>
                      <div className="text-right">
                        <div className="text-lg font-bold text-gray-900">
                          {order.totalAmount?.toFixed(2)}
                        </div>
                        <div className="text-sm text-gray-500">
                          {order.items?.length} {order.items?.length === 1 ? 'item' : 'items'}
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Order Items Preview */}
                  <div className="border-t border-gray-200 pt-4">
                    <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-3 mb-4">
                      {order.items?.slice(0, 3).map((item, index) => (
                        <div key={index} className="flex items-center space-x-3">
                          <div className="w-12 h-12 bg-gray-200 rounded-lg overflow-hidden flex-shrink-0">
                            <img
                              src={item.product?.imageUrl || '/favicon.ico'}
                              alt={item.product?.name}
                              className="w-full h-full object-cover"
                              onError={(e) => {
                                e.target.src = '/favicon.ico';
                              }}
                            />
                          </div>
                          <div className="flex-1 min-w-0">
                            <p className="text-sm font-medium text-gray-900 truncate">
                              {item.product?.name}
                            </p>
                            <p className="text-sm text-gray-500">
                              Qty: {item.quantity}  {item.price}
                            </p>
                          </div>
                        </div>
                      ))}
                      {order.items?.length > 3 && (
                        <div className="flex items-center justify-center text-sm text-gray-500 bg-gray-50 rounded-lg p-3">
                          +{order.items.length - 3} more items
                        </div>
                      )}
                    </div>

                    {/* Delivery Info */}
                    {order.deliveryAddress && (
                      <div className="bg-gray-50 rounded-lg p-3 mb-4">
                        <div className="flex items-start space-x-2">
                          <FiMapPin className="w-4 h-4 text-gray-500 mt-0.5" />
                          <div>
                            <p className="text-sm font-medium text-gray-900">Delivery Address</p>
                            <p className="text-sm text-gray-600">
                              {order.deliveryAddress.street}, {order.deliveryAddress.city}
                            </p>
                            <p className="text-sm text-gray-600">
                              {order.deliveryAddress.state} - {order.deliveryAddress.pincode}
                            </p>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Delivery Partner Info */}
                    {order.deliveryPartner && (
                      <div className="bg-blue-50 rounded-lg p-3 mb-4">
                        <div className="flex items-center space-x-3">
                          <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                            <FiTruck className="w-5 h-5 text-blue-600" />
                          </div>
                          <div>
                            <p className="text-sm font-medium text-blue-900">
                              {order.deliveryPartner.name}
                            </p>
                            <p className="text-sm text-blue-600">
                              {order.deliveryPartner.phone}
                            </p>
                          </div>
                        </div>
                      </div>
                    )}

                    {/* Actions */}
                    <div className="flex flex-col sm:flex-row gap-3">
                      <button
                        onClick={() => handleTrackOrder(order._id)}
                        disabled={trackingOrder === order._id}
                        className="flex-1 sm:flex-none inline-flex items-center justify-center px-4 py-2 bg-orange-500 text-white font-medium rounded-lg hover:bg-orange-600 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                      >
                        {trackingOrder === order._id ? (
                          <>
                            <FiRefreshCw className="w-4 h-4 mr-2 animate-spin" />
                            Tracking...
                          </>
                        ) : (
                          <>
                            <FiEye className="w-4 h-4 mr-2" />
                            Track Order
                          </>
                        )}
                      </button>
                      
                      {order.status === 'delivered' && (
                        <button className="flex-1 sm:flex-none inline-flex items-center justify-center px-4 py-2 border border-orange-500 text-orange-500 font-medium rounded-lg hover:bg-orange-50 transition-colors">
                          <FiStar className="w-4 h-4 mr-2" />
                          Rate Order
                        </button>
                      )}
                      
                      <Link
                        to={`/orders/${order._id}`}
                        className="flex-1 sm:flex-none inline-flex items-center justify-center px-4 py-2 border border-gray-300 text-gray-700 font-medium rounded-lg hover:bg-gray-50 transition-colors"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}

        {/* Quick Actions */}
        <div className="mt-8 text-center">
          <div className="inline-flex flex-col sm:flex-row gap-4">
            <Link
              to="/products"
              className="inline-flex items-center px-6 py-3 bg-orange-500 text-white font-semibold rounded-lg hover:bg-orange-600 transition-colors"
            >
              Order Again
            </Link>
            <Link
              to="/cart"
              className="inline-flex items-center px-6 py-3 border border-orange-500 text-orange-500 font-semibold rounded-lg hover:bg-orange-50 transition-colors"
            >
              View Cart
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Orders;
