import React, { useEffect, useState } from 'react';
import orderService from '../../services/orderService';

const ManageOrders = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      const data = await orderService.getAllOrders();
      setOrders(data);
    };
    fetchOrders();
  }, []);

  const updateOrderStatus = async (orderId, status) => {
    await orderService.updateOrderStatus(orderId, status);
    setOrders(prev => prev.map(order => order._id === orderId ? { ...order, status } : order));
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Manage Orders</h2>
      <table className="min-w-full bg-white">
        <thead>
          <tr>
            <th className="py-2">Order ID</th>
            <th className="py-2">User</th>
            <th className="py-2">Status</th>
            <th className="py-2">Total</th>
            <th className="py-2">Actions</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order._id}>
              <td className="py-2">{order._id}</td>
              <td className="py-2">{order.user?.name}</td>
              <td className="py-2">{order.status}</td>
              <td className="py-2">â‚¹{order.total}</td>
              <td className="py-2">
                <button className="px-2 py-1 bg-blue-500 text-white rounded mr-2" onClick={() => updateOrderStatus(order._id, 'Delivered')}>Mark Delivered</button>
                <button className="px-2 py-1 bg-red-500 text-white rounded" onClick={() => updateOrderStatus(order._id, 'Cancelled')}>Cancel</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ManageOrders;
