import React from 'react';
import { FiPlus, FiStar, FiClock, FiHeart } from 'react-icons/fi';
import { SkeletonLoader } from './Loader';

const ProductCard = ({ product, onAddToCart, index = 0 }) => {
  const [isFavorite, setIsFavorite] = React.useState(false);
  const [imageLoaded, setImageLoaded] = React.useState(false);

  const handleAddToCart = () => {
    if (onAddToCart) {
      onAddToCart(product);
    } else {
      // Simple alert for demonstration
      alert(`${product.name} added to cart!`);
    }
  };

  return (
    <div 
      className="group bg-white rounded-2xl shadow-sm hover:shadow-xl transition-all duration-300 overflow-hidden border border-gray-100 hover:border-orange-200 animate-slideUp"
      style={{
        animationDelay: `${index * 0.1}s`,
        animationFillMode: 'forwards',
      }}
    >
      {/* Product Image */}
      <div className="relative overflow-hidden">
        {!imageLoaded && (
          <div className="w-full h-48 bg-gray-200 animate-pulse"></div>
        )}
        <img
          src={product.imageUrl || '/favicon.ico'}
          alt={product.name}
          className={`w-full h-48 object-cover transition-transform duration-300 group-hover:scale-105 ${
            imageLoaded ? 'block' : 'hidden'
          }`}
          onLoad={() => setImageLoaded(true)}
          onError={(e) => {
            e.target.src = '/favicon.ico';
            setImageLoaded(true);
          }}
        />
        
        {/* Overlay */}
        <div className="absolute inset-0 bg-black bg-opacity-0 group-hover:bg-opacity-20 transition-all duration-300"></div>
        
        {/* Favorite Button */}
        <button
          onClick={() => setIsFavorite(!isFavorite)}
          className="absolute top-3 right-3 w-8 h-8 bg-white bg-opacity-90 backdrop-blur-sm rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 transition-all duration-300 hover:bg-opacity-100"
        >
          <FiHeart 
            size={16} 
            className={`transition-colors ${isFavorite ? 'text-red-500 fill-current' : 'text-gray-600'}`} 
          />
        </button>

        {/* Category Badge */}
        <div className="absolute top-3 left-3">
          <span className="bg-orange-500 bg-opacity-90 backdrop-blur-sm text-white text-xs font-medium px-2 py-1 rounded-full">
            {product.category || 'Other'}
          </span>
        </div>

        {/* Rating Badge */}
        {product.rating && (
          <div className="absolute bottom-3 left-3 flex items-center space-x-1 bg-white bg-opacity-90 backdrop-blur-sm px-2 py-1 rounded-full">
            <FiStar className="text-yellow-500 fill-current" size={12} />
            <span className="text-xs font-medium text-gray-700">
              {typeof product.rating === 'object' 
                ? (product.rating.average ? product.rating.average.toFixed(1) : '0.0')
                : product.rating
              }
            </span>
          </div>
        )}
      </div>

      {/* Product Info */}
      <div className="p-4">
        <h3 className="text-lg font-semibold text-gray-900 mb-2 line-clamp-2 group-hover:text-orange-600 transition-colors">
          {product.name || 'Unnamed Product'}
        </h3>
        
        <p className="text-gray-600 text-sm mb-3 line-clamp-2 leading-relaxed">
          {product.description || 'No description available'}
        </p>

        {/* Price and Time */}
        <div className="flex items-center justify-between mb-4">
          <div className="flex flex-col">
            <span className="text-2xl font-bold text-gray-900">‚Çπ{product.price || 0}</span>
            {product.originalPrice && product.originalPrice > product.price && (
              <span className="text-sm text-gray-500 line-through">‚Çπ{product.originalPrice}</span>
            )}
          </div>
          
          {product.preparationTime && (
            <div className="flex items-center space-x-1 text-gray-500">
              <FiClock size={14} />
              <span className="text-sm">{product.preparationTime} min</span>
            </div>
          )}
        </div>

        {/* Add to Cart Button */}
        <button
          onClick={handleAddToCart}
          className="w-full bg-orange-500 hover:bg-orange-600 text-white font-medium py-3 px-4 rounded-xl transition-all duration-200 flex items-center justify-center space-x-2 shadow-md hover:shadow-lg transform hover:-translate-y-0.5"
        >
          <FiPlus size={16} />
          <span>Add to Cart</span>
        </button>
      </div>
    </div>
  );
};

const ProductGrid = ({ 
  products = [], 
  loading = false, 
  onAddToCart,
  emptyMessage = "No products found",
  columns = "grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4"
}) => {
  if (loading) {
    return <SkeletonLoader count={8} type="card" />;
  }

  if (products.length === 0) {
    return (
      <div className="col-span-full flex flex-col items-center justify-center py-16">
        <div className="w-24 h-24 bg-gray-100 rounded-full flex items-center justify-center mb-4">
          <span className="text-4xl">üçΩÔ∏è</span>
        </div>
        <h3 className="text-xl font-semibold text-gray-900 mb-2">No Products Found</h3>
        <p className="text-gray-600 text-center max-w-md">{emptyMessage}</p>
      </div>
    );
  }

  return (
    <div className={`grid ${columns} gap-6`}>
      {products.map((product, index) => (
        <ProductCard 
          key={product._id || index}
          product={product}
          onAddToCart={onAddToCart}
          index={index}
        />
      ))}
    </div>
  );
};

export default ProductGrid;
export { ProductCard };
