import { useNavigate } from 'react-router-dom';
import { CartContext } from '../../Context/CartContext';
import { AuthContext } from '../../Context/AuthContext';
import { useDarkMode } from '../../Context/DarkModeContext';
import { useToast } from '../../components/ToastContainer';
import { FiArrowLeft, FiMapPin, FiCreditCard, FiCheck, FiTruck, FiShield } from 'react-icons/fi';
import orderService from '../../services/orderService';
import paymentService from '../../services/paymentService';

const Checkout = () => {
  const { cart, getCartTotal, clearCart } = useContext(CartContext);
  const { user } = useContext(AuthContext);
  const { isDarkMode } = useDarkMode();
  const { showSuccess, showError } = useToast();
  const navigate = useNavigate();

  // State for checkout steps
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  const [stepLoading, setStepLoading] = useState(false);

  // Address form state
  const [deliveryAddress, setDeliveryAddress] = useState({
    fullName: user?.name || '',
    phone: user?.phone || '',
    addressLine1: '',
    addressLine2: '',
    city: '',
    state: '',
    pincode: '',
    landmark: ''
  });

  // Payment state
  const [paymentMethod, setPaymentMethod] = useState('card');
  const [orderNotes, setOrderNotes] = useState('');

  // Pricing calculations
  const subtotal = getCartTotal ? getCartTotal() : cart.reduce((sum, item) => {
    const price = item.product?.price || item.price || 0;
    return sum + (price * (item.quantity || 1));
  }, 0);
  const deliveryFee = subtotal > 299 ? 0 : 49;
  const tax = Math.round(subtotal * 0.05); // 5% tax
  const total = subtotal + deliveryFee + tax;

  useEffect(() => {
    // Only redirect if cart is empty (ProtectedRoute handles authentication)
    if (!cart || cart.length === 0) {
      navigate('/cart');
      return;
    }
  }, [cart, navigate]);

  const handleAddressChange = (field, value) => {
    setDeliveryAddress(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const validateAddress = () => {
    const required = ['fullName', 'phone', 'addressLine1', 'city', 'state', 'pincode'];
    return required.every(field => deliveryAddress[field].trim() !== '');
  };

  const handleAddressSubmit = async () => {
    if (!validateAddress()) {
      showError('Please fill all required address fields');
      return;
    }
    
    setStepLoading(true);
    // Simulate API call for address validation
    await new Promise(resolve => setTimeout(resolve, 1500));
    setStepLoading(false);
    
    showSuccess('Address validated successfully!');
    setCurrentStep(2);
  };

  const handlePaymentMethodSubmit = async () => {
    setStepLoading(true);
    // Simulate payment method validation
    await new Promise(resolve => setTimeout(resolve, 1000));
    setStepLoading(false);
    
    showSuccess('Payment method selected!');
    setCurrentStep(3);
  };

  const handlePlaceOrder = async () => {
    if (!validateAddress()) {
      showError('Please fill all required address fields');
      return;
    }

    setLoading(true);
    try {
      // Prepare order data
      const orderData = {
        items: cart.map(item => ({
          product: item._id || item.id,
          quantity: item.quantity,
          price: item.product?.price || item.price || 0
        })),
        deliveryAddress,
        paymentMethod,
        orderNotes,
        subtotal,
        deliveryFee,
        tax,
        total,
        paymentStatus: paymentMethod === 'cod' ? 'pending' : 'pending' // Will be updated after payment
      };

      if (paymentMethod === 'cod') {
        // Cash on Delivery - Direct order placement
        const response = await orderService.createOrder(orderData);
        
        if (response.success !== false) {
          clearCart();
          showSuccess('Order placed successfully! You can pay cash on delivery.');
          navigate('/orders');
        } else {
          throw new Error(response.error || 'Failed to place order');
        }
      } else {
        // Online Payment - Razorpay Integration
        const paymentOrderData = {
          amount: total,
          currency: 'INR',
          items: orderData.items,
          address: deliveryAddress,
          total: total
        };

        // Create Razorpay order
        const paymentResponse = await paymentService.processPayment(paymentOrderData);
        
        if (!paymentResponse.success) {
          throw new Error('Failed to create payment order');
        }

        // Configure Razorpay options
        const razorpayOptions = {
          key: paymentResponse.key_id || 'rzp_test_RAi5uQX3MTrzfi', // Your test key
          amount: paymentResponse.razorpayOrder.amount,
          currency: paymentResponse.currency,
          order_id: paymentResponse.razorpayOrder.id,
          name: 'Food Delivery',
          description: `Order for ${cart.length} items`,
          image: '/favicon.ico',
          prefill: {
            name: user?.name || deliveryAddress.fullName,
            email: user?.email || '',
            contact: deliveryAddress.phone
          },
          theme: {
            color: isDarkMode ? '#F97316' : '#F97316'
          },
          handler: async function (response) {
            try {
              // Verify payment
              const verifyData = {
                razorpayOrderId: response.razorpay_order_id,
                razorpayPaymentId: response.razorpay_payment_id,
                razorpaySignature: response.razorpay_signature
              };

              const verifyResponse = await paymentService.verifyPayment(verifyData);
              
              if (verifyResponse.message === 'Payment verified successfully') {
                // Now create the actual order with payment details
                const finalOrderData = {
                  ...orderData,
                  paymentStatus: 'completed',
                  paymentId: response.razorpay_payment_id,
                  razorpayOrderId: response.razorpay_order_id
                };

                const orderResponse = await orderService.createOrder(finalOrderData);
                
                if (orderResponse.success !== false) {
                  clearCart();
                  showSuccess('Payment successful! Order placed successfully.');
                  navigate('/orders');
                } else {
                  throw new Error('Order creation failed after payment');
                }
              } else {
                throw new Error('Payment verification failed');
              }
            } catch (error) {
              console.error('Payment verification error:', error);
              showError('Payment verification failed. Please contact support.');
            } finally {
              setLoading(false);
            }
          },
          modal: {
            ondismiss: function () {
              showError('Payment cancelled. Your order was not placed.');
              setLoading(false);
            }
          }
        };

        // Safe Razorpay initialization with wallet interference protection
        try {
          // Temporarily disable any wallet extensions during Razorpay initialization
          const originalEthereum = window.ethereum;
          if (window.ethereum && window.ethereum.request) {
            // Temporarily neutralize MetaMask during payment
            Object.defineProperty(window, 'ethereum', {
              value: undefined,
              configurable: true
            });
          }

          // Load Razorpay script if not already loaded
          if (!window.Razorpay) {
            const script = document.createElement('script');
            script.src = 'https://checkout.razorpay.com/v1/checkout.js';
            script.onload = () => {
              try {
                const rzp = new window.Razorpay(razorpayOptions);
                rzp.open();
              } catch (razorpayError) {
                console.error('Razorpay initialization error:', razorpayError);
                showError('Payment gateway failed to load. Please try again.');
                setLoading(false);
              }
            };
            script.onerror = () => {
              showError('Failed to load payment gateway. Please try again.');
              setLoading(false);
            };
            document.body.appendChild(script);
          } else {
            // Open Razorpay payment modal
            const rzp = new window.Razorpay(razorpayOptions);
            rzp.open();
          }

          // Restore ethereum object after a brief delay
          setTimeout(() => {
            if (originalEthereum) {
              Object.defineProperty(window, 'ethereum', {
                value: originalEthereum,
                configurable: true
              });
            }
          }, 1000);

        } catch (error) {
          console.error('Payment initialization error:', error);
          showError('Failed to initialize payment. Please try again.');
          setLoading(false);
        }
        
        // Don't set loading to false here as it will be handled in the payment callback
        return;
      }

    } catch (error) {
      console.error('Order placement error:', error);
      showError(error.response?.data?.error || error.message || 'Failed to place order');
    } finally {
      if (paymentMethod === 'cod') {
        setLoading(false);
      }
    }
  };

  // If cart is empty, the useEffect will redirect to cart page
  if (!cart || cart.length === 0) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold mb-4">Your cart is empty</h2>
          <p className="text-gray-600 mb-4">Add some items to your cart to proceed with checkout</p>
          <button 
            onClick={() => navigate('/products')}
            className="px-6 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
          >
            Browse Products
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className={`min-h-screen py-8 ${isDarkMode ? 'bg-gray-900 text-white' : 'bg-gray-50 text-gray-900'}`}>
      <div className="max-w-6xl mx-auto px-4">
        {/* Header */}
        <div className="mb-8">
          <button
            onClick={() => navigate('/cart')}
            className={`flex items-center mb-4 transition-colors ${
              isDarkMode ? 'text-gray-400 hover:text-gray-200' : 'text-gray-600 hover:text-gray-800'
            }`}
          >
            <FiArrowLeft className="mr-2" />
            Back to Cart
          </button>
          <h1 className={`text-3xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>Checkout</h1>
        </div>

        {/* Step Progress Bar */}
        <div className={`mb-8 p-6 rounded-xl ${isDarkMode ? 'bg-gray-800 border border-gray-700' : 'bg-white border border-gray-200'} shadow-sm`}>
          <div className="flex items-center justify-between">
            {/* Step 1 */}
            <div className="flex items-center">
              <div className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-bold mr-3 transition-all ${
                currentStep >= 1 
                  ? 'bg-green-500 text-white shadow-lg shadow-green-500/25' 
                  : isDarkMode ? 'bg-gray-700 text-gray-400' : 'bg-gray-200 text-gray-600'
              }`}>
                {currentStep > 1 ? <FiCheck size={16} /> : '1'}
              </div>
              <div>
                <p className={`font-semibold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Delivery Address</p>
                <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>Where should we deliver?</p>
              </div>
            </div>

            {/* Progress Line */}
            <div className={`flex-1 mx-4 h-1 rounded-full ${isDarkMode ? 'bg-gray-700' : 'bg-gray-200'}`}>
              <div 
                className={`h-1 rounded-full transition-all duration-500 ${
                  currentStep >= 2 ? 'bg-green-500' : 'bg-transparent'
                }`} 
                style={{ width: currentStep >= 2 ? '100%' : '0%' }}
              />
            </div>

            {/* Step 2 */}
            <div className="flex items-center">
              <div className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-bold mr-3 transition-all ${
                currentStep >= 2 
                  ? 'bg-green-500 text-white shadow-lg shadow-green-500/25' 
                  : isDarkMode ? 'bg-gray-700 text-gray-400' : 'bg-gray-200 text-gray-600'
              }`}>
                {currentStep > 2 ? <FiCheck size={16} /> : '2'}
              </div>
              <div>
                <p className={`font-semibold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Payment Method</p>
                <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>Choose how to pay</p>
              </div>
            </div>

            {/* Progress Line */}
            <div className={`flex-1 mx-4 h-1 rounded-full ${isDarkMode ? 'bg-gray-700' : 'bg-gray-200'}`}>
              <div 
                className={`h-1 rounded-full transition-all duration-500 ${
                  currentStep >= 3 ? 'bg-green-500' : 'bg-transparent'
                }`} 
                style={{ width: currentStep >= 3 ? '100%' : '0%' }}
              />
            </div>

            {/* Step 3 */}
            <div className="flex items-center">
              <div className={`w-10 h-10 rounded-full flex items-center justify-center text-sm font-bold mr-3 transition-all ${
                currentStep >= 3 
                  ? 'bg-green-500 text-white shadow-lg shadow-green-500/25' 
                  : isDarkMode ? 'bg-gray-700 text-gray-400' : 'bg-gray-200 text-gray-600'
              }`}>
                {currentStep > 3 ? <FiCheck size={16} /> : '3'}
              </div>
              <div>
                <p className={`font-semibold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Confirm Order</p>
                <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>Review and place order</p>
              </div>
            </div>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Left Column - Step Forms */}
          <div className="lg:col-span-2">
            
            {/* Step 1: Delivery Address */}
            {currentStep === 1 && (
              <div className={`rounded-xl shadow-lg p-6 border transition-all ${
                isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-100'
              }`}>
                <div className="flex items-center mb-6">
                  <FiMapPin className="w-6 h-6 mr-3 text-orange-500" />
                  <h2 className={`text-xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
                    Delivery Address
                  </h2>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                  <div>
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      Full Name *
                    </label>
                    <input
                      type="text"
                      value={deliveryAddress.fullName}
                      onChange={(e) => handleAddressChange('fullName', e.target.value)}
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                      placeholder="Enter your full name"
                      required
                    />
                  </div>

                  <div>
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      Phone Number *
                    </label>
                    <input
                      type="tel"
                      value={deliveryAddress.phone}
                      onChange={(e) => handleAddressChange('phone', e.target.value)}
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                      placeholder="Enter your phone number"
                      required
                    />
                  </div>

                  <div className="md:col-span-2">
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      Address Line 1 *
                    </label>
                    <input
                      type="text"
                      value={deliveryAddress.addressLine1}
                      onChange={(e) => handleAddressChange('addressLine1', e.target.value)}
                      placeholder="House/Flat No., Street Name"
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                      required
                    />
                  </div>

                  <div className="md:col-span-2">
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      Address Line 2 (Optional)
                    </label>
                    <input
                      type="text"
                      value={deliveryAddress.addressLine2}
                      onChange={(e) => handleAddressChange('addressLine2', e.target.value)}
                      placeholder="Apartment, Floor, Building (Optional)"
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                    />
                  </div>

                  <div>
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      City *
                    </label>
                    <input
                      type="text"
                      value={deliveryAddress.city}
                      onChange={(e) => handleAddressChange('city', e.target.value)}
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                      placeholder="Enter city"
                      required
                    />
                  </div>

                  <div>
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      State *
                    </label>
                    <input
                      type="text"
                      value={deliveryAddress.state}
                      onChange={(e) => handleAddressChange('state', e.target.value)}
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                      placeholder="Enter state"
                      required
                    />
                  </div>

                  <div>
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      PIN Code *
                    </label>
                    <input
                      type="text"
                      value={deliveryAddress.pincode}
                      onChange={(e) => handleAddressChange('pincode', e.target.value)}
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                      placeholder="Enter PIN code"
                      maxLength="6"
                      required
                    />
                  </div>

                  <div>
                    <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                      Landmark (Optional)
                    </label>
                    <input
                      type="text"
                      value={deliveryAddress.landmark}
                      onChange={(e) => handleAddressChange('landmark', e.target.value)}
                      placeholder="Near bus stop, mall, etc."
                      className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent transition-all ${
                        isDarkMode 
                          ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                          : 'bg-white border-gray-300 text-gray-900'
                      }`}
                    />
                  </div>
                </div>

                <button
                  onClick={handleAddressSubmit}
                  disabled={!validateAddress() || stepLoading}
                  className={`w-full py-4 px-6 rounded-xl font-semibold text-white transition-all duration-200 disabled:opacity-50 disabled:cursor-not-allowed ${
                    isDarkMode
                      ? 'bg-gradient-to-r from-orange-500 to-red-500 hover:from-orange-600 hover:to-red-600 shadow-lg shadow-orange-500/25'
                      : 'bg-gradient-to-r from-orange-500 to-red-500 hover:from-orange-600 hover:to-red-600 shadow-lg hover:shadow-xl'
                  }`}
                >
                  {stepLoading ? (
                    <div className="flex items-center justify-center">
                      <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                      Validating Address...
                    </div>
                  ) : (
                    <>
                      Continue to Payment
                      <FiArrowLeft className="ml-2 rotate-180 w-4 h-4 inline" />
                    </>
                  )}
                </button>
              </div>
            )}

                <div className="md:col-span-2">
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Address Line 2
                  </label>
                  <input
                    type="text"
                    value={deliveryAddress.addressLine2}
                    onChange={(e) => handleAddressChange('addressLine2', e.target.value)}
                    placeholder="Apartment, Suite, etc. (optional)"
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    City *
                  </label>
                  <input
                    type="text"
                    value={deliveryAddress.city}
                    onChange={(e) => handleAddressChange('city', e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    State *
                  </label>
                  <input
                    type="text"
                    value={deliveryAddress.state}
                    onChange={(e) => handleAddressChange('state', e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Pincode *
                  </label>
                  <input
                    type="text"
                    value={deliveryAddress.pincode}
                    onChange={(e) => handleAddressChange('pincode', e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Landmark
                  </label>
                  <input
                    type="text"
                    value={deliveryAddress.landmark}
                    onChange={(e) => handleAddressChange('landmark', e.target.value)}
                    placeholder="Near bus stop, mall, etc."
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                  />
                </div>
              </div>

              {validateAddress() && (
                <button
                  onClick={() => setCurrentStep(2)}
                  className="mt-6 bg-orange-500 text-white px-6 py-3 rounded-lg hover:bg-orange-600 transition-colors font-semibold flex items-center"
                >
                  Continue to Payment
                  <FiArrowLeft className="ml-2 rotate-180" />
                </button>
              )}
            </div>

            {/* Step 2: Payment Method */}
            {currentStep >= 2 && (
              <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
                <div className="flex items-center mb-6">
                  <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-bold mr-3 ${
                    currentStep >= 2 ? 'bg-green-500 text-white' : 'bg-gray-200 text-gray-600'
                  }`}>
                    {currentStep > 2 ? <FiCheck size={16} /> : '2'}
                  </div>
                  <h2 className="text-xl font-bold text-gray-800">Payment Method</h2>
                </div>

                <div className="space-y-4">
                  <label className="flex items-center p-4 border border-gray-300 rounded-lg cursor-pointer hover:border-orange-500 transition-colors">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="cod"
                      checked={paymentMethod === 'cod'}
                      onChange={(e) => setPaymentMethod(e.target.value)}
                      className="mr-4"
                    />
                    <div className="flex items-center">
                      <FiTruck className="text-green-500 mr-3" size={24} />
                      <div>
                        <p className="font-semibold">Cash on Delivery</p>
                        <p className="text-sm text-gray-600">Pay when your order arrives</p>
                      </div>
                    </div>
                  </label>

                  <label className="flex items-center p-4 border border-gray-300 rounded-lg cursor-pointer hover:border-orange-500 transition-colors">
                    <input
                      type="radio"
                      name="paymentMethod"
                      value="card"
                      checked={paymentMethod === 'card'}
                      onChange={(e) => setPaymentMethod(e.target.value)}
                      className="mr-4"
                    />
                    <div className="flex items-center">
                      <FiCreditCard className="text-blue-500 mr-3" size={24} />
                      <div>
                        <p className="font-semibold">Credit/Debit Card</p>
                        <p className="text-sm text-gray-600">Secure online payment</p>
                      </div>
                    </div>
                  </label>
                </div>

                <button
                  onClick={() => setCurrentStep(3)}
                  className="mt-6 bg-orange-500 text-white px-6 py-3 rounded-lg hover:bg-orange-600 transition-colors font-semibold flex items-center"
                >
                  Review Order
                  <FiArrowLeft className="ml-2 rotate-180" />
                </button>
              </div>
            )}

            {/* Step 3: Order Notes */}
            {currentStep >= 3 && (
              <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100">
                <div className="flex items-center mb-6">
                  <div className="w-8 h-8 rounded-full bg-green-500 text-white flex items-center justify-center text-sm font-bold mr-3">
                    3
                  </div>
                  <h2 className="text-xl font-bold text-gray-800">Order Notes (Optional)</h2>
                </div>

                <textarea
                  value={orderNotes}
                  onChange={(e) => setOrderNotes(e.target.value)}
                  placeholder="Any special instructions for your order..."
                  className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                  rows="4"
                />
              </div>
            )}
          </div>

          {/* Right Column - Order Summary */}
          <div className="lg:col-span-1">
            <div className="bg-white rounded-xl shadow-lg p-6 border border-gray-100 sticky top-4">
              <h3 className="text-xl font-bold text-gray-800 mb-6">Order Summary</h3>

              {/* Cart Items */}
              <div className="space-y-4 mb-6">
                {cart.map((item) => (
                  <div key={item._id || item.id} className="flex justify-between items-center py-2 border-b border-gray-100">
                    <div className="flex-1">
                      <h4 className="font-medium text-gray-800">{item.name}</h4>
                      <p className="text-sm text-gray-600">Qty: {item.quantity}</p>
                    </div>
                    <span className="font-semibold text-gray-800">{item.price * item.quantity}</span>
                  </div>
                ))}
              </div>

              {/* Pricing Breakdown */}
              <div className="space-y-3 mb-6">
                <div className="flex justify-between">
                  <span className="text-gray-600">Subtotal</span>
                  <span className="text-gray-800">{subtotal}</span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Delivery Fee</span>
                  <span className="text-gray-800">
                    {deliveryFee === 0 ? (
                      <span className="text-green-600">FREE</span>
                    ) : (
                      `${deliveryFee}`
                    )}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className="text-gray-600">Tax</span>
                  <span className="text-gray-800">{tax}</span>
                </div>
                <div className="border-t border-gray-200 pt-3">
                  <div className="flex justify-between text-lg font-bold">
                    <span className="text-gray-800">Total</span>
                    <span className="text-gray-800">{total}</span>
                  </div>
                </div>
              </div>

              {/* Security Notice */}
              <div className="bg-gray-50 rounded-lg p-4 mb-6">
                <div className="flex items-center text-green-600 mb-2">
                  <FiShield className="mr-2" size={16} />
                  <span className="text-sm font-medium">Secure Checkout</span>
                </div>
                <p className="text-xs text-gray-600">
                  Your payment information is encrypted and secure
                </p>
              </div>

              {/* Place Order Button */}
              {currentStep >= 3 && validateAddress() && (
                <button
                  onClick={handlePlaceOrder}
                  disabled={loading}
                  className="w-full bg-gradient-to-r from-orange-500 to-red-500 text-white font-bold py-4 rounded-lg hover:from-orange-600 hover:to-red-600 transition-all duration-300 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  {loading ? 'Placing Order...' : `Place Order  ${total}`}
                </button>
              )}
            )}

            {/* Step 2: Payment Method */}
            {currentStep === 2 && (
              <div className={`rounded-xl shadow-lg p-6 border transition-all ${
                isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-100'
              }`}>
                <div className="flex items-center mb-6">
                  <FiCreditCard className="w-6 h-6 mr-3 text-green-500" />
                  <h2 className={`text-xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
                    Payment Method
                  </h2>
                </div>

                <div className="space-y-4 mb-6">
                  {/* Cash on Delivery */}
                  <div className={`p-4 border-2 rounded-xl cursor-pointer transition-all ${
                    paymentMethod === 'cod' 
                      ? 'border-green-500 bg-green-50 dark:bg-green-900/20' 
                      : isDarkMode ? 'border-gray-700 bg-gray-700/50' : 'border-gray-200 bg-gray-50'
                  }`} onClick={() => setPaymentMethod('cod')}>
                    <div className="flex items-center">
                      <input
                        type="radio"
                        name="paymentMethod"
                        value="cod"
                        checked={paymentMethod === 'cod'}
                        onChange={() => setPaymentMethod('cod')}
                        className="mr-3 text-green-500"
                      />
                      <div className="flex-1">
                        <h3 className={`font-semibold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                          Cash on Delivery
                        </h3>
                        <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                          Pay with cash when your order arrives
                        </p>
                      </div>
                    </div>
                  </div>

                  {/* Online Payment */}
                  <div className={`p-4 border-2 rounded-xl cursor-pointer transition-all ${
                    paymentMethod === 'card' 
                      ? 'border-green-500 bg-green-50 dark:bg-green-900/20' 
                      : isDarkMode ? 'border-gray-700 bg-gray-700/50' : 'border-gray-200 bg-gray-50'
                  }`} onClick={() => setPaymentMethod('card')}>
                    <div className="flex items-center">
                      <input
                        type="radio"
                        name="paymentMethod"
                        value="card"
                        checked={paymentMethod === 'card'}
                        onChange={() => setPaymentMethod('card')}
                        className="mr-3 text-green-500"
                      />
                      <div className="flex-1">
                        <h3 className={`font-semibold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                          Online Payment
                        </h3>
                        <p className={`text-sm ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                          Pay securely with UPI, Cards, Net Banking
                        </p>
                      </div>
                      <div className="text-xs text-green-600 font-medium">Secure</div>
                    </div>
                  </div>
                </div>

                <div className="flex space-x-4">
                  <button
                    onClick={() => setCurrentStep(1)}
                    className={`flex-1 py-3 px-6 rounded-xl font-semibold transition-all ${
                      isDarkMode 
                        ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' 
                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                    }`}
                  >
                     Back
                  </button>
                  <button
                    onClick={handlePaymentMethodSubmit}
                    disabled={stepLoading}
                    className={`flex-1 py-3 px-6 rounded-xl font-semibold text-white transition-all disabled:opacity-50 ${
                      isDarkMode
                        ? 'bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 shadow-lg shadow-green-500/25'
                        : 'bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 shadow-lg hover:shadow-xl'
                    }`}
                  >
                    {stepLoading ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                        Processing...
                      </div>
                    ) : (
                      'Continue to Review'
                    )}
                  </button>
                </div>
              </div>
            )}

            {/* Step 3: Order Review */}
            {currentStep === 3 && (
              <div className={`rounded-xl shadow-lg p-6 border transition-all ${
                isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-100'
              }`}>
                <div className="flex items-center mb-6">
                  <FiCheck className="w-6 h-6 mr-3 text-blue-500" />
                  <h2 className={`text-xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
                    Review Your Order
                  </h2>
                </div>

                {/* Address Summary */}
                <div className={`p-4 rounded-lg mb-4 ${isDarkMode ? 'bg-gray-700' : 'bg-gray-50'}`}>
                  <h3 className={`font-semibold mb-2 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    Delivery Address
                  </h3>
                  <p className={`text-sm ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                    {deliveryAddress.fullName}<br />
                    {deliveryAddress.addressLine1}{deliveryAddress.addressLine2 && `, ${deliveryAddress.addressLine2}`}<br />
                    {deliveryAddress.city}, {deliveryAddress.state} - {deliveryAddress.pincode}<br />
                    Phone: {deliveryAddress.phone}
                  </p>
                </div>

                {/* Payment Method Summary */}
                <div className={`p-4 rounded-lg mb-6 ${isDarkMode ? 'bg-gray-700' : 'bg-gray-50'}`}>
                  <h3 className={`font-semibold mb-2 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    Payment Method
                  </h3>
                  <p className={`text-sm ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                    {paymentMethod === 'cod' ? 'Cash on Delivery' : 'Online Payment (Razorpay)'}
                  </p>
                </div>

                {/* Order Notes */}
                <div className="mb-6">
                  <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>
                    Order Notes (Optional)
                  </label>
                  <textarea
                    value={orderNotes}
                    onChange={(e) => setOrderNotes(e.target.value)}
                    placeholder="Any special instructions for your order..."
                    className={`w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all resize-none ${
                      isDarkMode 
                        ? 'bg-gray-700 border-gray-600 text-white placeholder-gray-400' 
                        : 'bg-white border-gray-300 text-gray-900'
                    }`}
                    rows="3"
                  />
                </div>

                <div className="flex space-x-4">
                  <button
                    onClick={() => setCurrentStep(2)}
                    className={`flex-1 py-3 px-6 rounded-xl font-semibold transition-all ${
                      isDarkMode 
                        ? 'bg-gray-700 text-gray-300 hover:bg-gray-600' 
                        : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
                    }`}
                  >
                     Back
                  </button>
                  <button
                    onClick={handlePlaceOrder}
                    disabled={loading}
                    className={`flex-1 py-4 px-6 rounded-xl font-bold text-white transition-all disabled:opacity-50 ${
                      isDarkMode
                        ? 'bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 shadow-lg shadow-blue-500/25'
                        : 'bg-gradient-to-r from-blue-500 to-purple-500 hover:from-blue-600 hover:to-purple-600 shadow-lg hover:shadow-xl'
                    }`}
                  >
                    {loading ? (
                      <div className="flex items-center justify-center">
                        <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2"></div>
                        Placing Order...
                      </div>
                    ) : (
                      <>
                        <FiShield className="w-5 h-5 mr-2 inline" />
                        Place Order - {total.toFixed(2)}
                      </>
                    )}
                  </button>
                </div>
              </div>
            )}
          </div>

          {/* Right Column - Order Summary */}
          <div className="lg:col-span-1">
            <div className={`rounded-xl shadow-lg p-6 border sticky top-4 ${
              isDarkMode ? 'bg-gray-800 border-gray-700' : 'bg-white border-gray-100'
            }`}>
              <h3 className={`text-xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-800'}`}>
                Order Summary
              </h3>
              
              {/* Cart Items */}
              <div className="space-y-3 mb-4 max-h-60 overflow-y-auto">
                {cart.map((item) => (
                  <div key={item._id} className="flex justify-between items-start">
                    <div className="flex-1">
                      <p className={`font-medium text-sm ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                        {item.product?.name || item.name}
                      </p>
                      <p className={`text-xs ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                        Qty: {item.quantity || 1}
                      </p>
                    </div>
                    <p className={`font-semibold text-sm ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                      {((item.product?.price || item.price || 0) * (item.quantity || 1)).toFixed(2)}
                    </p>
                  </div>
                ))}
              </div>

              <div className={`border-t pt-4 space-y-2 ${isDarkMode ? 'border-gray-700' : 'border-gray-200'}`}>
                <div className="flex justify-between">
                  <span className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Subtotal</span>
                  <span className={isDarkMode ? 'text-white' : 'text-gray-900'}>{subtotal.toFixed(2)}</span>
                </div>
                <div className="flex justify-between">
                  <span className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Delivery Fee</span>
                  <span className={`${deliveryFee === 0 ? 'text-green-500' : ''} ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    {deliveryFee === 0 ? 'FREE' : `${deliveryFee.toFixed(2)}`}
                  </span>
                </div>
                <div className="flex justify-between">
                  <span className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Tax (5%)</span>
                  <span className={isDarkMode ? 'text-white' : 'text-gray-900'}>{tax.toFixed(2)}</span>
                </div>
                <div className={`flex justify-between text-lg font-bold pt-2 border-t ${
                  isDarkMode ? 'border-gray-700 text-white' : 'border-gray-200 text-gray-900'
                }`}>
                  <span>Total</span>
                  <span>{total.toFixed(2)}</span>
                </div>
              </div>

              {/* Security Badge */}
              <div className={`mt-4 p-3 rounded-lg text-center ${isDarkMode ? 'bg-green-900/20' : 'bg-green-50'}`}>
                <FiShield className="w-5 h-5 mx-auto mb-1 text-green-500" />
                <p className="text-xs text-green-600 font-medium">
                  Your payment information is encrypted and secure
                </p>
              </div>
            </div>
          </div>
      </div>
    </div>
  );
};

export default Checkout;
