import axios from 'axios';

const API = '/api/payment';

// Configure axios to send credentials with all requests
axios.defaults.withCredentials = true;

const paymentService = {
  // Create Razorpay order
  createPaymentOrder: async (data) => {
    const res = await axios.post(`${API}/create-order`, data);
    return res.data;
  },

  // Process payment (create order)
  processPayment: async (data) => {
    const res = await axios.post(`${API}/pay`, data);
    return res.data;
  },

  // Verify payment after successful transaction
  verifyPayment: async (data) => {
    const res = await axios.post(`${API}/verify`, data);
    return res.data;
  },

  // Handle payment failure
  paymentFailed: async (data) => {
    const res = await axios.post(`${API}/failed`, data);
    return res.data;
  },

  // Initialize Razorpay payment
  initiateRazorpayPayment: (options) => {
    return new Promise((resolve, reject) => {
      const rzp = new window.Razorpay({
        ...options,
        handler: function (response) {
          resolve(response);
        },
        modal: {
          ondismiss: function () {
            reject(new Error('Payment cancelled by user'));
          }
        }
      });
      rzp.open();
    });
  }
};

export default paymentService;
