import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import authService from '../../services/authService';
import { AiOutlineEye, AiOutlineEyeInvisible } from 'react-icons/ai';
import Loader from '../../components/Loader';

const Signup = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      await authService.signup({ name, email, password });
      setSuccess('Signup successful! Redirecting to login...');
      setTimeout(() => {
        navigate('/login');
      }, 1800);
    } catch (err) {
      setError('Signup failed. Please check your details.');
      setTimeout(() => setError(''), 3000);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-yellow-100 via-red-100 to-pink-100 p-4">
      <form
        onSubmit={handleSubmit}
        className="bg-white p-8 md:p-10 rounded-3xl shadow-2xl w-full max-w-md relative overflow-hidden hover:shadow-3xl transition-shadow duration-300 animate-fadeIn"
        aria-label="Signup form"
      >
        <h2 className="text-3xl font-extrabold mb-6 text-gray-800 text-center">
          Create Account
        </h2>

        {loading && (
          <div className="flex justify-center mb-4">
            <Loader />
          </div>
        )}
        {error && (
          <p className="text-red-500 mb-4 text-center animate-pulse font-medium">
            {error}
          </p>
        )}
        {success && (
          <p className="text-green-500 mb-4 text-center animate-fadeIn font-medium">
            {success}
          </p>
        )}

        {/* Name Input */}
        <div className="relative mb-6">
          <input
            type="text"
            id="name"
            placeholder=" "
            className="peer w-full border-b-2 border-gray-300 focus:border-yellow-500 outline-none py-2 placeholder-transparent transition-colors"
            value={name}
            onChange={e => setName(e.target.value)}
            required
            aria-label="Name"
            autoComplete="name"
          />
          <label
            htmlFor="name"
            className="absolute left-0 -top-3.5 text-gray-500 text-sm transition-all peer-placeholder-shown:top-2 peer-placeholder-shown:text-gray-400 peer-placeholder-shown:text-base peer-focus:-top-3.5 peer-focus:text-gray-500 peer-focus:text-sm"
          >
            Name
          </label>
        </div>

        {/* Email Input */}
        <div className="relative mb-6">
          <input
            type="email"
            id="email"
            placeholder=" "
            className="peer w-full border-b-2 border-gray-300 focus:border-yellow-500 outline-none py-2 placeholder-transparent transition-colors"
            value={email}
            onChange={e => setEmail(e.target.value)}
            required
            aria-label="Email"
            autoComplete="email"
          />
          <label
            htmlFor="email"
            className="absolute left-0 -top-3.5 text-gray-500 text-sm transition-all peer-placeholder-shown:top-2 peer-placeholder-shown:text-gray-400 peer-placeholder-shown:text-base peer-focus:-top-3.5 peer-focus:text-gray-500 peer-focus:text-sm"
          >
            Email
          </label>
        </div>

        {/* Password Input */}
        <div className="relative mb-6">
          <input
            type={showPassword ? 'text' : 'password'}
            id="password"
            placeholder=" "
            className="peer w-full border-b-2 border-gray-300 focus:border-yellow-500 outline-none py-2 pr-10 placeholder-transparent transition-colors"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
            aria-label="Password"
            autoComplete="new-password"
          />
          <label
            htmlFor="password"
            className="absolute left-0 -top-3.5 text-gray-500 text-sm transition-all peer-placeholder-shown:top-2 peer-placeholder-shown:text-gray-400 peer-placeholder-shown:text-base peer-focus:-top-3.5 peer-focus:text-gray-500 peer-focus:text-sm"
          >
            Password
          </label>
          <span
            className="absolute right-0 top-2.5 cursor-pointer text-gray-500"
            onClick={() => setShowPassword(!showPassword)}
            tabIndex={0}
            aria-label={showPassword ? 'Hide password' : 'Show password'}
            role="button"
          >
            {showPassword ? <AiOutlineEyeInvisible size={20} /> : <AiOutlineEye size={20} />}
          </span>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          className="w-full py-3 bg-gradient-to-r from-yellow-400 via-red-400 to-pink-500 text-white font-semibold rounded-xl shadow-lg hover:scale-105 transform transition-all duration-300 disabled:opacity-60"
          disabled={loading}
        >
          {loading ? 'Signing Up...' : 'Sign Up'}
        </button>

        <p className="mt-4 text-sm text-gray-500 text-center">
          Already have an account?{' '}
          <Link to="/login" className="text-yellow-500 font-medium hover:underline">
            Login
          </Link>
        </p>
      </form>
    </div>
  );
};

export default Signup;
