import React, { createContext, useState, useContext, useCallback } from 'react';

export const NotificationContext = createContext();

// Custom hook for using notification context
export const useNotification = () => {
  const context = useContext(NotificationContext);
  if (!context) {
    throw new Error('useNotification must be used within a NotificationProvider');
  }
  return context;
};

export const NotificationProvider = ({ children }) => {
  const [notifications, setNotifications] = useState([]);

  // Remove notification
  const removeNotification = useCallback((id) => {
    setNotifications(prev => prev.filter(notification => notification.id !== id));
  }, []);

  // Add notification
  const addNotification = useCallback((notification) => {
    const id = Date.now() + Math.random();
    const newNotification = {
      id,
      type: 'info', // info, success, warning, error
      title: '',
      message: '',
      duration: 5000, // 5 seconds default
      persistent: false,
      action: null,
      ...notification
    };

    setNotifications(prev => [...prev, newNotification]);

    // Auto-remove notification if not persistent
    if (!newNotification.persistent && newNotification.duration > 0) {
      setTimeout(() => {
        removeNotification(id);
      }, newNotification.duration);
    }

    return id;
  }, [removeNotification]);

  // Clear all notifications
  const clearAllNotifications = useCallback(() => {
    setNotifications([]);
  }, []);

  // Utility functions for different notification types
  const showSuccess = useCallback((message, options = {}) => {
    return addNotification({
      type: 'success',
      message,
      ...options
    });
  }, [addNotification]);

  const showError = useCallback((message, options = {}) => {
    return addNotification({
      type: 'error',
      message,
      duration: 8000, // Longer duration for errors
      ...options
    });
  }, [addNotification]);

  const showWarning = useCallback((message, options = {}) => {
    return addNotification({
      type: 'warning',
      message,
      ...options
    });
  }, [addNotification]);

  const showInfo = useCallback((message, options = {}) => {
    return addNotification({
      type: 'info',
      message,
      ...options
    });
  }, [addNotification]);

  const showPersistent = useCallback((message, type = 'info', options = {}) => {
    return addNotification({
      type,
      message,
      persistent: true,
      duration: 0,
      ...options
    });
  }, [addNotification]);

  // Show loading notification
  const showLoading = useCallback((message = 'Loading...', options = {}) => {
    return addNotification({
      type: 'loading',
      message,
      persistent: true,
      duration: 0,
      ...options
    });
  }, [addNotification]);

  // Order-specific notifications
  const showOrderSuccess = useCallback((orderNumber) => {
    return showSuccess(`Order #${orderNumber} placed successfully! ðŸŽ‰`, {
      title: 'Order Confirmed',
      duration: 7000
    });
  }, [showSuccess]);

  const showItemAdded = useCallback((itemName) => {
    return showSuccess(`${itemName} added to cart`, {
      duration: 3000
    });
  }, [showSuccess]);

  const showItemRemoved = useCallback((itemName) => {
    return showInfo(`${itemName} removed from cart`, {
      duration: 3000
    });
  }, [showInfo]);

  const showCartCleared = useCallback(() => {
    return showInfo('Cart cleared', {
      duration: 3000
    });
  }, [showInfo]);

  const showLoginSuccess = useCallback((userName) => {
    return showSuccess(`Welcome back, ${userName}! ðŸ‘‹`, {
      title: 'Login Successful',
      duration: 4000
    });
  }, [showSuccess]);

  const showLogoutSuccess = useCallback(() => {
    return showInfo('Successfully logged out', {
      duration: 3000
    });
  }, [showInfo]);

  // Update notification
  const updateNotification = useCallback((id, updates) => {
    setNotifications(prev => 
      prev.map(notification => 
        notification.id === id 
          ? { ...notification, ...updates }
          : notification
      )
    );
  }, []);

  const value = {
    // State
    notifications,
    
    // Actions
    addNotification,
    removeNotification,
    clearAllNotifications,
    updateNotification,
    
    // Utility functions
    showSuccess,
    showError,
    showWarning,
    showInfo,
    showPersistent,
    showLoading,
    
    // Specialized notifications
    showOrderSuccess,
    showItemAdded,
    showItemRemoved,
    showCartCleared,
    showLoginSuccess,
    showLogoutSuccess,
    
    // Derived state
    hasNotifications: notifications.length > 0,
    errorCount: notifications.filter(n => n.type === 'error').length,
    successCount: notifications.filter(n => n.type === 'success').length
  };

  return (
    <NotificationContext.Provider value={value}>
      {children}
    </NotificationContext.Provider>
  );
};
