import React, { useEffect, useState, useContext } from 'react';
import { CartContext } from '../../Context/CartContext';
import productService from '../../services/productService';
import CategoryFilter from '../../components/CategoryFilter';
import Loader from '../../components/Loader';

const Home = () => {
  const { addToCart } = useContext(CartContext);
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('All');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    const fetchProducts = async () => {
      setLoading(true);
      setError('');
      try {
        const data = await productService.getAllProducts();
        setProducts(data);
      } catch (error) {
        setError('Failed to fetch products. Please try again later.');
      } finally {
        setLoading(false);
      }
    };
    fetchProducts();
  }, []);

  const filteredProducts =
    selectedCategory === 'All'
      ? products
      : products.filter(product => product.category === selectedCategory);

  return (
    <div className="min-h-screen flex flex-col items-center justify-start bg-gradient-to-br from-yellow-100 via-red-100 to-pink-100 p-4">
      <h2 className="text-3xl md:text-4xl font-extrabold mb-6 text-center text-gray-800 animate-fadeIn">
        Welcome to Food Delivery
      </h2>

      <div className="w-full max-w-4xl">
        <CategoryFilter
          selectedCategory={selectedCategory}
          onCategoryChange={setSelectedCategory}
        />
      </div>

      {loading && (
        <div className="flex justify-center items-center w-full h-40">
          <Loader />
        </div>
      )}
      {error && (
        <div className="w-full max-w-2xl mx-auto mb-4">
          <p className="text-red-500 text-center font-medium animate-pulse bg-white rounded-xl shadow p-3">
            {error}
          </p>
        </div>
      )}
      {success && (
        <div className="w-full max-w-2xl mx-auto mb-4">
          <p className="text-green-500 text-center font-medium animate-fadeIn bg-white rounded-xl shadow p-3">
            {success}
          </p>
        </div>
      )}

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-8 mt-8 w-full max-w-6xl">
        {filteredProducts.map((product, index) => (
          <div
            key={product._id}
            className="bg-white rounded-3xl shadow-2xl p-6 flex flex-col items-center text-center transform transition-transform duration-300 hover:scale-105 hover:shadow-3xl opacity-0 animate-fadeIn"
            style={{
              animationDelay: `${index * 0.1}s`,
              animationFillMode: 'forwards',
            }}
          >
            <div className="w-40 h-40 mb-4 overflow-hidden rounded-xl">
              <img
                src={product.imageUrl || '/favicon.ico'}
                alt={product.name}
                className="w-full h-full object-cover transition-transform duration-300 hover:scale-110"
                onError={e => { e.target.src = '/favicon.ico'; }}
              />
            </div>

            <h3 className="text-lg md:text-xl font-semibold mb-2 text-yellow-600 hover:text-yellow-800 transition-colors">
              {product.name}
            </h3>

            <p className="text-gray-500 text-sm mb-2 line-clamp-2">
              {product.description}
            </p>

            <p className="text-gray-400 text-xs mb-1 uppercase tracking-wide">
              {product.category}
            </p>

            <p className="text-green-600 font-bold text-lg mb-4">â‚¹{product.price}</p>

            <button
              onClick={() => {
                addToCart(product);
                setSuccess(`${product.name} added to cart!`);
                setTimeout(() => setSuccess(''), 1500);
              }}
              className="px-6 py-2 bg-gradient-to-r from-blue-500 to-blue-700 text-white rounded-full shadow-lg hover:from-blue-700 hover:to-blue-900 transition-all duration-300 active:scale-95 focus:outline-none focus:ring-2 focus:ring-blue-400"
              aria-label={`Add ${product.name} to cart`}
            >
              <span className="inline-block animate-bounce">ðŸ›’</span> Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
