import React from 'react';
import { CheckCircle, XCircle, AlertCircle, Info, X, Loader } from 'react-feather';
import { useNotification } from '../Context/NotificationContext';

const Toast = ({ notification }) => {
  const { removeNotification } = useNotification();

  const getIcon = () => {
    switch (notification.type) {
      case 'success':
        return <CheckCircle className="w-5 h-5 text-green-600" />;
      case 'error':
        return <XCircle className="w-5 h-5 text-red-600" />;
      case 'warning':
        return <AlertCircle className="w-5 h-5 text-yellow-600" />;
      case 'loading':
        return <Loader className="w-5 h-5 text-blue-600 animate-spin" />;
      default:
        return <Info className="w-5 h-5 text-blue-600" />;
    }
  };

  const getBackgroundColor = () => {
    switch (notification.type) {
      case 'success':
        return 'bg-green-50 border-green-200';
      case 'error':
        return 'bg-red-50 border-red-200';
      case 'warning':
        return 'bg-yellow-50 border-yellow-200';
      case 'loading':
        return 'bg-blue-50 border-blue-200';
      default:
        return 'bg-blue-50 border-blue-200';
    }
  };

  return (
    <div className={`flex items-start p-4 rounded-lg border shadow-md max-w-sm w-full transform transition-all duration-300 ${getBackgroundColor()}`}>
      <div className="flex-shrink-0">
        {getIcon()}
      </div>
      
      <div className="ml-3 flex-1">
        {notification.title && (
          <h4 className="text-sm font-semibold text-gray-900 mb-1">
            {notification.title}
          </h4>
        )}
        <p className="text-sm text-gray-700">
          {notification.message}
        </p>
        
        {notification.action && (
          <div className="mt-2">
            <button
              onClick={notification.action.onClick}
              className="text-sm font-medium text-orange-600 hover:text-orange-500"
            >
              {notification.action.label}
            </button>
          </div>
        )}
      </div>
      
      {!notification.persistent && (
        <button
          onClick={() => removeNotification(notification.id)}
          className="ml-4 flex-shrink-0 text-gray-400 hover:text-gray-600 transition-colors"
        >
          <X className="w-4 h-4" />
        </button>
      )}
    </div>
  );
};

const ToastContainer = () => {
  const { notifications } = useNotification();

  return (
    <div className="fixed top-4 right-4 z-50 space-y-2">
      {notifications.map((notification) => (
        <div
          key={notification.id}
          className="animate-slide-in-right"
        >
          <Toast notification={notification} />
        </div>
      ))}
    </div>
  );
};

export default ToastContainer;
