import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import axios from 'axios';

// Configure axios defaults
axios.defaults.withCredentials = true;
axios.defaults.baseURL = process.env.REACT_APP_API_URL || 'http://localhost:5000';
axios.defaults.timeout = 15000; // 15 second timeout

// Add request interceptor for error handling
axios.interceptors.request.use(
  (config) => {
    // Add authorization header if token exists
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    // Add content type for JSON requests
    if (!config.headers['Content-Type']) {
      config.headers['Content-Type'] = 'application/json';
    }
    
    return config;
  },
  (error) => {
    console.error('Request error:', error);
    return Promise.reject(error);
  }
);

// Add response interceptor for global error handling
axios.interceptors.response.use(
  (response) => {
    return response;
  },
  (error) => {
    // Handle token expiration
    if (error.response?.status === 401) {
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      // Only redirect to login if we're not already there
      if (!window.location.pathname.includes('/login')) {
        console.log('Session expired, please login again');
      }
    }
    
    // Handle server errors
    if (error.response?.status >= 500) {
      console.error('Server error occurred:', error.response.data?.message || 'Internal server error');
    }
    
    // Handle network errors
    if (!error.response) {
      console.error('Network error - please check your internet connection');
    }
    
    return Promise.reject(error);
  }
);

// Suppress MetaMask warnings if not needed for food delivery app
const originalError = console.error;
console.error = (...args) => {
  // Filter out MetaMask related errors
  if (args[0] && typeof args[0] === 'string' && 
      (args[0].includes('MetaMask') || 
       args[0].includes('ethereum') || 
       args[0].includes('wallet'))) {
    return; // Suppress wallet-related errors for food delivery app
  }
  originalError.apply(console, args);
};

// Performance monitoring in development
if (process.env.NODE_ENV === 'development') {
  // Log slow renders
  const originalRender = ReactDOM.render;
  if (originalRender) {
    ReactDOM.render = function(...args) {
      const start = performance.now();
      const result = originalRender.apply(this, args);
      const end = performance.now();
      
      if (end - start > 16) { // Slower than 60fps
        console.warn(`Slow render detected: ${(end - start).toFixed(2)}ms`);
      }
      
      return result;
    };
  }
} else {
  // Disable console in production
  console.log = () => {};
  console.warn = () => {};
  console.info = () => {};
}

// Create root and render app
const root = ReactDOM.createRoot(document.getElementById('root'));

// Enhanced error handling for rendering
const renderApp = () => {
  try {
    root.render(
      <React.StrictMode>
        <App />
      </React.StrictMode>
    );
  } catch (error) {
    console.error('Failed to render app:', error);
    
    // Fallback render without StrictMode
    try {
      root.render(<App />);
    } catch (fallbackError) {
      console.error('Fallback render failed:', fallbackError);
      
      // Ultimate fallback
      root.render(
        <div style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          minHeight: '100vh',
          fontFamily: 'Arial, sans-serif',
          backgroundColor: '#f9fafb'
        }}>
          <div style={{ textAlign: 'center', padding: '2rem' }}>
            <h1 style={{ color: '#ef4444', marginBottom: '1rem' }}>App Loading Error</h1>
            <p style={{ color: '#6b7280', marginBottom: '1rem' }}>
              Unable to load the application. Please refresh the page.
            </p>
            <button 
              onClick={() => window.location.reload()}
              style={{
                backgroundColor: '#f97316',
                color: 'white',
                padding: '0.5rem 1rem',
                border: 'none',
                borderRadius: '0.5rem',
                cursor: 'pointer'
              }}
            >
              Refresh Page
            </button>
          </div>
        </div>
      );
    }
  }
};

// Initialize app
renderApp();

// Hot module replacement for development
if (module.hot) {
  module.hot.accept('./App', () => {
    renderApp();
  });
}
